import React, { useCallback, useMemo, useState } from "react";
import { View, Text, Pressable, Share, Modal } from "react-native";
import { Ionicons } from "@expo/vector-icons";
import * as Clipboard from "expo-clipboard";
import { BottomSheetModal, BottomSheetView, BottomSheetBackdrop } from "@gorhom/bottom-sheet";
import { useSavedStore } from "../state/savedStore";
import { usePreferenceAwareHaptics } from "../utils/haptics";
import ReportModal from "./ReportModal";
import { BlurView } from "expo-blur";

interface FeedActionSheetProps {
  confessionId: string;
  confessionText: string;
  bottomSheetModalRef: React.RefObject<BottomSheetModal | null>;
}

export default function FeedActionSheet({ confessionId, confessionText, bottomSheetModalRef }: FeedActionSheetProps) {
  const [showModal, setShowModal] = useState(false);
  const [reportModalVisible, setReportModalVisible] = useState(false);
  const [modalMessage, setModalMessage] = useState("");
  const [_modalType, setModalType] = useState<"success" | "confirm">("success");

  const { saveConfession, unsaveConfession, isSaved } = useSavedStore();
  const { impactAsync } = usePreferenceAwareHaptics();

  // Bottom sheet configuration
  const snapPoints = useMemo(() => ["45%"], []);

  const showMessage = (message: string, type: "success" | "confirm") => {
    setModalMessage(message);
    setModalType(type);
    setShowModal(true);
  };

  // Backdrop component
  const renderBackdrop = useCallback(
    (props: any) => <BottomSheetBackdrop {...props} disappearsOnIndex={-1} appearsOnIndex={0} opacity={0.5} />,
    [],
  );

  const handleNativeShare = useCallback(async () => {
    try {
      const shareUrl = `https://secrets.app/confession/${confessionId}`;
      await Share.share({
        message: `Check out this anonymous confession: "${confessionText.substring(0, 100)}..." ${shareUrl}`,
        url: shareUrl,
      });
      impactAsync();
      bottomSheetModalRef.current?.dismiss();
    } catch (error) {
      if (__DEV__) {
        console.error("Share failed:", error);
      }
    }
  }, [confessionId, confessionText, bottomSheetModalRef, impactAsync]);

  const handleCopyLink = useCallback(async () => {
    try {
      const shareUrl = `https://secrets.app/confession/${confessionId}`;
      await Clipboard.setStringAsync(shareUrl);
      showMessage("Link copied to clipboard!", "success");
      impactAsync();
      bottomSheetModalRef.current?.dismiss();
    } catch (error) {
      if (__DEV__) {
        console.error("Copy failed:", error);
      }
    }
  }, [confessionId, bottomSheetModalRef, impactAsync]);

  const handleCopyText = useCallback(async () => {
    try {
      await Clipboard.setStringAsync(confessionText);
      showMessage("Text copied to clipboard!", "success");
      impactAsync();
      bottomSheetModalRef.current?.dismiss();
    } catch (error) {
      if (__DEV__) {
        console.error("Copy failed:", error);
      }
    }
  }, [confessionText, bottomSheetModalRef, impactAsync]);

  const handleSave = useCallback(() => {
    const isCurrentlySaved = isSaved(confessionId);

    if (isCurrentlySaved) {
      unsaveConfession(confessionId);
      showMessage("Removed from saved secrets", "success");
    } else {
      saveConfession(confessionId);
      showMessage("Saved to your collection!", "success");
    }

    impactAsync();
    bottomSheetModalRef.current?.dismiss();
  }, [confessionId, isSaved, saveConfession, unsaveConfession, bottomSheetModalRef, impactAsync]);

  const handleReport = useCallback(() => {
    impactAsync();
    bottomSheetModalRef.current?.dismiss();
    setTimeout(() => {
      setReportModalVisible(true);
    }, 300);
  }, [bottomSheetModalRef, impactAsync]);

  const ActionOption = ({
    icon,
    title,
    subtitle,
    onPress,
    color = "#8B98A5",
  }: {
    icon: string;
    title: string;
    subtitle: string;
    onPress: () => void;
    color?: string;
  }) => (
    <Pressable className="flex-row items-center py-4 px-4 active:bg-gray-800 rounded-xl" onPress={onPress}>
      <View
        className="w-10 h-10 rounded-full items-center justify-center mr-3"
        style={{ backgroundColor: `${color}20` }}
      >
        <Ionicons name={icon as any} size={20} color={color} />
      </View>
      <View className="flex-1">
        <Text className="text-white text-16 font-medium">{title}</Text>
        <Text className="text-gray-400 text-13 mt-0.5">{subtitle}</Text>
      </View>
    </Pressable>
  );

  return (
    <>
      <BottomSheetModal
        ref={bottomSheetModalRef}
        index={0}
        snapPoints={snapPoints}
        backdropComponent={renderBackdrop}
        backgroundStyle={{ backgroundColor: "#1F2937" }}
        handleIndicatorStyle={{ backgroundColor: "#4B5563" }}
      >
        <BottomSheetView className="flex-1 px-4">
          {/* Header */}
          <View className="items-center mb-6">
            <Text className="text-white text-18 font-semibold">Actions</Text>
            <Text className="text-gray-400 text-14 mt-1">Choose an action for this secret</Text>
          </View>

          {/* Action Options */}
          <View className="space-y-2">
            <ActionOption
              icon="share-outline"
              title="Share"
              subtitle="Share this anonymous confession"
              onPress={handleNativeShare}
              color="#1D9BF0"
            />

            <ActionOption
              icon="link-outline"
              title="Copy Link"
              subtitle="Copy link to clipboard"
              onPress={handleCopyLink}
              color="#10B981"
            />

            <ActionOption
              icon="copy-outline"
              title="Copy Text"
              subtitle="Copy confession text"
              onPress={handleCopyText}
              color="#8B5CF6"
            />

            <ActionOption
              icon={isSaved(confessionId) ? "bookmark" : "bookmark-outline"}
              title={isSaved(confessionId) ? "Remove from Saved" : "Save"}
              subtitle={isSaved(confessionId) ? "Remove from your collection" : "Save to your collection"}
              onPress={handleSave}
              color="#F59E0B"
            />

            <ActionOption
              icon="flag-outline"
              title="Report"
              subtitle="Report inappropriate content"
              onPress={handleReport}
              color="#EF4444"
            />
          </View>
        </BottomSheetView>
      </BottomSheetModal>

      {/* Success/Confirm Modal */}
      <Modal visible={showModal} transparent animationType="fade" onRequestClose={() => setShowModal(false)}>
        <BlurView intensity={20} tint="dark" style={{ flex: 1 }}>
          <View className="flex-1 items-center justify-center px-8">
            <View className="bg-gray-800 rounded-2xl p-6 w-full max-w-sm">
              <View className="items-center">
                <View className="w-12 h-12 bg-green-600 rounded-full items-center justify-center mb-4">
                  <Ionicons name="checkmark" size={24} color="white" />
                </View>
                <Text className="text-white text-16 font-medium text-center mb-6">{modalMessage}</Text>
                <Pressable className="bg-blue-600 rounded-xl py-3 px-6 w-full" onPress={() => setShowModal(false)}>
                  <Text className="text-white text-16 font-medium text-center">OK</Text>
                </Pressable>
              </View>
            </View>
          </View>
        </BlurView>
      </Modal>

      {/* Report Modal */}
      <ReportModal
        isVisible={reportModalVisible}
        onClose={() => setReportModalVisible(false)}
        confessionId={confessionId}
        contentType="confession"
      />
    </>
  );
}

import React, { useRef, useEffect, useCallback, useState } from "react";
import { VideoView, useVideoPlayer } from "expo-video";
import { View } from "react-native";
import { useVideoAnalyticsTracker } from "../hooks/useVideoAnalyticsTracker";
import { consentStore } from "../state/consentStore";

interface HermesCompatibleVideoPlayerProps {
  videoUri: string;
  videoId?: string;
  isActive: boolean;
  onError?: (error: Error) => void;
  onPlaybackStatusUpdate?: (status: any) => void;
  onPlay?: () => void;
  onPause?: () => void;
  onSeek?: (from: number, to: number) => void;
  onBufferStart?: () => void;
  onBufferEnd?: () => void;
  onComplete?: () => void;
  onQualityChange?: (quality: string) => void;
  enableAnalytics?: boolean;
  trackDetailedEngagement?: boolean;
  sessionId?: string;
  style?: any;
  className?: string;
}

/**
 * Enhanced video player component with Hermes-specific disposal handling
 * Addresses "Player pause failed during disposal" warnings
 */
export const HermesCompatibleVideoPlayer: React.FC<HermesCompatibleVideoPlayerProps> = ({
  videoUri,
  videoId,
  isActive,
  onError,
  onPlaybackStatusUpdate,
  onPlay,
  onPause,
  onSeek,
  onBufferStart,
  onBufferEnd,
  onComplete,
  onQualityChange,
  enableAnalytics = true,
  trackDetailedEngagement = true,
  sessionId,
  style,
  className,
}) => {
  const playerRef = useRef<VideoView>(null);
  const [isDisposing, setIsDisposing] = useState(false);
  const disposalTimeoutRef = useRef<NodeJS.Timeout | null>(null);
  const lastPlaybackStatusRef = useRef<any>(null);
  const bufferingStateRef = useRef(false);
  const seekDetectionRef = useRef<{ time: number; position: number } | null>(null);
  const qualityRef = useRef<string>("auto");
  const videoDurationRef = useRef<number>(0);
  const hasStartedRef = useRef(false);
  const performanceStartRef = useRef<number>(Date.now());

  // Create player with enhanced error handling
  const player = useVideoPlayer(videoUri, (player) => {
    try {
      player.loop = true;
      player.muted = false;

      // Set initial play state
      if (isActive && !isDisposing) {
        player.play();
      } else {
        player.pause();
      }
    } catch (error) {
      if (__DEV__) {
        console.warn("Video player setup error:", error);
      }
      onError?.(error as Error);
    }
  });

  const analyticsConsent = consentStore.preferences.analytics;
  const analytics = useVideoAnalyticsTracker({
    videoId: videoId || videoUri,
    videoDuration: videoDurationRef.current,
    enableDetailedTracking: trackDetailedEngagement && analyticsConsent && enableAnalytics,
    sessionId,
    onEngagementUpdate: (score) => {
      if (__DEV__) {
        console.log(`Video engagement score: ${score}`);
      }
    },
    onCompletionDetected: (completionRate) => {
      if (__DEV__) {
        console.log(`Video completed: ${completionRate}%`);
      }
      onComplete?.();
    },
  });

  // Handle play/pause based on active state
  useEffect(() => {
    if (!player || isDisposing) return;

    try {
      if (isActive) {
        player.play();
      } else {
        player.pause();
      }
    } catch (error) {
      if (__DEV__) {
        console.warn("Video play/pause error:", error);
      }
    }
  }, [player, isActive, isDisposing]);

  // Enhanced disposal handling for Hermes compatibility
  const disposePlayer = useCallback(async () => {
    if (isDisposing || !player) return;

    setIsDisposing(true);

    try {
      // Clear any pending disposal timeout
      if (disposalTimeoutRef.current) {
        clearTimeout(disposalTimeoutRef.current);
        disposalTimeoutRef.current = null;
      }

      // Graceful pause with timeout
      const pausePromise = new Promise<void>((resolve) => {
        try {
          // Check if player is still valid
          let shouldPause = false;
          try {
            shouldPause = player.playing;
          } catch (checkError: any) {
            // Player already disposed
            if (
              checkError?.message?.includes("NativeSharedObjectNotFoundException") ||
              checkError?.message?.includes("Unable to find the native shared object")
            ) {
              resolve();
              return;
            }
          }

          if (shouldPause) {
            try {
              player.pause();
            } catch (pauseErr: any) {
              // Only log non-disposal errors
              if (
                __DEV__ &&
                !pauseErr?.message?.includes("NativeSharedObjectNotFoundException") &&
                !pauseErr?.message?.includes("Unable to find the native shared object")
              ) {
                console.warn("Video pause error during disposal:", pauseErr?.message);
              }
            }
          }
          resolve();
        } catch (pauseError: any) {
          // Ignore disposal-related errors
          if (
            __DEV__ &&
            !pauseError?.message?.includes("NativeSharedObjectNotFoundException") &&
            !pauseError?.message?.includes("Unable to find the native shared object")
          ) {
            console.warn("Video pause failed during disposal:", pauseError?.message);
          }
          resolve();
        }
      });

      // Set a timeout for pause operation
      const timeoutPromise = new Promise<void>((resolve) => {
        disposalTimeoutRef.current = setTimeout(() => {
          resolve();
        }, 100); // 100ms timeout for pause
      });

      // Wait for either pause to complete or timeout
      await Promise.race([pausePromise, timeoutPromise]);

      // Note: player.unload() does not exist in expo-video
      // The player is automatically disposed when the component unmounts
    } catch (error) {
      // Silently ignore all disposal errors
      if (__DEV__) {
        console.warn("Video disposal error:", error);
      }
    } finally {
      // Clean up timeout
      if (disposalTimeoutRef.current) {
        clearTimeout(disposalTimeoutRef.current);
        disposalTimeoutRef.current = null;
      }
    }
  }, [player, isDisposing]);

  // Enhanced cleanup with analytics
  useEffect(() => {
    return () => {
      // Clean up analytics tracking
      if (enableAnalytics && analyticsConsent) {
        analytics.cleanup();
      }
      disposePlayer();
    };
  }, [disposePlayer, enableAnalytics, analytics, analyticsConsent]);

  // Track quality changes if supported
  useEffect(() => {
    if (!player || !trackDetailedEngagement || !enableAnalytics || !analyticsConsent) return;

    // expo-video doesn't expose quality directly, but we can track if it changes
    // This is a placeholder for when quality information becomes available
    const checkQuality = () => {
      // Future: Check player.quality or similar property
      const currentQuality = "auto"; // Default for now
      if (currentQuality !== qualityRef.current) {
        qualityRef.current = currentQuality;
        onQualityChange?.(currentQuality);
        analytics.updateQuality(currentQuality);
      }
    };

    const qualityInterval = setInterval(checkQuality, 5000);
    return () => clearInterval(qualityInterval);
  }, [player, trackDetailedEngagement, enableAnalytics, analyticsConsent, onQualityChange, analytics]);

  // Track buffer events (placeholder for when expo-video exposes buffering state)
  useEffect(() => {
    if (!player || !trackDetailedEngagement || !enableAnalytics || !analyticsConsent) return;

    // This is a placeholder - expo-video doesn't currently expose buffering events
    // When available, implement proper buffer tracking
    const checkBuffering = () => {
      // Future: Check player.isBuffering or similar
      const isBuffering = false; // Default for now

      if (isBuffering !== bufferingStateRef.current) {
        bufferingStateRef.current = isBuffering;
        if (isBuffering) {
          onBufferStart?.();
          analytics.handleBufferStart();
        } else {
          onBufferEnd?.();
          analytics.handleBufferEnd();
        }
      }
    };

    const bufferInterval = setInterval(checkBuffering, 500);
    return () => clearInterval(bufferInterval);
  }, [player, trackDetailedEngagement, enableAnalytics, analyticsConsent, onBufferStart, onBufferEnd, analytics]);

  // Track interaction events
  const trackInteraction = useCallback(
    (type: "like" | "unlike" | "comment" | "share" | "save") => {
      if (enableAnalytics && analyticsConsent) {
        analytics.trackInteraction(type);
      }
    },
    [enableAnalytics, analyticsConsent, analytics],
  );

  // Handle player status changes with enhanced analytics
  useEffect(() => {
    if (!player || isDisposing) return;

    // Monitor player properties for status updates
    const checkStatus = () => {
      try {
        const currentStatus = {
          isLoaded: true,
          isPlaying: player.playing,
          isBuffering: false, // expo-video doesn't expose buffering state directly
          currentTime: player.currentTime,
          duration: player.duration,
          positionMillis: player.currentTime * 1000,
          durationMillis: player.duration * 1000,
          muted: player.muted,
          loop: player.loop,
          didJustFinish: false,
          isLooping: player.loop,
          error: null,
        };

        // Update duration reference
        if (player.duration > 0 && videoDurationRef.current !== player.duration) {
          videoDurationRef.current = player.duration;
        }

        // Detect first play
        if (player.playing && !hasStartedRef.current) {
          hasStartedRef.current = true;
          const startupTime = Date.now() - performanceStartRef.current;
          if (__DEV__) {
            console.log(`Video startup time: ${startupTime}ms`);
          }
        }

        // Detect playback state changes
        if (lastPlaybackStatusRef.current) {
          const lastStatus = lastPlaybackStatusRef.current;

          // Play/Pause detection
          if (player.playing !== lastStatus.isPlaying) {
            if (player.playing) {
              onPlay?.();
            } else {
              onPause?.();
            }
          }

          // Seek detection
          if (seekDetectionRef.current) {
            const timeDiff = Date.now() - seekDetectionRef.current.time;
            const positionDiff = Math.abs(player.currentTime - seekDetectionRef.current.position);

            if (timeDiff < 100 && positionDiff > 2) {
              // Likely a seek if position changed more than 2 seconds within 100ms
              onSeek?.(seekDetectionRef.current.position, player.currentTime);
            }
          }
          seekDetectionRef.current = { time: Date.now(), position: player.currentTime };

          // Completion detection
          if (player.duration > 0 && player.currentTime >= player.duration * 0.95) {
            if (!lastStatus.didJustFinish) {
              currentStatus.didJustFinish = true;
            }
          }
        }

        // Send status updates
        onPlaybackStatusUpdate?.(currentStatus);

        // Send to analytics tracker if enabled
        if (enableAnalytics && analyticsConsent) {
          analytics.onPlaybackStatusUpdate(currentStatus);
        }

        lastPlaybackStatusRef.current = currentStatus;
      } catch (statusError) {
        if (__DEV__) {
          console.warn("Playback status update error:", statusError);
        }
        onError?.(new Error("Failed to get playback status"));
      }
    };

    // Check status periodically with higher frequency for better tracking
    const interval = setInterval(checkStatus, 250); // 250ms for more accurate tracking

    // Initial status check
    checkStatus();

    return () => {
      clearInterval(interval);
    };
  }, [
    player,
    isDisposing,
    onPlaybackStatusUpdate,
    onError,
    onPlay,
    onPause,
    onSeek,
    onComplete,
    enableAnalytics,
    analytics,
    analyticsConsent,
  ]);

  if (isDisposing) {
    return <View style={style} className={className} />;
  }

  return (
    <VideoView
      ref={playerRef}
      style={style}
      player={player}
      fullscreenOptions={{ enable: false }}
      allowsPictureInPicture={false}
      showsTimecodes={false}
      requiresLinearPlayback={false}
    />
  );
};

export default HermesCompatibleVideoPlayer;

import React, { useEffect, useState, useRef, useMemo } from "react";
import { View, Text, StyleSheet, Pressable, AccessibilityInfo, Platform } from "react-native";
import Animated, {
  useSharedValue,
  useAnimatedStyle,
  withTiming,
  withSpring,
  withSequence,
  withDelay,
  withRepeat,
  Easing,
  FadeIn,
  FadeOut,
  SlideInUp,
  SlideOutUp,
  runOnJS,
} from "react-native-reanimated";
import NetInfo, { NetInfoState, NetInfoStateType } from "@react-native-community/netinfo";
import * as Haptics from "expo-haptics";
import { useTheme } from "../hooks/useTheme";
import { Ionicons } from "@expo/vector-icons";

const AnimatedPressable = Animated.createAnimatedComponent(Pressable);

type ConnectionStatus = "online" | "offline" | "poor" | "reconnecting";
type ConnectionQuality = "excellent" | "good" | "fair" | "poor" | "offline";

interface NetworkStatusIndicatorProps {
  position?: "top" | "bottom" | "inline";
  autoHideDelay?: number;
  showBandwidth?: boolean;
  showRetryButton?: boolean;
  onRetry?: () => void;
  minimalMode?: boolean;
  persistentMode?: boolean;
  scrollOffset?: any;
  style?: any;
}

interface ConnectionMetrics {
  status: ConnectionStatus;
  quality: ConnectionQuality;
  type: NetInfoStateType;
  effectiveType?: string;
  downlinkSpeed?: number;
  isInternetReachable: boolean | null;
  details?: string;
}

const getConnectionMetrics = (state: NetInfoState): ConnectionMetrics => {
  const { isConnected, isInternetReachable, type, details } = state;

  if (!isConnected || isInternetReachable === false) {
    return {
      status: "offline",
      quality: "offline",
      type,
      isInternetReachable: isInternetReachable ?? false,
      details: "No internet connection",
    };
  }

  if (type === NetInfoStateType.wifi) {
    const wifiDetails = details as any;
    const strength = wifiDetails?.strength || 100;

    if (strength > 70) {
      return {
        status: "online",
        quality: "excellent",
        type,
        isInternetReachable: true,
        details: "Wi-Fi connected",
      };
    } else if (strength > 50) {
      return {
        status: "online",
        quality: "good",
        type,
        isInternetReachable: true,
        details: "Wi-Fi connected",
      };
    } else {
      return {
        status: "poor",
        quality: "fair",
        type,
        isInternetReachable: true,
        details: "Weak Wi-Fi signal",
      };
    }
  }

  if (type === NetInfoStateType.cellular) {
    const cellularDetails = details as any;
    const generation = cellularDetails?.cellularGeneration;

    switch (generation) {
      case "5g":
        return {
          status: "online",
          quality: "excellent",
          type,
          effectiveType: "5G",
          isInternetReachable: true,
          details: "5G connected",
        };
      case "4g":
        return {
          status: "online",
          quality: "good",
          type,
          effectiveType: "4G",
          isInternetReachable: true,
          details: "4G LTE connected",
        };
      case "3g":
        return {
          status: "poor",
          quality: "fair",
          type,
          effectiveType: "3G",
          isInternetReachable: true,
          details: "3G connection (slower speeds)",
        };
      default:
        return {
          status: "poor",
          quality: "poor",
          type,
          effectiveType: "2G",
          isInternetReachable: true,
          details: "Slow cellular connection",
        };
    }
  }

  return {
    status: isInternetReachable === null ? "reconnecting" : "online",
    quality: "fair",
    type,
    isInternetReachable: isInternetReachable ?? true,
    details: "Checking connection...",
  };
};

export default function NetworkStatusIndicator({
  position = "top",
  autoHideDelay = 5000,
  showBandwidth = false,
  showRetryButton = true,
  onRetry,
  minimalMode = false,
  persistentMode = false,
  scrollOffset,
  style,
}: NetworkStatusIndicatorProps) {
  const theme = useTheme();
  const [metrics, setMetrics] = useState<ConnectionMetrics>({
    status: "online",
    quality: "good",
    type: NetInfoStateType.unknown,
    isInternetReachable: true,
  });
  const [isVisible, setIsVisible] = useState(false);
  const [bandwidthMbps, setBandwidthMbps] = useState<number | null>(null);

  const hideTimer = useRef<NodeJS.Timeout | undefined>(undefined);
  const reconnectAttempts = useRef(0);

  const opacity = useSharedValue(0);
  const translateY = useSharedValue(position === "top" ? -50 : 50);
  const scale = useSharedValue(0.95);
  const pulseScale = useSharedValue(1);
  const dotScale = useSharedValue(1);
  const retryRotation = useSharedValue(0);

  const statusColors = useMemo(
    () => ({
      online: "#10B981",
      offline: "#EF4444",
      poor: "#F59E0B",
      reconnecting: "#6B7280",
    }),
    [],
  );

  const qualityIcons = useMemo(
    () => ({
      excellent: "wifi",
      good: "wifi",
      fair: "cellular-outline",
      poor: "cellular-outline",
      offline: "cloud-offline",
    }),
    [],
  );

  useEffect(() => {
    const unsubscribe = NetInfo.addEventListener((state) => {
      const newMetrics = getConnectionMetrics(state);
      const prevStatus = metrics.status;

      setMetrics(newMetrics);

      if (Platform.OS === "android" && state.details) {
        const details = state.details as any;
        if (details.linkDownBandwidthKbps) {
          setBandwidthMbps(details.linkDownBandwidthKbps / 1000);
        }
      }

      if (newMetrics.status === "offline") {
        setIsVisible(true);
        reconnectAttempts.current = 0;
        Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Medium);
        AccessibilityInfo.announceForAccessibility("Internet connection lost");
      } else if (newMetrics.status === "poor") {
        setIsVisible(true);
        scheduleAutoHide();
        Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Light);
        AccessibilityInfo.announceForAccessibility("Poor connection detected");
      } else if (prevStatus === "offline" && newMetrics.status === "online") {
        setIsVisible(true);
        scheduleAutoHide();
        Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Light);
        AccessibilityInfo.announceForAccessibility("Internet connection restored");
      } else if (!persistentMode && newMetrics.status === "online") {
        scheduleAutoHide();
      }

      if (newMetrics.status === "reconnecting") {
        reconnectAttempts.current++;
        startReconnectAnimation();
      }
    });

    NetInfo.fetch().then((state) => {
      const initialMetrics = getConnectionMetrics(state);
      setMetrics(initialMetrics);
      if (initialMetrics.status !== "online" || persistentMode) {
        setIsVisible(true);
      }
    });

    return () => {
      unsubscribe();
      if (hideTimer.current) clearTimeout(hideTimer.current);
    };
  }, []);

  useEffect(() => {
    if (isVisible) {
      opacity.value = withTiming(1, { duration: 300 });
      translateY.value = withSpring(0, { damping: 15, stiffness: 100 });
      scale.value = withSpring(1, { damping: 12, stiffness: 150 });

      if (metrics.status === "poor" || metrics.status === "reconnecting") {
        pulseScale.value = withRepeat(
          withSequence(withTiming(1.05, { duration: 600 }), withTiming(1, { duration: 600 })),
          -1,
          false,
        );
      }

      dotScale.value = withRepeat(
        withSequence(withTiming(1.2, { duration: 500 }), withTiming(0.8, { duration: 500 })),
        -1,
        false,
      );
    } else {
      opacity.value = withTiming(0, { duration: 300 });
      translateY.value = withTiming(position === "top" ? -50 : 50, { duration: 300 });
      scale.value = withTiming(0.95, { duration: 300 });
    }
  }, [isVisible, metrics.status, position]);

  const scheduleAutoHide = () => {
    if (!persistentMode && autoHideDelay > 0 && metrics.status === "online") {
      if (hideTimer.current) clearTimeout(hideTimer.current);
      hideTimer.current = setTimeout(() => {
        setIsVisible(false);
      }, autoHideDelay);
    }
  };

  const startReconnectAnimation = () => {
    retryRotation.value = withRepeat(withTiming(360, { duration: 1000, easing: Easing.linear }), -1, false);
  };

  const handleRetry = () => {
    Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Light);
    retryRotation.value = withSequence(withTiming(360, { duration: 500 }), withTiming(0, { duration: 0 }));
    if (onRetry) onRetry();
    NetInfo.fetch();
  };

  const animatedStyle = useAnimatedStyle(() => {
    const baseTranslateY = translateY.value;
    const scrollAdjustment = scrollOffset ? scrollOffset.value * 0.5 : 0;

    return {
      opacity: opacity.value,
      transform: [{ translateY: baseTranslateY - scrollAdjustment }, { scale: scale.value * pulseScale.value }],
    };
  });

  const dotAnimatedStyle = useAnimatedStyle(() => ({
    transform: [{ scale: dotScale.value }],
  }));

  const retryAnimatedStyle = useAnimatedStyle(() => ({
    transform: [{ rotate: `${retryRotation.value}deg` }],
  }));

  if (!isVisible && metrics.status === "online" && !persistentMode) {
    return null;
  }

  const positionStyles = {
    top: { top: 50 },
    bottom: { bottom: 100 },
    inline: {},
  };

  const renderMinimal = () => (
    <Animated.View style={[styles.minimalContainer, positionStyles[position], animatedStyle, style]}>
      <Animated.View style={[styles.minimalDot, { backgroundColor: statusColors[metrics.status] }, dotAnimatedStyle]} />
      {metrics.status === "offline" && <Text style={[styles.minimalText, { color: theme.colors.error }]}>Offline</Text>}
    </Animated.View>
  );

  const renderFull = () => (
    <Animated.View
      style={[
        styles.container,
        positionStyles[position],
        { backgroundColor: theme.colors.surfaceLight },
        animatedStyle,
        style,
      ]}
      entering={SlideInUp.duration(300).springify()}
      exiting={SlideOutUp.duration(300).springify()}
    >
      <View style={styles.content}>
        <View style={styles.statusSection}>
          <Animated.View
            style={[styles.statusDot, { backgroundColor: statusColors[metrics.status] }, dotAnimatedStyle]}
          />
          <Ionicons
            name={qualityIcons[metrics.quality] as any}
            size={20}
            color={statusColors[metrics.status]}
            style={styles.icon}
          />
          <View style={styles.textContainer}>
            <Text style={[styles.statusText, { color: theme.colors.text }]}>{metrics.details}</Text>
            {showBandwidth && bandwidthMbps && (
              <Text style={[styles.bandwidthText, { color: theme.colors.textSecondary }]}>
                {bandwidthMbps.toFixed(1)} Mbps
              </Text>
            )}
          </View>
        </View>

        {showRetryButton && metrics.status !== "online" && (
          <AnimatedPressable
            onPress={handleRetry}
            style={[styles.retryButton, retryAnimatedStyle]}
            accessibilityRole="button"
            accessibilityLabel="Retry connection"
          >
            <Ionicons name="refresh" size={18} color={theme.colors.primary} />
          </AnimatedPressable>
        )}
      </View>

      {metrics.status === "reconnecting" && (
        <View style={styles.progressBar}>
          <Animated.View style={[styles.progressFill, { backgroundColor: theme.colors.primary }]} />
        </View>
      )}
    </Animated.View>
  );

  return minimalMode ? renderMinimal() : renderFull();
}

