import React, { useCallback, useMemo, useRef, useState, useEffect, memo } from "react";
import {
  View,
  Dimensions,
  StatusBar,
  NativeSyntheticEvent,
  NativeScrollEvent,
  Text,
  RefreshControl,
  Pressable,
} from "react-native";
import { FlashList } from "@shopify/flash-list";
import { Ionicons } from "@expo/vector-icons";
import Animated, { useSharedValue, useAnimatedStyle, withTiming, FadeIn, FadeOut } from "react-native-reanimated";
import type { Confession } from "../types/confession";
import { BottomSheetModal } from "@gorhom/bottom-sheet";
import { useFocusEffect, useIsFocused } from "@react-navigation/native";
import { useConfessionStore } from "../state/confessionStore";
import { useSavedStore } from "../state/savedStore";
import { useGlobalVideoStore } from "../state/globalVideoStore";
import EnhancedVideoItem from "./EnhancedVideoItem";
import EnhancedCommentBottomSheet from "./EnhancedCommentBottomSheet";
import EnhancedShareBottomSheet from "./EnhancedShareBottomSheet";
import ReportModal from "./ReportModal";
import VideoFeedSkeleton from "./VideoFeedSkeleton";
import NetworkStatusIndicator from "./NetworkStatusIndicator";
import { ErrorState } from "./ErrorState";
import { LoadingTransitions } from "../utils/loadingTransitions";
import { VideoErrorMessages, UserFriendlyError } from "../utils/videoErrorMessages";
import { VideoError, VideoErrorType } from "../types/videoErrors";
import * as Haptics from "expo-haptics";

const { height: screenHeight, width: screenWidth } = Dimensions.get("window");

// Performance constants
const PRELOAD_BUFFER = 2;
const VELOCITY_THRESHOLD = 0.5;
const CLEANUP_INTERVAL = 30000; // 30 seconds

interface OptimizedVideoListProps {
  onClose: () => void;
  initialIndex?: number;
  onError?: (error: unknown) => void;
}

function OptimizedVideoList({ onClose, initialIndex = 0, onError }: OptimizedVideoListProps) {
  const confessions = useConfessionStore((state) => state.confessions);
  const loadConfessions = useConfessionStore((state) => state.loadConfessions);
  const isLoading = useConfessionStore((state) => state.isLoading);
  const videoConfessions = useMemo(() => {
    const filtered = confessions.filter((c) => c && c.type === "video" && c.id);

    // Validate for duplicates and dedupe if necessary
    if (filtered.length > 0) {
      const ids = filtered.map((c) => c.id);
      const duplicates = ids.filter((id, index) => ids.indexOf(id) !== index);

      if (duplicates.length > 0) {
        if (__DEV__) {
          console.warn("OptimizedVideoList: Duplicate confession IDs detected:", duplicates);
        }
        // Dedupe by ID using Map
        const uniqueConfessions = Array.from(new Map(filtered.map((item) => [item.id, item])).values());
        if (__DEV__) {
          console.log(
            `OptimizedVideoList: Deduplicated ${filtered.length - uniqueConfessions.length} duplicate confessions`,
          );
        }
        return uniqueConfessions;
      }
    }

    return filtered;
  }, [confessions]);

  const { saveConfession, unsaveConfession, isSaved } = useSavedStore();
  const { pauseAllVideos } = useGlobalVideoStore();

  const [currentIndex, setCurrentIndex] = useState(initialIndex);
  const currentIndexRef = useRef(initialIndex);
  const [scrollVelocity, setScrollVelocity] = useState(0);
  const [isScrolling, setIsScrolling] = useState(false);
  const lastScrollTime = useRef(Date.now());
  const lastScrollY = useRef(0);
  const preloadedIndexes = useRef<Set<number>>(new Set());
  const performanceMetrics = useRef({
    renderCount: 0,
    scrollEvents: 0,
    avgVelocity: 0,
  });

  // Track if this tab is currently focused
  const isFocused = useIsFocused();

  // Debug focus changes and handle global video pause
  useEffect(() => {
    if (__DEV__) console.log(`üé• OptimizedVideoList: isFocused changed to ${isFocused}`);

    if (!isFocused) {
      // When tab loses focus, force all video items to pause
      if (__DEV__) console.log(`üé• Tab lost focus - forcing all videos to pause`);
      // Note: FlashList will re-render items based on extraData including isFocused
    }
  }, [isFocused]);

  // Bottom sheet refs
  const commentSheetRef = useRef<BottomSheetModal>(null);
  const shareSheetRef = useRef<BottomSheetModal>(null);

  // State for current video being interacted with
  const [currentVideoId, setCurrentVideoId] = useState<string | null>(null);
  const [currentVideoText, setCurrentVideoText] = useState<string>("");
  const [reportModalVisible, setReportModalVisible] = useState(false);
  const [modalOpen, setModalOpen] = useState(false);
  const [isRefreshing, setIsRefreshing] = useState(false);
  const [isLoadingMore, setIsLoadingMore] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [userFriendlyError, setUserFriendlyError] = useState<UserFriendlyError | null>(null);
  const [retryAttempts, setRetryAttempts] = useState(0);

  // Animated values for loading transitions
  const scrollOffset = useSharedValue(0);
  const loadingOpacity = useSharedValue(1);
  const errorOpacity = useSharedValue(0);

  // Load confessions with enhanced error handling
  useEffect(() => {
    const loadData = async () => {
      try {
        if (__DEV__) {
          console.log("OptimizedVideoList: Loading confessions...");
        }
        // Trigger loading animation
        LoadingTransitions.fadeIn(loadingOpacity);

        // Always try to load fresh data when the video tab is accessed
        await loadConfessions();

        // Success animation
        LoadingTransitions.fadeOut(loadingOpacity, {}, () => {
          Haptics.notificationAsync(Haptics.NotificationFeedbackType.Success);
        });

        if (__DEV__) {
          console.log(
            `OptimizedVideoList: Loaded ${confessions.length} total confessions, ${videoConfessions.length} video confessions`,
          );
        }
      } catch (error: any) {
        if (__DEV__) {
          console.error("OptimizedVideoList: Failed to load confessions:", error);
        }

        // Create user-friendly error
        const videoError: VideoError = {
          type: VideoErrorType.NETWORK,
          code: error.code || "LOAD_ERROR",
          message: error.message || "Failed to load videos",
          timestamp: Date.now(),
          debugInfo: error.stack,
        };

        const attemptNum = retryAttempts + 1;
        setRetryAttempts(attemptNum);
        const friendlyError = VideoErrorMessages.getUserFriendlyError(videoError, attemptNum);

        setUserFriendlyError(friendlyError);
        setError(friendlyError.message);
        LoadingTransitions.createErrorShake(errorOpacity);

        onError?.(error);
      }
    };

    loadData();
  }, []); // Run only on mount

  // Handle screen focus for video playback control
  useFocusEffect(
    useCallback(() => {
      // Screen gains focus - this handles navigation to/from this screen
      return () => {
        // Screen loses focus - this handles navigation away from this screen
      };
    }, []),
  );

  // FlashList doesn't need getItemLayout as it handles layout automatically

  // Enhanced viewability changes with intelligent preloading
  const onViewableItemsChanged = useCallback(
    ({ viewableItems }: { viewableItems: { item: Confession; isViewable: boolean; index: number | null }[] }) => {
      const first = viewableItems.find((v) => v.isViewable && v.index !== null) || viewableItems[0];
      if (first && first.index !== null) {
        const newIndex = first.index;
        currentIndexRef.current = newIndex;
        setCurrentIndex(newIndex);

        // Intelligent preloading based on scroll direction
        if (Math.abs(scrollVelocity) > VELOCITY_THRESHOLD) {
          const direction = scrollVelocity > 0 ? 1 : -1;
          for (let i = 1; i <= PRELOAD_BUFFER; i++) {
            const preloadIndex = newIndex + i * direction;
            if (preloadIndex >= 0 && preloadIndex < videoConfessions.length) {
              preloadedIndexes.current.add(preloadIndex);
            }
          }
        }

        // Cleanup old preloaded indexes
        if (preloadedIndexes.current.size > PRELOAD_BUFFER * 2) {
          const toKeep = new Set<number>();
          for (let i = -PRELOAD_BUFFER; i <= PRELOAD_BUFFER; i++) {
            const keepIndex = newIndex + i;
            if (preloadedIndexes.current.has(keepIndex)) {
              toKeep.add(keepIndex);
            }
          }
          preloadedIndexes.current = toKeep;
        }
      }
    },
    [scrollVelocity, videoConfessions.length],
  );

  const viewabilityConfig = useMemo(
    () => ({
      itemVisiblePercentThreshold: 60,
      minimumViewTime: 100,
      waitForInteraction: false,
    }),
    [],
  );

  // Optimized scroll handler with velocity tracking
  const handleScroll = useCallback((event: NativeSyntheticEvent<NativeScrollEvent>) => {
    const currentTime = Date.now();
    const currentY = event.nativeEvent.contentOffset.y;
    const timeDelta = currentTime - lastScrollTime.current;
    const scrollDelta = currentY - lastScrollY.current;

    if (timeDelta > 0) {
      const velocity = scrollDelta / timeDelta;
      setScrollVelocity(velocity);

      // Update performance metrics
      performanceMetrics.current.scrollEvents++;
      performanceMetrics.current.avgVelocity =
        (performanceMetrics.current.avgVelocity * (performanceMetrics.current.scrollEvents - 1) + Math.abs(velocity)) /
        performanceMetrics.current.scrollEvents;
    }

    lastScrollTime.current = currentTime;
    lastScrollY.current = currentY;
  }, []);

  const handleScrollBeginDrag = useCallback(() => {
    setIsScrolling(true);
  }, []);

  const handleScrollEndDrag = useCallback(() => {
    setIsScrolling(false);
    setScrollVelocity(0);
  }, []);

  // Handle comment press
  const handleCommentPress = useCallback(
    (confessionId: string) => {
      try {
        // Pause all videos when opening comments
        const currentPlayer = videoConfessions.findIndex((v) => v.id === confessionId);
        if (currentPlayer >= 0) {
          // Explicitly pause all videos to prevent background playback
          pauseAllVideos();
        }

        setCurrentVideoId(confessionId);
        setModalOpen(true);
        const video = videoConfessions.find((v) => v.id === confessionId);
        setCurrentVideoText(video?.transcription || video?.content || "");
        commentSheetRef.current?.present();
      } catch (error) {
        onError?.(error);
        console.error("Failed to open comments:", error);
      }
    },
    [videoConfessions, onError, pauseAllVideos],
  );

  // Handle share press
  const handleSharePress = useCallback(
    (confessionId: string, confessionText: string) => {
      try {
        setCurrentVideoId(confessionId);
        setCurrentVideoText(confessionText);
        shareSheetRef.current?.present();
      } catch (error) {
        onError?.(error);
        console.error("Failed to open share:", error);
      }
    },
    [onError],
  );

  // Handle save press
  const handleSavePress = useCallback(
    async (confessionId: string) => {
      try {
        if (isSaved(confessionId)) {
          await unsaveConfession(confessionId);
        } else {
          await saveConfession(confessionId);
        }
      } catch (error) {
        onError?.(error);
        console.error("Failed to toggle save:", error);
      }
    },
    [saveConfession, unsaveConfession, isSaved, onError],
  );

  // Handle report press
  const handleReportPress = useCallback((confessionId: string, confessionText: string) => {
    setCurrentVideoId(confessionId);
    setCurrentVideoText(confessionText);
    setReportModalVisible(true);
  }, []);

  const renderItem = useCallback(
    ({ item, index }: { item: Confession; index: number }) => {
      // Add null check for item to prevent crashes
      if (!item || !item.id) {
        if (__DEV__) {
          console.warn(`OptimizedVideoList: Invalid item at index ${index}:`, item);
        }
        return (
          <View
            style={{
              height: screenHeight,
              width: screenWidth,
              backgroundColor: "black",
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            <Text style={{ color: "#999", fontSize: 16 }}>Invalid video data</Text>
          </View>
        );
      }

      const isPreloading = preloadedIndexes.current.has(index);
      const isNearActive = Math.abs(index - currentIndex) <= 1;
      const shouldOptimizeRender = isScrolling && !isNearActive;

      // Track render performance
      if (__DEV__) {
        performanceMetrics.current.renderCount++;
      }

      return (
        <View style={{ height: screenHeight, width: screenWidth }}>
          <EnhancedVideoItem
            confession={item}
            isActive={index === currentIndex && isFocused && !isScrolling}
            onClose={onClose}
            onCommentPress={handleCommentPress}
            onSharePress={handleSharePress}
            onSavePress={handleSavePress}
            onReportPress={handleReportPress}
            forceUnmuted={true}
            screenFocused={isFocused && index === currentIndex}
          />
        </View>
      );
    },
    [
      onClose,
      currentIndex,
      isFocused,
      isScrolling,
      handleCommentPress,
      handleSharePress,
      handleSavePress,
      handleReportPress,
    ],
  );

  const keyExtractor = useCallback((item: Confession, index: number) => {
    // Ensure unique key: prefer id, fallback to index (but warn if no id)
    if (!item?.id) {
      if (__DEV__) {
        console.warn("OptimizedVideoList: Confession missing ID at index", index, item);
      }
      return `fallback-${index}-${Date.now()}`;
    }
    return item.id; // Unique UUID
  }, []);

  // Performance tracking and cleanup
  useEffect(() => {
    const interval = setInterval(() => {
      if (__DEV__) {
        const metrics = performanceMetrics.current;
        if (metrics.scrollEvents > 0) {
          console.log("[OptimizedVideoList] Performance:", {
            renders: metrics.renderCount,
            scrolls: metrics.scrollEvents,
            avgVelocity: metrics.avgVelocity.toFixed(2),
            preloaded: preloadedIndexes.current.size,
          });
        }
      }
      // Clear old preloaded indexes
      if (preloadedIndexes.current.size > PRELOAD_BUFFER * 3) {
        const currentIdx = currentIndexRef.current;
        const newSet = new Set<number>();
        preloadedIndexes.current.forEach((idx) => {
          if (Math.abs(idx - currentIdx) <= PRELOAD_BUFFER * 2) {
            newSet.add(idx);
          }
        });
        preloadedIndexes.current = newSet;
      }
    }, CLEANUP_INTERVAL);

    return () => clearInterval(interval);
  }, []);

  // Show enhanced loading state
  if (isLoading && videoConfessions.length === 0) {
    return (
      <>
        <VideoFeedSkeleton
          isVisible={true}
          state="initial"
          itemCount={3}
          animationTiming={{ fade: 400, stagger: 120 }}
        />
        <NetworkStatusIndicator position="top" minimalMode={false} autoHideDelay={5000} />
      </>
    );
  }

  // Show enhanced empty/error state
  if (!isLoading && videoConfessions.length === 0) {
    if (error && userFriendlyError) {
      return (
        <>
          <VideoFeedSkeleton isVisible={true} state="initial" showErrorIndicator={true} />
          <ErrorState
            type="video"
            title={userFriendlyError.title}
            message={userFriendlyError.message}
            actionLabel={userFriendlyError.actionText}
            onRetry={async () => {
              setError(null);
              setUserFriendlyError(null);
              setRetryAttempts(0);
              await loadConfessions();
            }}
          />
          <NetworkStatusIndicator
            position="top"
            persistentMode={true}
            onRetry={async () => {
              await loadConfessions();
            }}
          />
        </>
      );
    }

    return (
      <View style={{ flex: 1, backgroundColor: "black", justifyContent: "center", alignItems: "center" }}>
        <StatusBar hidden />
        <View style={{ alignItems: "center" }}>
          <View style={{ marginBottom: 16 }}>
            <Ionicons name="videocam-off-outline" size={48} color="#666" />
          </View>
          <Text style={{ color: "#999", fontSize: 16, marginBottom: 8 }}>No videos available</Text>
          <Text style={{ color: "#666", fontSize: 14 }}>Check back later for new content</Text>
        </View>
      </View>
    );
  }

  return (
    <>
      <StatusBar hidden />
      <View style={{ flex: 1, backgroundColor: "black" }}>
        <FlashList
          data={videoConfessions}
          renderItem={renderItem}
          keyExtractor={keyExtractor}
          onViewableItemsChanged={onViewableItemsChanged}
          viewabilityConfig={viewabilityConfig}
          pagingEnabled={true}
          snapToInterval={screenHeight}
          snapToAlignment="start"
          decelerationRate="fast"
          extraData={{ currentIndex, isFocused, isScrolling }}
          onScroll={(e) => {
            handleScroll(e);
            scrollOffset.value = e.nativeEvent.contentOffset.y;
          }}
          onScrollBeginDrag={handleScrollBeginDrag}
          onScrollEndDrag={handleScrollEndDrag}
          showsVerticalScrollIndicator={false}
          initialScrollIndex={initialIndex}
          getItemType={() => "video"}
          overrideItemLayout={(layout) => {
            layout.span = 1;
          }}
          contentContainerStyle={{ backgroundColor: "black" }}
          bounces={true}
          scrollEventThrottle={16}
          disableIntervalMomentum={true}
          snapToEnd={false}
          refreshControl={
            <RefreshControl
              refreshing={isRefreshing}
              onRefresh={async () => {
                setIsRefreshing(true);
                setError(null);
                setUserFriendlyError(null);
                setRetryAttempts(0);
                await loadConfessions();
                setIsRefreshing(false);
              }}
              tintColor="#fff"
              colors={["#fff"]}
            />
          }
        />

        {/* Loading overlay for refresh */}
        {isRefreshing && <VideoFeedSkeleton isVisible={true} state="pullToRefresh" itemCount={1} />}

        {/* Loading overlay for load more */}
        {isLoadingMore && <VideoFeedSkeleton isVisible={true} state="loadMore" itemCount={1} />}

        {/* Network status indicator */}
        <NetworkStatusIndicator position="top" minimalMode={true} autoHideDelay={3000} scrollOffset={scrollOffset} />
      </View>

      {/* Comment Bottom Sheet */}
      <EnhancedCommentBottomSheet bottomSheetModalRef={commentSheetRef} confessionId={currentVideoId || ""} />

      {/* Share Bottom Sheet */}
      <EnhancedShareBottomSheet
        bottomSheetModalRef={shareSheetRef}
        confessionId={currentVideoId || ""}
        confessionText={currentVideoText}
      />

      {/* Report Modal */}
      <ReportModal
        isVisible={reportModalVisible}
        onClose={() => {
          setReportModalVisible(false);
          setModalOpen(false);
        }}
        confessionId={currentVideoId || undefined}
        contentType="confession"
      />
    </>
  );
}

// Memoized export with optimized comparison
export default memo(OptimizedVideoList, (prevProps, nextProps) => {
  return prevProps.onClose === nextProps.onClose && prevProps.initialIndex === nextProps.initialIndex;
});

import React, { useEffect, useState } from "react";
import { View, Text, TouchableOpacity, StyleSheet, ActivityIndicator, Linking, Platform } from "react-native";
import { Ionicons } from "@expo/vector-icons";
import { useMediaPermissions } from "../hooks/useMediaPermissions";
import { getUserFriendlyMessage, StandardError } from "../utils/errorHandling";
import { ErrorState } from "./ErrorState";

interface PermissionGateProps {
  children: React.ReactNode;
  permissions?: ("camera" | "microphone" | "mediaLibrary")[];
  onPermissionDenied?: () => void;
  onPermissionGranted?: () => void;
  customMessages?: {
    camera?: string;
    microphone?: string;
    mediaLibrary?: string;
    permanentlyDenied?: string;
  };
  showSettingsButton?: boolean;
}

const defaultMessages = {
  camera:
    "Camera access is required to record videos. Your privacy is important to us - we only access the camera when you're actively recording.",
  microphone: "Microphone access is needed to capture audio with your videos. Audio is processed locally for privacy.",
  mediaLibrary:
    "Media library access allows you to save and share your videos. We only access files you explicitly choose.",
  permanentlyDenied: "Permissions have been denied. Please enable them in your device settings to continue.",
};

export const PermissionGate: React.FC<PermissionGateProps> = ({
  children,
  permissions = ["camera", "microphone"],
  onPermissionDenied,
  onPermissionGranted,
  customMessages = {},
  showSettingsButton = true,
}) => {
  const {
    permissionState,
    rawPermissions,
    requestCameraPermission,
    requestMicrophonePermission,
    requestMediaLibraryPermission,
    checkAllPermissions,
  } = useMediaPermissions();

  const [isChecking, setIsChecking] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [deniedPermissions, setDeniedPermissions] = useState<string[]>([]);

  const messages = { ...defaultMessages, ...customMessages };

  useEffect(() => {
    checkInitialPermissions();
  }, []);

  const checkInitialPermissions = async () => {
    setIsChecking(true);
    setError(null);

    try {
      await checkAllPermissions();

      const denied: string[] = [];

      if (permissions.includes("camera") && !permissionState.camera) {
        denied.push("camera");
      }
      if (permissions.includes("microphone") && !permissionState.microphone) {
        denied.push("microphone");
      }
      if (permissions.includes("mediaLibrary") && !permissionState.mediaLibrary) {
        denied.push("mediaLibrary");
      }

      setDeniedPermissions(denied);

      if (denied.length === 0) {
        onPermissionGranted?.();
      } else {
        onPermissionDenied?.();
      }
    } catch (err) {
      const standardError: StandardError = {
        code: "PERMISSION_CHECK_FAILED",
        message: (err as Error)?.message || "Failed to check permissions",
      };
      setError(getUserFriendlyMessage(standardError));
    } finally {
      setIsChecking(false);
    }
  };

  const requestPermission = async (type: "camera" | "microphone" | "mediaLibrary") => {
    setError(null);

    try {
      let result: boolean = false;

      switch (type) {
        case "camera":
          result = await requestCameraPermission();
          break;
        case "microphone":
          result = await requestMicrophonePermission();
          break;
        case "mediaLibrary":
          result = await requestMediaLibraryPermission();
          break;
      }

      if (result) {
        // Remove from denied list
        setDeniedPermissions((prev) => prev.filter((p) => p !== type));

        // Check if all permissions are now granted
        await checkInitialPermissions();
      } else {
        setError(
          `${type.charAt(0).toUpperCase() + type.slice(1)} permission was denied. You can enable it in settings.`,
        );
      }
    } catch (err) {
      const standardError: StandardError = {
        code: "PERMISSION_REQUEST_FAILED",
        message: (err as Error)?.message || `Failed to request ${type} permission`,
      };
      setError(getUserFriendlyMessage(standardError));
    }
  };

  const openSettings = () => {
    Linking.openSettings();
  };

  const isPermanentlyDenied = () => {
    // Compute which permissions are needed based on the permissions prop
    const needsCamera = permissions.includes("camera");
    const needsMic = permissions.includes("microphone");
    const needsLib = permissions.includes("mediaLibrary");

    // Helper function to check if a permission is permanently denied
    const isPermanent = (p: any) => p?.status === "denied" && p?.canAskAgain === false;

    // Check if any required permission is permanently denied
    if (needsCamera && isPermanent(rawPermissions.camera)) return true;
    if (needsMic && isPermanent(rawPermissions.microphone)) return true;
    if (needsLib && isPermanent(rawPermissions.mediaLibrary)) return true;
    return false;
  };

  // Show loading state while checking permissions
  if (isChecking) {
    return (
      <View style={styles.container}>
        <ActivityIndicator size="large" color="#007AFF" />
        <Text style={styles.loadingText}>Checking permissions...</Text>
      </View>
    );
  }

  // Show error state if there's an error
  if (error) {
    return <ErrorState message={error} onRetry={checkInitialPermissions} type="permission" />;
  }

  // All permissions granted - render children
  if (deniedPermissions.length === 0) {
    return <>{children}</>;
  }

  // Show permission request UI
  const permanentlyDenied = isPermanentlyDenied();

  return (
    <View style={styles.container}>
      <View style={styles.content}>
        <Ionicons
          name={permanentlyDenied ? "lock-closed" : "shield-checkmark"}
          size={64}
          color={permanentlyDenied ? "#FF3B30" : "#007AFF"}
          style={styles.icon}
        />

        <Text style={styles.title}>{permanentlyDenied ? "Permissions Required" : "Grant Permissions"}</Text>

        {permanentlyDenied ? (
          <>
            <Text style={styles.description}>{messages.permanentlyDenied}</Text>
            {showSettingsButton && (
              <TouchableOpacity style={styles.settingsButton} onPress={openSettings}>
                <Ionicons name="settings-outline" size={20} color="white" />
                <Text style={styles.settingsButtonText}>Open Settings</Text>
              </TouchableOpacity>
            )}
          </>
        ) : (
          <>
            <Text style={styles.description}>This app needs the following permissions to work properly:</Text>

            <View style={styles.permissionList}>
              {deniedPermissions.map((permission) => (
                <View key={permission} style={styles.permissionItem}>
                  <View style={styles.permissionInfo}>
                    <Ionicons
                      name={permission === "camera" ? "camera" : permission === "microphone" ? "mic" : "images"}
                      size={24}
                      color="#007AFF"
                      style={styles.permissionIcon}
                    />
                    <View style={styles.permissionTextContainer}>
                      <Text style={styles.permissionName}>
                        {permission.charAt(0).toUpperCase() + permission.slice(1)}
                      </Text>
                      <Text style={styles.permissionDescription}>{messages[permission as keyof typeof messages]}</Text>
                    </View>
                  </View>
                  <TouchableOpacity style={styles.grantButton} onPress={() => requestPermission(permission as any)}>
                    <Text style={styles.grantButtonText}>Grant</Text>
                  </TouchableOpacity>
                </View>
              ))}
            </View>

            <TouchableOpacity style={styles.retryButton} onPress={checkInitialPermissions}>
              <Text style={styles.retryButtonText}>I've granted permissions</Text>
            </TouchableOpacity>
          </>
        )}
      </View>
    </View>
  );
};

