import React, { useEffect, useState } from "react";
import { Text, View } from "react-native";
import { Ionicons } from "@expo/vector-icons";
import Animated, {
  useSharedValue,
  useAnimatedStyle,
  withSpring,
  withRepeat,
  withTiming,
  interpolate,
  Extrapolation,
  Easing,
  withDelay,
  withSequence,
} from "react-native-reanimated";
import { useConfessionStore } from "../state/confessionStore";
import { useTrendingStore } from "../state/trendingStore";

interface PullToRefreshOverlayProps {
  pullDistance: number;
  isRefreshing: boolean;
  threshold?: number;
  context?: "secrets" | "videos";
  onRefreshComplete?: () => void;
}

export default function PullToRefreshOverlay({
  pullDistance,
  isRefreshing,
  threshold = 80,
  context = "secrets",
  onRefreshComplete,
}: PullToRefreshOverlayProps) {
  const { userPreferences } = useConfessionStore();
  const { trendingHashtags } = useTrendingStore();
  const [showTrendingHint, setShowTrendingHint] = useState(false);
  const [trendingHintText, setTrendingHintText] = useState("");

  const rotation = useSharedValue(0);
  const scale = useSharedValue(1);
  const progressArc = useSharedValue(0);
  const trendingHintOpacity = useSharedValue(0);

  const shouldTrigger = pullDistance >= threshold;

  // Update progress arc based on pull distance
  useEffect(() => {
    const progress = Math.min(pullDistance / threshold, 1);
    progressArc.value = withTiming(progress, { duration: 100 });
  }, [pullDistance, threshold]);

  useEffect(() => {
    let timeoutId: NodeJS.Timeout | null = null;

    if (isRefreshing) {
      if (!userPreferences?.reduced_motion) {
        rotation.value = withRepeat(
          withTiming(360, {
            duration: 1000,
            easing: Easing.linear,
          }),
          -1,
        );
        scale.value = withSpring(1.1);
      }
    } else {
      rotation.value = withTiming(0, { duration: 300 });
      scale.value = withSpring(1);

      // Show trending hint after refresh completes
      if (onRefreshComplete && trendingHashtags.length > 0) {
        const topHashtag = trendingHashtags[0];
        setTrendingHintText(`Top tag: ${topHashtag.hashtag}`);
        setShowTrendingHint(true);

        trendingHintOpacity.value = withSequence(
          withDelay(500, withTiming(1, { duration: 300 })),
          withDelay(2000, withTiming(0, { duration: 300 })),
        );

        timeoutId = setTimeout(() => {
          setShowTrendingHint(false);
          onRefreshComplete();
        }, 3000);
      }
    }

    return () => {
      if (timeoutId) {
        clearTimeout(timeoutId);
      }
    };
  }, [isRefreshing, userPreferences?.reduced_motion, trendingHashtags, onRefreshComplete]);

  // Dynamic messaging based on context and pull state
  const getContextualMessage = () => {
    const contextMessages = {
      secrets: {
        pull: "Pull to refresh secrets",
        near: "Release to refresh ‚Äî fetching freshest confessions",
        refreshing: "Refreshing‚Ä¶ new secrets secure and anonymous",
      },
      videos: {
        pull: "Pull to refresh videos",
        near: "Release to refresh ‚Äî loading fresh video confessions",
        refreshing: "Refreshing‚Ä¶ discovering new stories",
      },
    };

    const messages = contextMessages[context];

    if (isRefreshing) return messages.refreshing;
    if (shouldTrigger) return messages.near;
    return messages.pull;
  };

  const containerStyle = useAnimatedStyle(() => {
    const opacity = interpolate(pullDistance, [0, threshold], [0, 1], {
      extrapolateLeft: Extrapolation.CLAMP,
      extrapolateRight: Extrapolation.CLAMP,
    });
    const translateY = interpolate(pullDistance, [0, threshold], [-20, 0], {
      extrapolateLeft: Extrapolation.CLAMP,
      extrapolateRight: Extrapolation.CLAMP,
    });

    return {
      opacity,
      transform: [{ translateY }],
    };
  });

  const iconStyle = useAnimatedStyle(() => ({
    transform: [{ rotate: `${rotation.value}deg` }, { scale: scale.value }],
  }));

  const progressStyle = useAnimatedStyle(() => ({
    transform: [{ rotate: `${progressArc.value * 360}deg` }],
  }));

  const trendingHintStyle = useAnimatedStyle(() => ({
    opacity: trendingHintOpacity.value,
  }));

  return (
    <>
      <Animated.View
        style={[
          {
            position: "absolute",
            top: 60,
            left: 0,
            right: 0,
            alignItems: "center",
            zIndex: 20,
          },
          containerStyle,
        ]}
      >
        {/* Main refresh indicator */}
        <View style={{ position: "relative", marginBottom: 8 }}>
          {/* Progress arc background */}
          {!isRefreshing && (
            <View
              style={{
                position: "absolute",
                width: 44,
                height: 44,
                borderRadius: 22,
                borderWidth: 2,
                borderColor: "rgba(255, 255, 255, 0.2)",
                top: -2,
                left: -2,
              }}
            />
          )}

          {/* Progress arc */}
          {!isRefreshing && (
            <Animated.View
              style={[
                {
                  position: "absolute",
                  width: 44,
                  height: 44,
                  borderRadius: 22,
                  borderWidth: 2,
                  borderColor: "#FFFFFF",
                  borderRightColor: "transparent",
                  borderBottomColor: "transparent",
                  top: -2,
                  left: -2,
                },
                progressStyle,
              ]}
            />
          )}

          {/* Icon container */}
          <Animated.View
            style={[
              {
                width: 40,
                height: 40,
                borderRadius: 20,
                backgroundColor: "rgba(0, 0, 0, 0.7)",
                alignItems: "center",
                justifyContent: "center",
              },
              iconStyle,
            ]}
          >
            {isRefreshing && !userPreferences?.reduced_motion ? (
              <Ionicons name="refresh" size={20} color="#FFFFFF" />
            ) : (
              <Ionicons
                name={isRefreshing ? "refresh" : shouldTrigger ? "checkmark" : "arrow-down"}
                size={20}
                color="#FFFFFF"
              />
            )}
          </Animated.View>
        </View>

        {/* Contextual message */}
        <Text style={{ color: "white", fontSize: 12, fontWeight: "500", textAlign: "center", maxWidth: 256 }}>
          {getContextualMessage()}
        </Text>
      </Animated.View>

      {/* Trending hint */}
      {showTrendingHint && (
        <Animated.View
          style={[
            {
              position: "absolute",
              top: 140,
              left: 0,
              right: 0,
              alignItems: "center",
              zIndex: 19,
            },
            trendingHintStyle,
          ]}
        >
          <View
            style={{
              backgroundColor: "rgba(59, 130, 246, 0.9)",
              paddingHorizontal: 12,
              paddingVertical: 6,
              borderRadius: 16,
              flexDirection: "row",
              alignItems: "center",
            }}
          >
            <Ionicons name="trending-up" size={14} color="#FFFFFF" />
            <Text style={{ color: "white", fontSize: 11, fontWeight: "500", marginLeft: 4 }}>{trendingHintText}</Text>
          </View>
        </Animated.View>
      )}
    </>
  );
}

import React, { useState, useEffect } from "react";
import { View, Text, Pressable, TextInput, Alert, ScrollView } from "react-native";
import { Ionicons } from "@expo/vector-icons";
import * as Haptics from "expo-haptics";
import Animated, { useSharedValue, useAnimatedStyle, withSpring, withTiming, runOnJS } from "react-native-reanimated";
import { Gesture, GestureDetector } from "react-native-gesture-handler";
import { useReportStore } from "../state/reportStore";
import { ReportReason, REPORT_REASON_LABELS, REPORT_REASON_DESCRIPTIONS } from "../types/report";
import { t } from "../utils/i18n";
import { trackInteraction } from "../utils/reviewPrompt";

interface ReportModalProps {
  isVisible: boolean;
  onClose: () => void;
  confessionId?: string;
  replyId?: string;
  contentType: "confession" | "reply";
}

const MODAL_HEIGHT = 500;

const REPORT_REASONS: ReportReason[] = [
  "inappropriate_content",
  "spam",
  "harassment",
  "false_information",
  "violence",
  "hate_speech",
  "other",
];

export default function ReportModal({ isVisible, onClose, confessionId, replyId, contentType }: ReportModalProps) {
  const [selectedReason, setSelectedReason] = useState<ReportReason | null>(null);
  const [additionalDetails, setAdditionalDetails] = useState("");
  const [isSubmitting, setIsSubmitting] = useState(false);

  const { createReport, isLoading, error, clearError } = useReportStore();

  const translateY = useSharedValue(MODAL_HEIGHT);
  const backdropOpacity = useSharedValue(0);

  useEffect(() => {
    if (isVisible) {
      translateY.value = withSpring(0, { damping: 15, stiffness: 150 });
      backdropOpacity.value = withTiming(0.5, { duration: 300 });
    } else {
      translateY.value = withSpring(MODAL_HEIGHT, { damping: 15, stiffness: 150 });
      backdropOpacity.value = withTiming(0, { duration: 300 });
      // Reset form when modal closes
      setSelectedReason(null);
      setAdditionalDetails("");
      clearError();
    }
  }, [isVisible]);

  useEffect(() => {
    if (error) {
      Alert.alert("Error", error);
      clearError();
    }
  }, [error, clearError]);

  const panGesture = Gesture.Pan()
    .onUpdate((event) => {
      if (event.translationY > 0) {
        translateY.value = event.translationY;
      }
    })
    .onEnd((event) => {
      "worklet";
      if (event.translationY > MODAL_HEIGHT * 0.3) {
        translateY.value = withSpring(MODAL_HEIGHT);
        runOnJS(onClose)();
      } else {
        translateY.value = withSpring(0);
      }
    });

  const modalStyle = useAnimatedStyle(() => ({
    transform: [{ translateY: translateY.value }],
  }));

  const backdropStyle = useAnimatedStyle(() => ({
    opacity: backdropOpacity.value,
  }));

  const handleSubmit = async () => {
    if (!selectedReason) {
      Alert.alert(t("common.error"), "Please select a reason for reporting");
      return;
    }

    if (selectedReason === "other" && !additionalDetails.trim()) {
      Alert.alert(t("common.error"), "Please provide additional details for 'Other' reason");
      return;
    }

    setIsSubmitting(true);
    trackInteraction(); // Track user interaction for review prompting

    try {
      await createReport({
        confessionId,
        replyId,
        reason: selectedReason,
        additionalDetails: additionalDetails.trim() || undefined,
      });

      Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Light);
      Alert.alert(t("reports.submitted"), t("reports.submittedMessage"), [{ text: t("common.ok"), onPress: onClose }]);
    } catch {
      // Error is handled by the store and useEffect above
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleReasonSelect = (reason: ReportReason) => {
    setSelectedReason(reason);
    Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Light);
  };

  if (!isVisible) return null;

  return (
    <View style={{ position: "absolute", top: 0, left: 0, right: 0, bottom: 0, zIndex: 1000 }}>
      {/* Backdrop */}
      <Animated.View
        style={[
          {
            position: "absolute",
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            backgroundColor: "black",
          },
          backdropStyle,
        ]}
      >
        <Pressable style={{ flex: 1 }} onPress={onClose} />
      </Animated.View>

      {/* Modal */}
      <GestureDetector gesture={panGesture}>
        <Animated.View
          style={[
            {
              position: "absolute",
              bottom: 0,
              left: 0,
              right: 0,
              height: MODAL_HEIGHT,
              backgroundColor: "#1A1A1A",
              borderTopLeftRadius: 20,
              borderTopRightRadius: 20,
              paddingHorizontal: 20,
              paddingTop: 12,
            },
            modalStyle,
          ]}
        >
          {/* Handle */}
          <View className="items-center mb-4">
            <View className="w-10 h-1 bg-gray-600 rounded-full" />
          </View>

          {/* Header */}
          <View className="flex-row items-center justify-between mb-6">
            <Text className="text-white text-18 font-semibold">{t("reports.title")}</Text>
            <Pressable onPress={onClose}>
              <Ionicons name="close" size={24} color="#8B98A5" />
            </Pressable>
          </View>

          <ScrollView showsVerticalScrollIndicator={false} className="flex-1">
            {/* Reason Selection */}
            <Text className="text-white text-16 font-medium mb-4">Why are you reporting this {contentType}?</Text>

            <View className="space-y-2 mb-6">
              {REPORT_REASONS.map((reason) => (
                <Pressable
                  key={reason}
                  className={`flex-row items-center p-3 rounded-lg border ${
                    selectedReason === reason ? "border-red-500 bg-red-500/10" : "border-gray-700 bg-gray-800/50"
                  }`}
                  onPress={() => handleReasonSelect(reason)}
                >
                  <View className="mr-3">
                    <Ionicons
                      name={selectedReason === reason ? "radio-button-on" : "radio-button-off"}
                      size={20}
                      color={selectedReason === reason ? "#EF4444" : "#8B98A5"}
                    />
                  </View>
                  <View className="flex-1">
                    <Text className="text-white text-15 font-medium">{REPORT_REASON_LABELS[reason]}</Text>
                    <Text className="text-gray-400 text-13 mt-1">{REPORT_REASON_DESCRIPTIONS[reason]}</Text>
                  </View>
                </Pressable>
              ))}
            </View>

            {/* Additional Details */}
            {(selectedReason === "other" || selectedReason) && (
              <View className="mb-6">
                <Text className="text-white text-16 font-medium mb-3">
                  Additional Details {selectedReason === "other" ? "(Required)" : "(Optional)"}
                </Text>
                <TextInput
                  className="bg-gray-800 border border-gray-700 rounded-lg p-3 text-white text-15"
                  placeholder={`Provide more details about this ${contentType}...`}
                  placeholderTextColor="#8B98A5"
                  value={additionalDetails}
                  onChangeText={setAdditionalDetails}
                  multiline
                  numberOfLines={3}
                  textAlignVertical="top"
                />
              </View>
            )}
          </ScrollView>

          {/* Submit Button */}
          <View className="pt-4 pb-6">
            <Pressable
              className={`rounded-lg py-4 ${
                selectedReason && !isSubmitting && !isLoading ? "bg-red-500" : "bg-gray-700"
              }`}
              onPress={handleSubmit}
              disabled={!selectedReason || isSubmitting || isLoading}
            >
              <Text className="text-white text-16 font-semibold text-center">
                {isSubmitting || isLoading ? "Submitting..." : "Submit Report"}
              </Text>
            </Pressable>
          </View>
        </Animated.View>
      </GestureDetector>
    </View>
  );
}

import React from "react";
import { View, Text, Pressable, ViewStyle } from "react-native";
import { Ionicons } from "@expo/vector-icons";
import Animated, { useSharedValue, useAnimatedStyle, withSpring } from "react-native-reanimated";
import { usePreferenceAwareHaptics } from "../utils/haptics";

export interface TabItem {
  id: string;
  label: string;
  icon?: keyof typeof Ionicons.glyphMap;
  badge?: number;
}

interface SegmentedTabsProps {
  tabs: TabItem[];
  activeTab: string;
  onTabChange: (tabId: string) => void;
  style?: ViewStyle;
}

export default function SegmentedTabs({ tabs, activeTab, onTabChange, style }: SegmentedTabsProps) {
  const { impactAsync } = usePreferenceAwareHaptics();
  const activeIndex = tabs.findIndex((tab) => tab.id === activeTab);
  const indicatorPosition = useSharedValue(activeIndex);
  const [containerWidth, setContainerWidth] = React.useState(0);

  React.useEffect(() => {
    indicatorPosition.value = withSpring(activeIndex, {
      damping: 15,
      stiffness: 150,
    });
  }, [activeIndex]);

  const tabWidth = containerWidth > 0 ? containerWidth / Math.max(1, tabs.length) : 0;

  const indicatorStyle = useAnimatedStyle(() => ({
    left: indicatorPosition.value * tabWidth,
    width: tabWidth,
  }));

  const handleTabPress = (tabId: string) => {
    // Only trigger haptic if it's a different tab to avoid excessive feedback
    if (tabId !== activeTab) {
      impactAsync();
    }
    onTabChange(tabId);
  };

  return (
    <View
      style={[
        {
          backgroundColor: "transparent",
          borderRadius: 16,
          padding: 2,
          flexDirection: "row",
          position: "relative",
        },
        style,
      ]}
      onLayout={(e) => setContainerWidth(e.nativeEvent.layout.width)}
    >
      {/* Active indicator with enhanced styling */}
      <Animated.View
        style={[
          {
            position: "absolute",
            top: 1,
            bottom: 1,
            backgroundColor: "#3B82F6",
            borderRadius: 10,
            zIndex: 1,
            shadowColor: "#3B82F6",
            shadowOffset: { width: 0, height: 1 },
            shadowOpacity: 0.25,
            shadowRadius: 6,
            elevation: 4,
          },
          indicatorStyle,
        ]}
      />

      {/* Tab buttons */}
      {tabs.map((tab, index) => {
        const isActive = tab.id === activeTab;

        return (
          <Pressable
            key={tab.id}
            onPress={() => handleTabPress(tab.id)}
            style={{
              flex: 1,
              paddingVertical: 10,
              paddingHorizontal: 8,
              alignItems: "center",
              justifyContent: "center",
              flexDirection: "row",
              zIndex: 2,
              borderRadius: 10,
            }}
          >
            {/* Icon */}
            {Boolean(tab.icon) && (
              <Ionicons
                name={tab.icon}
                size={12}
                color={isActive ? "#FFFFFF" : "#9CA3AF"}
                style={{ marginRight: tab.label ? 4 : 0 }}
              />
            )}

            {/* Label */}
            {Boolean(tab.label) && (
              <Text
                style={{
                  fontSize: 11,
                  fontWeight: "600",
                  color: isActive ? "#FFFFFF" : "#9CA3AF",
                }}
              >
                {tab.label}
              </Text>
            )}

            {/* Badge */}
            {(tab.badge ?? 0) > 0 && (
              <View
                style={{
                  backgroundColor: "#EF4444",
                  borderRadius: 10,
                  minWidth: 18,
                  height: 18,
                  alignItems: "center",
                  justifyContent: "center",
                  marginLeft: 4,
                  shadowColor: "#EF4444",
                  shadowOffset: { width: 0, height: 1 },
                  shadowOpacity: 0.5,
                  shadowRadius: 3,
                  elevation: 3,
                }}
              >
                <Text
                  style={{
                    color: "#FFFFFF",
                    fontSize: 10,
                    fontWeight: "700",
                  }}
                >
                  {(tab.badge ?? 0) > 99 ? "99+" : String(tab.badge ?? 0)}
                </Text>
              </View>
            )}
          </Pressable>
        );
      })}
    </View>
  );
}

import React, { useEffect, useState } from "react";
import { View, Text, Pressable, Share, Alert } from "react-native";
import { Ionicons } from "@expo/vector-icons";
import * as Clipboard from "expo-clipboard";
import { usePreferenceAwareHaptics } from "../utils/haptics";
import { generateConfessionLink, generateShareMessage } from "../utils/links";
import Animated, { useSharedValue, useAnimatedStyle, withSpring, withTiming, runOnJS } from "react-native-reanimated";
import { Gesture, GestureDetector } from "react-native-gesture-handler";

import ReportModal from "./ReportModal";

interface ShareModalProps {
  isVisible: boolean;
  onClose: () => void;
  confessionId: string;
  confessionText: string;
}

const MODAL_HEIGHT = 300;

export default function ShareModal({ isVisible, onClose, confessionId, confessionText }: ShareModalProps) {
  const { impactAsync } = usePreferenceAwareHaptics();
  const [reportModalVisible, setReportModalVisible] = useState(false);
  const translateY = useSharedValue(MODAL_HEIGHT);
  const backdropOpacity = useSharedValue(0);

  useEffect(() => {
    if (isVisible) {
      translateY.value = withSpring(0, { damping: 15, stiffness: 150 });
      backdropOpacity.value = withTiming(0.5, { duration: 300 });
    } else {
      translateY.value = withSpring(MODAL_HEIGHT, { damping: 15, stiffness: 150 });
      backdropOpacity.value = withTiming(0, { duration: 300 });
    }
  }, [isVisible]);

  const panGesture = Gesture.Pan()
    .onUpdate((event) => {
      if (event.translationY > 0) {
        translateY.value = event.translationY;
      }
    })
    .onEnd((event) => {
      "worklet";
      if (event.translationY > MODAL_HEIGHT * 0.3) {
        translateY.value = withSpring(MODAL_HEIGHT);
        runOnJS(onClose)();
      } else {
        translateY.value = withSpring(0);
      }
    });

  const modalStyle = useAnimatedStyle(() => ({
    transform: [{ translateY: translateY.value }],
  }));

  const backdropStyle = useAnimatedStyle(() => ({
    opacity: backdropOpacity.value,
  }));

  const handleNativeShare = async () => {
    try {
      const shareUrl = generateConfessionLink(confessionId);
      const shareMessage = generateShareMessage(confessionText, confessionId);

      await Share.share({
        message: shareMessage,
        url: shareUrl,
      });
      impactAsync();
      onClose();
    } catch (error) {
      console.error("Share failed:", error);
    }
  };

  const handleCopyLink = async () => {
    try {
      const shareUrl = generateConfessionLink(confessionId);
      await Clipboard.setStringAsync(shareUrl);
      Alert.alert("Copied!", "Link copied to clipboard");
      impactAsync();
      onClose();
    } catch (error) {
      console.error("Copy failed:", error);
    }
  };

  const handleCopyText = async () => {
    try {
      await Clipboard.setStringAsync(confessionText);
      Alert.alert("Copied!", "Confession text copied to clipboard");
      impactAsync();
      onClose();
    } catch (error) {
      console.error("Copy failed:", error);
    }
  };

  const handleReport = () => {
    setReportModalVisible(true);
    impactAsync();
  };

  const handleReportModalClose = () => {
    setReportModalVisible(false);
  };

  if (!isVisible) return null;

  return (
    <View style={{ position: "absolute", top: 0, left: 0, right: 0, bottom: 0, zIndex: 1000 }}>
      {/* Backdrop */}
      <Animated.View
        style={[
          {
            position: "absolute",
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            backgroundColor: "black",
          },
          backdropStyle,
        ]}
      >
        <Pressable style={{ flex: 1 }} onPress={onClose} />
      </Animated.View>

      {/* Modal */}
      <GestureDetector gesture={panGesture}>
        <Animated.View
          style={[
            {
              position: "absolute",
              bottom: 0,
              left: 0,
              right: 0,
              height: MODAL_HEIGHT,
              backgroundColor: "#1A1A1A",
              borderTopLeftRadius: 20,
              borderTopRightRadius: 20,
              paddingHorizontal: 20,
              paddingTop: 12,
            },
            modalStyle,
          ]}
        >
          {/* Handle */}
          <View className="items-center mb-6">
            <View className="w-10 h-1 bg-gray-600 rounded-full" />
          </View>

          {/* Share Options */}
          <View className="space-y-4">
            <Pressable className="flex-row items-center py-4 px-2" onPress={handleNativeShare}>
              <View className="w-10 h-10 bg-blue-500 rounded-full items-center justify-center mr-4">
                <Ionicons name="share-outline" size={20} color="#FFFFFF" />
              </View>
              <View className="flex-1">
                <Text className="text-white text-16 font-medium">Share</Text>
                <Text className="text-gray-400 text-13">Share this confession</Text>
              </View>
            </Pressable>

            <Pressable className="flex-row items-center py-4 px-2" onPress={handleCopyLink}>
              <View className="w-10 h-10 bg-green-500 rounded-full items-center justify-center mr-4">
                <Ionicons name="link-outline" size={20} color="#FFFFFF" />
              </View>
              <View className="flex-1">
                <Text className="text-white text-16 font-medium">Copy Link</Text>
                <Text className="text-gray-400 text-13">Copy link to clipboard</Text>
              </View>
            </Pressable>

            <Pressable className="flex-row items-center py-4 px-2" onPress={handleCopyText}>
              <View className="w-10 h-10 bg-purple-500 rounded-full items-center justify-center mr-4">
                <Ionicons name="copy-outline" size={20} color="#FFFFFF" />
              </View>
              <View className="flex-1">
                <Text className="text-white text-16 font-medium">Copy Text</Text>
                <Text className="text-gray-400 text-13">Copy confession text</Text>
              </View>
            </Pressable>

            <Pressable className="flex-row items-center py-4 px-2" onPress={handleReport}>
              <View className="w-10 h-10 bg-red-500 rounded-full items-center justify-center mr-4">
                <Ionicons name="flag-outline" size={20} color="#FFFFFF" />
              </View>
              <View className="flex-1">
                <Text className="text-white text-16 font-medium">Report</Text>
                <Text className="text-gray-400 text-13">Report inappropriate content</Text>
              </View>
            </Pressable>
          </View>
        </Animated.View>
      </GestureDetector>

      {/* Report Modal */}
      <ReportModal
        isVisible={reportModalVisible}
        onClose={handleReportModalClose}
        confessionId={confessionId}
        contentType="confession"
      />
    </View>
