import { useState, useEffect } from "react";
import { AccessibilityInfo, PixelRatio } from "react-native";

export interface DynamicTypeSettings {
  fontScale: number;
  isBoldTextEnabled: boolean;
  isReduceMotionEnabled: boolean;
  isScreenReaderEnabled: boolean;
}

export interface ScaledFontSizes {
  xs: number;
  sm: number;
  base: number;
  lg: number;
  xl: number;
  "2xl": number;
  "3xl": number;
  "4xl": number;
  "5xl": number;
}

// Base font sizes (in points)
const BASE_FONT_SIZES = {
  xs: 12,
  sm: 14,
  base: 16,
  lg: 18,
  xl: 20,
  "2xl": 24,
  "3xl": 30,
  "4xl": 36,
  "5xl": 48,
};

/**
 * Hook for supporting dynamic type and accessibility preferences
 * Automatically scales fonts and adjusts UI based on user preferences
 */
export const useDynamicType = () => {
  const [settings, setSettings] = useState<DynamicTypeSettings>({
    fontScale: PixelRatio.getFontScale(),
    isBoldTextEnabled: false,
    isReduceMotionEnabled: false,
    isScreenReaderEnabled: false,
  });

  useEffect(() => {
    const loadAccessibilitySettings = async () => {
      try {
        const [isBoldTextEnabled, isReduceMotionEnabled, isScreenReaderEnabled] = await Promise.all([
          // Feature-detect accessibility APIs before calling
          AccessibilityInfo.isBoldTextEnabled?.() ?? Promise.resolve(false),
          AccessibilityInfo.isReduceMotionEnabled?.() ?? Promise.resolve(false),
          AccessibilityInfo.isScreenReaderEnabled?.() ?? Promise.resolve(false),
        ]);

        setSettings((prev) => ({
          ...prev,
          isBoldTextEnabled,
          isReduceMotionEnabled,
          isScreenReaderEnabled,
        }));
      } catch (error) {
        if (__DEV__) {
          console.warn("Failed to load accessibility settings:", error);
        }
      }
    };

    loadAccessibilitySettings();

    // Listen for changes in accessibility settings
    const subscriptions = [
      AccessibilityInfo.addEventListener("reduceMotionChanged", (isReduceMotionEnabled) => {
        setSettings((prev) => ({ ...prev, isReduceMotionEnabled }));
      }),
      AccessibilityInfo.addEventListener("screenReaderChanged", (isScreenReaderEnabled) => {
        setSettings((prev) => ({ ...prev, isScreenReaderEnabled }));
      }),
      AccessibilityInfo.addEventListener("boldTextChanged", (isBoldTextEnabled) => {
        setSettings((prev) => ({ ...prev, isBoldTextEnabled }));
      }),
    ].filter(Boolean);

    return () => {
      subscriptions.forEach((subscription) => subscription?.remove());
    };
  }, []);

  // Calculate scaled font sizes based on user preferences
  const getScaledFontSizes = (): ScaledFontSizes => {
    const scale = settings.fontScale;
    const finalScale = scale;

    return Object.entries(BASE_FONT_SIZES).reduce((acc, [key, size]) => {
      acc[key as keyof ScaledFontSizes] = Math.round(size * finalScale);
      return acc;
    }, {} as ScaledFontSizes);
  };

  // Get font weight based on bold text preference
  const getFontWeight = (baseWeight: string = "normal"): string => {
    if (settings.isBoldTextEnabled) {
      switch (baseWeight) {
        case "normal":
        case "400":
          return "600";
        case "medium":
        case "500":
          return "700";
        case "semibold":
        case "600":
          return "700";
        case "bold":
        case "700":
          return "800";
        default:
          return "bold";
      }
    }
    return baseWeight;
  };

  // Get animation duration based on reduce motion preference
  const getAnimationDuration = (baseDuration: number): number => {
    return settings.isReduceMotionEnabled ? 0 : baseDuration;
  };

  // Get spacing scale based on font scale
  const getSpacingScale = (): number => {
    return settings.fontScale > 1.2 ? 1.15 : 1;
  };

  // Check if text should be larger for better readability
  const shouldUseLargerText = (): boolean => {
    return settings.fontScale > 1.3;
  };

  // Get minimum touch target size (44pt recommended by Apple/Google)
  const getMinTouchTargetSize = (): number => {
    const baseSize = 44;
    return settings.fontScale > 1.2 ? baseSize * 1.2 : baseSize;
  };

  // Get contrast ratio adjustments
  const getContrastAdjustments = () => {
    return {
      // Increase contrast for better readability
      textOpacity: settings.isScreenReaderEnabled ? 1 : 0.9,
      borderOpacity: settings.isScreenReaderEnabled ? 1 : 0.8,
      backgroundOpacity: settings.isScreenReaderEnabled ? 1 : 0.95,
    };
  };

  return {
    settings,
    scaledFontSizes: getScaledFontSizes(),
    getFontWeight,
    getAnimationDuration,
    getSpacingScale,
    shouldUseLargerText,
    getMinTouchTargetSize,
    getContrastAdjustments,
  };
};

// Utility functions for responsive design
export const DynamicTypeUtils = {
  // Scale a value based on font scale
  scaleValue: (value: number, fontScale: number): number => {
    return Math.round(value * fontScale);
  },

  // Get responsive padding based on text size
  getResponsivePadding: (basePadding: number, fontScale: number): number => {
    const scale = Math.max(1, fontScale);
    return Math.round(basePadding * scale);
  },

  // Get responsive margin based on text size
  getResponsiveMargin: (baseMargin: number, fontScale: number): number => {
    const scale = Math.max(1, fontScale);
    return Math.round(baseMargin * scale);
  },

  // Calculate line height based on font size and accessibility needs
  getLineHeight: (fontSize: number, isLargeText: boolean = false): number => {
    const baseRatio = isLargeText ? 1.6 : 1.5;
    return Math.round(fontSize * baseRatio);
  },

  // Get accessible color contrast with WCAG compliance
  getAccessibleColor: (baseColor: string, backgroundColor: string, isHighContrast: boolean = false): string => {
    // Helper function to convert hex to RGB
    const hexToRgb = (hex: string): { r: number; g: number; b: number } | null => {
      // Remove # if present and convert to lowercase
      const cleanHex = hex.replace(/^#/, "").toLowerCase();

      // Check for 3-digit or 6-digit hex format
      const shortHexRegex = /^([a-f\d])([a-f\d])([a-f\d])$/;
      const longHexRegex = /^([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/;

      let result = longHexRegex.exec(cleanHex);

      if (!result) {
        // Try 3-digit format and expand to 6-digit
        const shortResult = shortHexRegex.exec(cleanHex);
        if (shortResult) {
          // Expand 3-digit to 6-digit (e.g., "abc" -> "aabbcc")
          const expandedHex =
            shortResult[1] + shortResult[1] + shortResult[2] + shortResult[2] + shortResult[3] + shortResult[3];
          result = longHexRegex.exec(expandedHex);
        }
      }

      return result
        ? {
            r: parseInt(result[1], 16),
            g: parseInt(result[2], 16),
            b: parseInt(result[3], 16),
          }
        : null;
    };

    // Calculate relative luminance
    const getLuminance = (r: number, g: number, b: number): number => {
      const [rs, gs, bs] = [r, g, b].map((c) => {
        c = c / 255;
        return c <= 0.03928 ? c / 12.92 : Math.pow((c + 0.055) / 1.055, 2.4);
      });
      return 0.2126 * rs + 0.7152 * gs + 0.0722 * bs;
    };

    // Calculate contrast ratio
    const getContrastRatio = (lum1: number, lum2: number): number => {
      const brightest = Math.max(lum1, lum2);
      const darkest = Math.min(lum1, lum2);
      return (brightest + 0.05) / (darkest + 0.05);
    };

    try {
      const baseRgb = hexToRgb(baseColor);
      const bgRgb = hexToRgb(backgroundColor);

      if (!baseRgb || !bgRgb) {
        // Fallback for invalid colors
        return isHighContrast ? "#000000" : baseColor;
      }

      const baseLum = getLuminance(baseRgb.r, baseRgb.g, baseRgb.b);
      const bgLum = getLuminance(bgRgb.r, bgRgb.g, bgRgb.b);
      const contrast = getContrastRatio(baseLum, bgLum);

      // WCAG AA requires 4.5:1 for normal text, 7:1 for AAA
      const requiredContrast = isHighContrast ? 7 : 4.5;

      if (contrast >= requiredContrast) {
        return baseColor;
      }

      // If contrast is insufficient, return black or white based on background
      return bgLum > 0.5 ? "#000000" : "#FFFFFF";
    } catch (error) {
      // Fallback on error
      return isHighContrast ? "#000000" : baseColor;
    }
  },
};

import React, { useState, useCallback, useRef, useEffect, createContext, useContext } from "react";

export interface LoadingState {
  isLoading: boolean;
  error: string | null;
  data: any;
  lastUpdated: number | null;
}

export interface LoadingStates {
  [key: string]: LoadingState;
}

export interface LoadingOptions {
  key: string;
  minLoadingTime?: number; // Minimum time to show loading state (prevents flashing)
  timeout?: number; // Timeout for the operation
  retryCount?: number; // Number of automatic retries
  retryDelay?: number; // Delay between retries in ms
}

/**
 * Hook for managing consistent loading states across the application
 * Provides unified loading, error, and success states with retry logic
 */
export const useLoadingStates = () => {
  const [states, setStates] = useState<LoadingStates>({});
  const timeoutsRef = useRef<Map<string, ReturnType<typeof setTimeout>>>(new Map());
  const retriesRef = useRef<Map<string, number>>(new Map());
  const callTokensRef = useRef<Map<string, number>>(new Map());
  const isMountedRef = useRef(true);

  // Initialize a loading state
  const initializeState = useCallback((key: string, initialData?: any) => {
    setStates((prev) => ({
      ...prev,
      [key]: {
        isLoading: false,
        error: null,
        data: initialData || null,
        lastUpdated: null,
      },
    }));
  }, []);

  // Set loading state
  const setLoading = useCallback((key: string, isLoading: boolean) => {
    if (!isMountedRef.current) return;

    setStates((prev) => {
      const existing = prev[key] ?? { isLoading: false, error: null, data: null, lastUpdated: null };
      return {
        ...prev,
        [key]: {
          ...existing,
          isLoading,
          error: isLoading ? null : existing.error,
        },
      };
    });
  }, []);

  // Set error state
  const setError = useCallback((key: string, error: string | null) => {
    if (!isMountedRef.current) return;

    setStates((prev) => {
      const existing = prev[key] ?? { isLoading: false, error: null, data: null, lastUpdated: null };
      return {
        ...prev,
        [key]: {
          ...existing,
          isLoading: false,
          error,
        },
      };
    });
  }, []);

  // Set success state with data
  const setSuccess = useCallback((key: string, data: any) => {
    if (!isMountedRef.current) return;

    setStates((prev) => {
      const existing = prev[key] ?? { isLoading: false, error: null, data: null, lastUpdated: null };
      return {
        ...prev,
        [key]: {
          ...existing,
          isLoading: false,
          error: null,
          data,
          lastUpdated: Date.now(),
        },
      };
    });

    // Clear retry count on success
    retriesRef.current.delete(key);
  }, []);

  // Execute an async operation with loading state management
  const executeWithLoading = useCallback(
    async function <T>(options: LoadingOptions, operation: () => Promise<T>): Promise<T | null> {
      const { key, minLoadingTime = 300, timeout = 30000, retryCount = 0, retryDelay = 1000 } = options;

      // Generate call token to prevent race conditions
      const currentToken = (callTokensRef.current.get(key) || 0) + 1;
      callTokensRef.current.set(key, currentToken);

      const startTime = Date.now();
      setLoading(key, true);

      // Clear any existing timeout
      const existingTimeout = timeoutsRef.current.get(key);
      if (existingTimeout) {
        clearTimeout(existingTimeout);
      }

      try {
        // Set up timeout
        const timeoutPromise = new Promise<never>((_, reject) => {
          const timeoutId = setTimeout(() => {
            timeoutsRef.current.delete(key); // Remove from map when timeout fires
            reject(new Error("Operation timed out"));
          }, timeout);
          timeoutsRef.current.set(key, timeoutId);
        });

        // Execute operation with timeout
        const result = await Promise.race([operation(), timeoutPromise]);

        // Clear timeout
        const timeoutId = timeoutsRef.current.get(key);
        if (timeoutId) {
          clearTimeout(timeoutId);
          timeoutsRef.current.delete(key);
        }

        // Ensure minimum loading time
        const elapsedTime = Date.now() - startTime;
        if (elapsedTime < minLoadingTime) {
          await new Promise((resolve) => setTimeout(resolve, minLoadingTime - elapsedTime));
        }

        // Only update state if this is still the latest call and component is mounted
        if (callTokensRef.current.get(key) === currentToken && isMountedRef.current) {
          setSuccess(key, result);
        }
        return result;
      } catch (error) {
        // Clear timeout
        const timeoutId = timeoutsRef.current.get(key);
        if (timeoutId) {
          clearTimeout(timeoutId);
          timeoutsRef.current.delete(key);
        }

        const errorMessage = error instanceof Error ? error.message : "An error occurred";

        // Handle retries
        const currentRetries = retriesRef.current.get(key) || 0;
        if (currentRetries < retryCount) {
          retriesRef.current.set(key, currentRetries + 1);

          // Wait before retry
          await new Promise((resolve) => setTimeout(resolve, retryDelay));

          // Retry the operation
          return executeWithLoading(options, operation);
        }

        // Ensure minimum loading time even for errors
        const elapsedTime = Date.now() - startTime;
        if (elapsedTime < minLoadingTime) {
          await new Promise((resolve) => setTimeout(resolve, minLoadingTime - elapsedTime));
        }

        // Only update state if this is still the latest call and component is mounted
        if (callTokensRef.current.get(key) === currentToken && isMountedRef.current) {
          setError(key, errorMessage);
        }
        return null;
      }
    },
    [states, initializeState, setLoading, setSuccess, setError],
  );

  // Retry a failed operation
  const retry = useCallback(
    async function <T>(key: string, operation: () => Promise<T>, options?: Partial<LoadingOptions>): Promise<T | null> {
      // Reset retry count
      retriesRef.current.delete(key);

      return executeWithLoading({ key, ...options }, operation);
    },
    [executeWithLoading],
  );

  // Clear a specific state
  const clearState = useCallback((key: string) => {
    setStates((prev) => {
      const newStates = { ...prev };
      delete newStates[key];
      return newStates;
    });

    // Clear any associated timeouts and retries
    const timeoutId = timeoutsRef.current.get(key);
    if (timeoutId) {
      clearTimeout(timeoutId);
      timeoutsRef.current.delete(key);
    }
    retriesRef.current.delete(key);
  }, []);

  // Clear all states
  const clearAllStates = useCallback(() => {
    setStates({});

    // Clear all timeouts
    timeoutsRef.current.forEach((timeoutId) => clearTimeout(timeoutId));
    timeoutsRef.current.clear();
    retriesRef.current.clear();
  }, []);

  // Get state for a specific key
  const getState = useCallback(
    (key: string): LoadingState => {
      return (
        states[key] || {
          isLoading: false,
          error: null,
          data: null,
          lastUpdated: null,
        }
      );
    },
    [states],
  );

  // Check if any state is loading
  const isAnyLoading = useCallback(() => {
    return Object.values(states).some((state) => state.isLoading);
  }, [states]);

  // Get all errors
  const getAllErrors = useCallback(() => {
    return Object.entries(states)
      .filter(([_, state]) => state.error)
      .map(([key, state]) => ({ key, error: state.error }));
  }, [states]);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      isMountedRef.current = false;
      timeoutsRef.current.forEach((timeoutId) => clearTimeout(timeoutId));
      timeoutsRef.current.clear();
      callTokensRef.current.clear();
    };
  }, []);

  return {
    states,
    initializeState,
    setLoading,
    setError,
    setSuccess,
    executeWithLoading,
    retry,
    clearState,
    clearAllStates,
    getState,
    isAnyLoading,
    getAllErrors,
  };
};

// React Context pattern for global loading states
const LoadingStatesContext = createContext<ReturnType<typeof useLoadingStates> | null>(null);

export const LoadingStatesProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const loadingStates = useLoadingStates();
  return <LoadingStatesContext.Provider value={loadingStates}>{children}</LoadingStatesContext.Provider>;
};

export const useGlobalLoadingStates = () => {
  const context = useContext(LoadingStatesContext);
  if (!context) {
    throw new Error("useGlobalLoadingStates must be used within a LoadingStatesProvider");
  }
  return context;
};

import { useEffect, useRef, useCallback, useState } from "react";
import { AppState, AppStateStatus } from "react-native";

import { VideoDataService } from "../services/VideoDataService";
import { consentStore } from "../state/consentStore";

interface PlaybackStatusSnapshot {
  isLoaded: boolean;
  isPlaying: boolean;
  isBuffering?: boolean;
  positionMillis?: number;
  durationMillis?: number;
  didJustFinish?: boolean;
}

export interface VideoAnalyticsConfig {
  videoId: string;
  videoDuration?: number;
  enableDetailedTracking?: boolean;
  trackQualityChanges?: boolean;
  sessionId?: string;
  onEngagementUpdate?: (score: number) => void;
  onCompletionDetected?: (completionRate: number) => void;
}

export interface VideoAnalyticsState {
  watchTime: number;
  completionRate: number;
  engagementScore: number;
  isPlaying: boolean;
  bufferingCount: number;
  seekCount: number;
  sessionActive: boolean;
}

interface WatchTimeState {
  startTime: number | null;
  totalWatchTime: number;
  lastPosition: number;
}

const WATCH_TIME_UPDATE_INTERVAL = 1000; // Update watch time every second
const ENGAGEMENT_CALC_INTERVAL = 5000; // Calculate engagement every 5 seconds
const POSITION_TOLERANCE = 2; // 2 second tolerance for seek detection

export function useVideoAnalyticsTracker(config: VideoAnalyticsConfig) {
  const {
    videoId,
    videoDuration,
    enableDetailedTracking = true,
    trackQualityChanges = true,
    sessionId: providedSessionId,
    onEngagementUpdate,
    onCompletionDetected,
  } = config;

  const [analyticsState, setAnalyticsState] = useState<VideoAnalyticsState>({
    watchTime: 0,
    completionRate: 0,
    engagementScore: 0,
    isPlaying: false,
    bufferingCount: 0,
    seekCount: 0,
    sessionActive: false,
  });

  const watchTimeRef = useRef<WatchTimeState>({
    startTime: null,
    totalWatchTime: 0,
    lastPosition: 0,
  });

  const sessionIdRef = useRef<string | null>(providedSessionId || null);
  const watchTimeIntervalRef = useRef<NodeJS.Timeout | null>(null);
  const engagementIntervalRef = useRef<NodeJS.Timeout | null>(null);
  const appStateRef = useRef<AppStateStatus>(AppState.currentState);
  const lastPlaybackStatusRef = useRef<PlaybackStatusSnapshot | null>(null);
  const hasTrackedImpressionRef = useRef(false);
  const isBufferingRef = useRef(false);
  const lastQualityRef = useRef<string | null>(null);
  const hasMarkedCompletedRef = useRef(false);

  // Initialize session
  useEffect(() => {
    if (!videoId || !consentStore.preferences.analytics) return;

    // Get or create session
    if (!sessionIdRef.current) {
      sessionIdRef.current = VideoDataService.getOrCreateSession(videoId);
    }

    // Track impression
    if (!hasTrackedImpressionRef.current) {
      VideoDataService.trackVideoEvent(videoId, {
        type: "impression",
        timestamp: Date.now(),
        metadata: { videoDuration },
      });
      hasTrackedImpressionRef.current = true;
    }

    setAnalyticsState((prev) => ({ ...prev, sessionActive: true }));

    return () => {
      // Cleanup on unmount
      if (sessionIdRef.current) {
        VideoDataService.endSession(sessionIdRef.current);
      }
    };
  }, [videoId, videoDuration]);

  // Handle app state changes
  useEffect(() => {
    const subscription = AppState.addEventListener("change", (nextAppState: AppStateStatus) => {
      if (appStateRef.current === "active" && nextAppState.match(/inactive|background/)) {
        // App going to background - pause tracking
        handlePause();
      } else if (
        appStateRef.current.match(/inactive|background/) &&
        nextAppState === "active" &&
        analyticsState.isPlaying
      ) {
        // App returning to foreground - resume tracking if was playing
        handlePlay();
      }
      appStateRef.current = nextAppState;
    });

    return () => {
      subscription.remove();
    };
  }, [analyticsState.isPlaying]);

  // Start watch time tracking
  const startWatchTimeTracking = useCallback(() => {
    if (watchTimeIntervalRef.current) {
      clearInterval(watchTimeIntervalRef.current);
    }

    watchTimeRef.current.startTime = Date.now();

    watchTimeIntervalRef.current = setInterval(() => {
      if (watchTimeRef.current.startTime) {
        const elapsed = (Date.now() - watchTimeRef.current.startTime) / 1000;
        const newTotalTime = watchTimeRef.current.totalWatchTime + elapsed;
        watchTimeRef.current.totalWatchTime = newTotalTime;
        watchTimeRef.current.startTime = Date.now();

        setAnalyticsState((prev) => ({
          ...prev,
          watchTime: newTotalTime,
          completionRate: videoDuration ? (newTotalTime / videoDuration) * 100 : 0,
        }));

        // Update cached analytics
        VideoDataService.updateAnalyticsCache(videoId, {
          watchTime: newTotalTime,
          completionRate: videoDuration ? (newTotalTime / videoDuration) * 100 : 0,
        });
      }
    }, WATCH_TIME_UPDATE_INTERVAL);
  }, [videoId, videoDuration]);

  // Stop watch time tracking
  const stopWatchTimeTracking = useCallback(() => {
    if (watchTimeIntervalRef.current) {
      clearInterval(watchTimeIntervalRef.current);
      watchTimeIntervalRef.current = null;
    }

    if (watchTimeRef.current.startTime) {
      const elapsed = (Date.now() - watchTimeRef.current.startTime) / 1000;
      watchTimeRef.current.totalWatchTime += elapsed;
      watchTimeRef.current.startTime = null;
    }
  }, []);

  // Calculate engagement score
  const calculateEngagement = useCallback(() => {
    const engagementScore = VideoDataService.calculateEngagementScore({
      completionRate: analyticsState.completionRate / 100,
      watchTime: analyticsState.watchTime,
    });

    setAnalyticsState((prev) => ({ ...prev, engagementScore }));

    if (onEngagementUpdate) {
      onEngagementUpdate(engagementScore);
    }

    VideoDataService.updateAnalyticsCache(videoId, { engagementScore });
  }, [videoId, analyticsState.completionRate, analyticsState.watchTime, onEngagementUpdate]);

  // Start engagement calculation
  useEffect(() => {
    if (analyticsState.isPlaying && enableDetailedTracking) {
      engagementIntervalRef.current = setInterval(calculateEngagement, ENGAGEMENT_CALC_INTERVAL);
    } else if (engagementIntervalRef.current) {
      clearInterval(engagementIntervalRef.current);
      engagementIntervalRef.current = null;
    }

    return () => {
      if (engagementIntervalRef.current) {
        clearInterval(engagementIntervalRef.current);
      }
    };
  }, [analyticsState.isPlaying, enableDetailedTracking, calculateEngagement]);

  // Handle play event
  const handlePlay = useCallback(() => {
    if (!consentStore.preferences.analytics) return;

    VideoDataService.trackVideoEvent(videoId, {
      type: analyticsState.watchTime > 0 ? "resume" : "play",
      timestamp: Date.now(),
      metadata: { position: watchTimeRef.current.lastPosition },
    });

    startWatchTimeTracking();
    setAnalyticsState((prev) => ({ ...prev, isPlaying: true }));
  }, [videoId, analyticsState.watchTime, startWatchTimeTracking]);

  // Handle pause event
  const handlePause = useCallback(() => {
    if (!consentStore.preferences.analytics) return;

    VideoDataService.trackVideoEvent(videoId, {
      type: "pause",
      timestamp: Date.now(),
      metadata: {
        position: watchTimeRef.current.lastPosition,
        watchTime: watchTimeRef.current.totalWatchTime,
      },
    });

    stopWatchTimeTracking();
    setAnalyticsState((prev) => ({ ...prev, isPlaying: false }));
  }, [videoId, stopWatchTimeTracking]);

  // Handle seek event
  const handleSeek = useCallback(
    (fromPosition: number, toPosition: number) => {
      if (!consentStore.preferences.analytics || !enableDetailedTracking) return;

      VideoDataService.trackVideoEvent(videoId, {
        type: "seek",
        timestamp: Date.now(),
        metadata: { from: fromPosition, to: toPosition },
      });

      setAnalyticsState((prev) => ({ ...prev, seekCount: prev.seekCount + 1 }));
    },
    [videoId, enableDetailedTracking],
  );

  // Handle buffer start
  const handleBufferStart = useCallback(() => {
    if (!consentStore.preferences.analytics || !enableDetailedTracking) return;

    isBufferingRef.current = true;

    VideoDataService.trackVideoEvent(videoId, {
      type: "buffer_start",
      timestamp: Date.now(),
      metadata: { position: watchTimeRef.current.lastPosition },
    });

    VideoDataService.trackBufferingStart(videoId);
    setAnalyticsState((prev) => ({ ...prev, bufferingCount: prev.bufferingCount + 1 }));
  }, [videoId, enableDetailedTracking]);

  // Handle buffer end
  const handleBufferEnd = useCallback(() => {
    if (!consentStore.preferences.analytics || !enableDetailedTracking) return;

    if (isBufferingRef.current) {
      isBufferingRef.current = false;

      VideoDataService.trackVideoEvent(videoId, {
        type: "buffer_end",
        timestamp: Date.now(),
        metadata: { position: watchTimeRef.current.lastPosition },
      });

      VideoDataService.trackBufferingEnd(videoId);
    }
  }, [videoId, enableDetailedTracking]);

  // Handle quality change
  const handleQualityChange = useCallback(
    (newQuality: string) => {
      if (!consentStore.preferences.analytics || !trackQualityChanges) return;

      VideoDataService.trackVideoEvent(videoId, {
        type: "quality_change",
        timestamp: Date.now(),
        metadata: {
          from: lastQualityRef.current,
          to: newQuality,
          position: watchTimeRef.current.lastPosition,
        },
      });

      lastQualityRef.current = newQuality;
    },
    [videoId, trackQualityChanges],
  );

  // Handle completion
  const handleCompletion = useCallback(() => {
    if (!consentStore.preferences.analytics) return;

    const completionRate = videoDuration ? (watchTimeRef.current.totalWatchTime / videoDuration) * 100 : 0;

    VideoDataService.trackVideoCompletion(videoId, watchTimeRef.current.totalWatchTime, videoDuration || 0);

    if (onCompletionDetected) {
      onCompletionDetected(completionRate);
    }

    setAnalyticsState((prev) => ({ ...prev, completionRate }));
  }, [videoId, videoDuration, onCompletionDetected]);

  // Process playback status update
  const onPlaybackStatusUpdate = useCallback(
    (status: PlaybackStatusSnapshot) => {
      if (!status.isLoaded || !consentStore.preferences.analytics) return;

      const prevStatus = lastPlaybackStatusRef.current;
      lastPlaybackStatusRef.current = status;

      // Track play/pause state changes
      if (status.isPlaying && (!prevStatus || !prevStatus.isLoaded || !prevStatus.isPlaying)) {
        handlePlay();
      } else if (!status.isPlaying && prevStatus?.isLoaded && prevStatus.isPlaying) {
        handlePause();
      }

      // Track buffering
      if (enableDetailedTracking) {
        if (status.isBuffering && !isBufferingRef.current) {
          handleBufferStart();
        } else if (!status.isBuffering && isBufferingRef.current) {
          handleBufferEnd();
        }
      }

      // Track position and detect seeks
      if (status.positionMillis !== undefined) {
        const currentPosition = status.positionMillis / 1000;

        if (prevStatus?.isLoaded && prevStatus.positionMillis !== undefined) {
          const prevPosition = prevStatus.positionMillis / 1000;
          const timeDiff = Math.abs(currentPosition - prevPosition);

          // Detect seek (position change larger than normal playback)
          if (timeDiff > POSITION_TOLERANCE && enableDetailedTracking) {
            handleSeek(prevPosition, currentPosition);
          }
        }

        watchTimeRef.current.lastPosition = currentPosition;
      }

      // Check for completion - track once per session regardless of looping
      const currentCompletionRate = videoDuration ? watchTimeRef.current.totalWatchTime / videoDuration : 0;

      if (!hasMarkedCompletedRef.current && (status.didJustFinish || currentCompletionRate >= 0.8) && videoDuration) {
        hasMarkedCompletedRef.current = true;
        handleCompletion();
      }
    },
    [
      handlePlay,
      handlePause,
      handleBufferStart,
      handleBufferEnd,
      handleSeek,
      handleCompletion,
      enableDetailedTracking,
      videoDuration,
    ],
  );

  // Track interaction events
  const trackInteraction = useCallback(
    (type: "like" | "unlike" | "comment" | "share" | "save") => {
      if (!consentStore.preferences.analytics) return;

      VideoDataService.trackVideoEvent(videoId, {
        type,
        timestamp: Date.now(),
        metadata: {
          position: watchTimeRef.current.lastPosition,
          watchTime: watchTimeRef.current.totalWatchTime,
        },
      });
    },
    [videoId],
  );

  // Manual quality update
  const updateQuality = useCallback(
    (quality: string) => {
      handleQualityChange(quality);
    },
    [handleQualityChange],
  );

  // Manual completion trigger
  const markAsCompleted = useCallback(() => {
    handleCompletion();
  }, [handleCompletion]);

  // Get current analytics state
  const getCurrentAnalytics = useCallback(
    () => ({
      ...analyticsState,
      sessionId: sessionIdRef.current,
      currentPosition: watchTimeRef.current.lastPosition,
    }),
    [analyticsState],
  );

  // Cleanup function
  const cleanup = useCallback(() => {
    stopWatchTimeTracking();
    if (engagementIntervalRef.current) {
      clearInterval(engagementIntervalRef.current);
    }
    if (sessionIdRef.current) {
      VideoDataService.endSession(sessionIdRef.current);
    }
  }, [stopWatchTimeTracking]);

  return {
    // State
    analyticsState,

    // Event handlers
    onPlaybackStatusUpdate,
    trackInteraction,
    updateQuality,
    markAsCompleted,

    // Utilities
    getCurrentAnalytics,
    cleanup,

    // Direct event handlers (for custom players)
    handlePlay,
    handlePause,
    handleSeek,
    handleBufferStart,
    handleBufferEnd,
    handleQualityChange,
    handleCompletion,
  };
}

import { useCallback, useRef } from "react";
import { Gesture } from "react-native-gesture-handler";
import {
  useSharedValue,
  useAnimatedStyle,
  withSpring,
  withSequence,
  withTiming,
  runOnJS,
  interpolate,
} from "react-native-reanimated";
import { Dimensions } from "react-native";
import * as Haptics from "expo-haptics";

const { height: SCREEN_HEIGHT, width: SCREEN_WIDTH } = Dimensions.get("window");

// Gesture thresholds and configuration
const SWIPE_THRESHOLD = 60;
const SWIPE_VELOCITY_THRESHOLD = 650;
const HORIZONTAL_SWIPE_THRESHOLD = 80;
const DOUBLE_TAP_MAX_DELAY = 280;
const PINCH_SCALE_THRESHOLD = 0.1;
const SPRING_CONFIG = {
  damping: 20,
  stiffness: 300,
  mass: 1,
};

interface UseVideoFeedGesturesProps {
  currentIndex: number;
  totalVideos: number;
  onLongPress?: () => void;
  onRefresh?: () => void;
  onSwipeUp?: () => void;
  onSwipeDown?: () => void;
  onSwipeLeft?: () => void;
  onSwipeRight?: () => void;
  onDoubleTap?: () => void;
  onPinch?: (scale: number) => void;
  onPlaybackSpeedChange?: () => void;
  isLoading?: boolean;
}

export function useVideoFeedGestures({
  currentIndex,
  totalVideos,
  onLongPress,
  onRefresh,
  onSwipeUp,
  onSwipeDown,
  onSwipeLeft,
  onSwipeRight,
  onDoubleTap,
  onPinch,
  onPlaybackSpeedChange,
  isLoading = false,
}: UseVideoFeedGesturesProps) {
  // Animation values
  const translateY = useSharedValue(0);
  const translateX = useSharedValue(0);
  const scale = useSharedValue(1);
  const opacity = useSharedValue(1);
  const progressY = useSharedValue(0);
  const progressX = useSharedValue(0);
  const pinchScale = useSharedValue(1);

  // Gesture state
  const isScrolling = useSharedValue(false);
  const activeDirection = useSharedValue<"idle" | "up" | "down" | "left" | "right">("idle");
  const lastTapTime = useRef(0);
  const tapTimeout = useRef<NodeJS.Timeout | null>(null);

  // Pan gesture for swipe navigation (vertical and horizontal)
  const panGesture = Gesture.Pan()
    .onStart(() => {
      isScrolling.value = true;
      activeDirection.value = "idle";
    })
    .onUpdate((event) => {
      const absY = Math.abs(event.translationY);
      const absX = Math.abs(event.translationX);

      // Determine primary direction
      if (absY > absX) {
        // Vertical swipe
        const dampenedTranslation = event.translationY + event.velocityY * 0.05;
        translateY.value = dampenedTranslation;

        // Scale effect for visual feedback
        const progress = Math.abs(dampenedTranslation) / SCREEN_HEIGHT;
        scale.value = interpolate(progress, [0, 0.3], [1, 0.95], "clamp");

        // Opacity effect for smooth transitions
        opacity.value = interpolate(progress, [0, 0.5], [1, 0.8], "clamp");

        activeDirection.value = dampenedTranslation > 0 ? "down" : "up";

        // Update shared value for progress
        const normalized = Math.max(-1, Math.min(1, dampenedTranslation / SCREEN_HEIGHT));
        progressY.value = normalized;
      } else {
        // Horizontal swipe
        translateX.value = event.translationX;

        // Scale effect for horizontal swipes
        const progress = Math.abs(event.translationX) / SCREEN_WIDTH;
        scale.value = interpolate(progress, [0, 0.2], [1, 0.98], "clamp");

        activeDirection.value = event.translationX > 0 ? "right" : "left";

        // Update shared value for progress
        const normalized = Math.max(-1, Math.min(1, event.translationX / SCREEN_WIDTH));
        progressX.value = normalized;
      }
    })
    .onEnd((event) => {
      const absY = Math.abs(event.translationY);
      const absX = Math.abs(event.translationX);
      const shouldSwipeVertical = absY > SWIPE_THRESHOLD || Math.abs(event.velocityY) > SWIPE_VELOCITY_THRESHOLD;
      const shouldSwipeHorizontal =
        absX > HORIZONTAL_SWIPE_THRESHOLD || Math.abs(event.velocityX) > SWIPE_VELOCITY_THRESHOLD;

      if (absY > absX) {
        // Vertical swipe handling
        if (shouldSwipeVertical && event.translationY > 0 && currentIndex === 0 && onRefresh && !isLoading) {
          runOnJS(() => {
            Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Medium);
            onRefresh();
          })();
        } else if (shouldSwipeVertical) {
          runOnJS(() => {
            if (event.translationY > 0 && onSwipeDown) {
              Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Light);
              onSwipeDown();
            } else if (event.translationY < 0 && onSwipeUp) {
              Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Light);
              onSwipeUp();
            }
          })();
        }
      } else {
        // Horizontal swipe handling
        if (shouldSwipeHorizontal) {
          runOnJS(() => {
            if (event.translationX > 0 && onSwipeRight) {
              Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Light);
              onSwipeRight();
            } else if (event.translationX < 0 && onSwipeLeft) {
              Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Light);
              onSwipeLeft();
            }
          })();
        }
      }

      // Reset animations
      translateY.value = withSpring(0, SPRING_CONFIG);
      translateX.value = withSpring(0, SPRING_CONFIG);
      scale.value = withSpring(1, SPRING_CONFIG);
      opacity.value = withSpring(1, SPRING_CONFIG);
      progressY.value = withSpring(0, SPRING_CONFIG);
      progressX.value = withSpring(0, SPRING_CONFIG);
      isScrolling.value = false;
      activeDirection.value = "idle";
    });

  // Double tap gesture for like
  const doubleTapGesture = Gesture.Tap()
    .numberOfTaps(2)
    .maxDuration(250)
    .onEnd(() => {
      const now = Date.now();
      const timeSinceLastTap = now - lastTapTime.current;

      if (timeSinceLastTap < DOUBLE_TAP_MAX_DELAY && onDoubleTap) {
        runOnJS(() => {
          Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Medium);
          onDoubleTap();
        })();
      }
      lastTapTime.current = now;
    });

  // Long press gesture for playback speed control
  const longPressGesture = Gesture.LongPress()
    .minDuration(500)
    .onStart(() => {
      if (onPlaybackSpeedChange) {
        runOnJS(() => {
          Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Heavy);
          onPlaybackSpeedChange();
        })();
      } else if (onLongPress) {
        runOnJS(onLongPress)();
      }
    });

  // Pinch gesture for zoom controls
  const pinchGesture = Gesture.Pinch()
    .onUpdate((event) => {
      pinchScale.value = event.scale;
    })
    .onEnd((event) => {
      const scaleChange = event.scale - 1;
      if (Math.abs(scaleChange) > PINCH_SCALE_THRESHOLD && onPinch) {
        runOnJS(() => {
          onPinch(event.scale);
        })();
      }
      pinchScale.value = withSpring(1, SPRING_CONFIG);
    });

  // Compose all gestures with proper priority
  const composedGestures = Gesture.Race(
    doubleTapGesture,
    Gesture.Simultaneous(panGesture, pinchGesture),
    longPressGesture,
  );

  // Animated styles
  const containerStyle = useAnimatedStyle(() => ({
    transform: [
      { translateY: translateY.value },
      { translateX: translateX.value },
      { scale: scale.value * pinchScale.value },
    ],
    opacity: opacity.value,
  }));

  const overlayStyle = useAnimatedStyle(() => ({
    opacity: isScrolling.value ? 0.5 : 1,
  }));

  // Reset animations
  const resetAnimations = useCallback(() => {
    translateY.value = withSpring(0, SPRING_CONFIG);
    translateX.value = withSpring(0, SPRING_CONFIG);
    scale.value = withSpring(1, SPRING_CONFIG);
    opacity.value = withSpring(1, SPRING_CONFIG);
    progressY.value = withSpring(0, SPRING_CONFIG);
    progressX.value = withSpring(0, SPRING_CONFIG);
    pinchScale.value = withSpring(1, SPRING_CONFIG);
    isScrolling.value = false;
    activeDirection.value = "idle";
  }, [translateY, translateX, scale, opacity, progressY, progressX, pinchScale, isScrolling]);

  return {
    gestures: composedGestures,
    containerStyle,
    overlayStyle,
    resetAnimations,
    isScrolling,
    progressY,
    progressX,
    pinchScale,
    gestureState: {
      activeDirection,
    },
  };
}

import { useState, useEffect, useCallback, useRef } from "react";
import { AppState, AppStateStatus } from "react-native";
import NetInfo from "@react-native-community/netinfo";
import { videoQualitySelector } from "../services/VideoQualitySelector";
import { videoBackgroundQueue, JobType, JobPriority } from "../services/VideoBackgroundQueue";
import { networkProfiler, NetworkProfile, NetworkQuality } from "../utils/networkProfiler";
import { videoPerformanceConfig, DevicePerformanceTier, NetworkQualityTier } from "../config/videoPerformance";
import { videoCacheManager } from "../utils/videoCacheManager";
import { environmentDetector } from "../utils/environmentDetector";
import { VideoDataService } from "../services/VideoDataService";

export interface VideoPerformanceState {
  deviceTier: DevicePerformanceTier;
  networkQuality: NetworkQuality;
  currentVideoQuality: "360p" | "720p" | "1080p";
  isOptimizing: boolean;
  cacheStats: {
    size: number;
    count: number;
    hitRate: number;
  };
  backgroundQueueStats: {
    pending: number;
    processing: number;
    completed: number;
  };
  memoryPressure: number;
  adaptiveSettings: {
    preloadCount: number;
    cacheSize: number;
    qualityMode: "auto" | "manual" | "save-data";
  };
  errors: string[];
}

export interface VideoPerformanceActions {
  optimizeVideoQuality: (videoUri: string) => Promise<void>;
  preloadVideos: (videoUris: string[], priority?: "high" | "normal" | "low") => Promise<void>;
  clearCache: () => Promise<void>;
  forceQuality: (quality: "360p" | "720p" | "1080p") => void;
  toggleSaveDataMode: () => void;
  runPerformanceTest: () => Promise<PerformanceTestResult>;
  getRecommendedSettings: () => PerformanceRecommendations;
}

export interface PerformanceTestResult {
  bandwidth: number;
  latency: number;
  deviceScore: number;
  recommendedQuality: "360p" | "720p" | "1080p";
  issues: string[];
}

export interface PerformanceRecommendations {
  quality: "360p" | "720p" | "1080p";
  preloadCount: number;
  cacheStrategy: "aggressive" | "balanced" | "conservative";
  features: {
    autoQualityUpgrade: boolean;
    backgroundProcessing: boolean;
    aggressivePreloading: boolean;
  };
}

interface UseVideoPerformanceOptions {
  enableAutoOptimization?: boolean;
  enableBackgroundProcessing?: boolean;
  enableAnalytics?: boolean;
  debugMode?: boolean;
}

export function useVideoPerformanceOptimization(
  currentVideoUri?: string,
  options: UseVideoPerformanceOptions = {},
): [VideoPerformanceState, VideoPerformanceActions] {
  const {
    enableAutoOptimization = true,
    enableBackgroundProcessing = true,
    enableAnalytics = true,
    debugMode = false,
  } = options;

  // State
  const [state, setState] = useState<VideoPerformanceState>({
    deviceTier: DevicePerformanceTier.MID,
    networkQuality: "fair",
    currentVideoQuality: "720p",
    isOptimizing: false,
    cacheStats: {
      size: 0,
      count: 0,
      hitRate: 0,
    },
    backgroundQueueStats: {
      pending: 0,
      processing: 0,
      completed: 0,
    },
    memoryPressure: 0,
    adaptiveSettings: {
      preloadCount: 5,
      cacheSize: 500 * 1024 * 1024,
      qualityMode: "auto",
    },
    errors: [],
  });

  // Refs
  const appStateRef = useRef<AppStateStatus>(AppState.currentState);
  const networkSubscription = useRef<any>(null);
  const memoryMonitorInterval = useRef<NodeJS.Timeout | null>(null);
  const analyticsInterval = useRef<NodeJS.Timeout | null>(null);
  const appStateSubscription = useRef<any>(null);
  const queueStatsInterval = useRef<NodeJS.Timeout | null>(null);
  const isInitialized = useRef(false);

  // Initialize performance optimization
  useEffect(() => {
    if (isInitialized.current) return;
    isInitialized.current = true;

    initializePerformanceOptimization();

    return () => {
      cleanup();
    };
  }, []);

  const initializePerformanceOptimization = async () => {
    try {
      // Detect device capabilities
      await detectDeviceCapabilities();

      // Initialize network monitoring
      initializeNetworkMonitoring();

      // Start memory monitoring
      startMemoryMonitoring();

      // Initialize background processing
      if (enableBackgroundProcessing) {
        await initializeBackgroundProcessing();
      }

      // Start analytics collection
      if (enableAnalytics) {
        startAnalyticsCollection();
      }

      // Initialize app state monitoring
      initializeAppStateMonitoring();

      // Update initial state
      await updatePerformanceState();
    } catch (error) {
      console.error("Failed to initialize performance optimization:", error);
      addError("Failed to initialize performance optimization");
    }
  };

  const detectDeviceCapabilities = async () => {
    try {
      const deviceInfo = await environmentDetector.getDeviceInfo();
      const memoryInfo = await environmentDetector.getMemoryInfo();

      const totalMemoryGB = memoryInfo.totalMemory / (1024 * 1024 * 1024);
      let tier: DevicePerformanceTier;

      if (totalMemoryGB >= 6) {
        tier = DevicePerformanceTier.HIGH;
      } else if (totalMemoryGB >= 4) {
        tier = DevicePerformanceTier.MID;
      } else {
        tier = DevicePerformanceTier.LOW;
      }

      videoPerformanceConfig.setDeviceTier(tier);

      setState((prev) => ({ ...prev, deviceTier: tier }));
    } catch (error) {
      console.error("Failed to detect device capabilities:", error);
      addError("Failed to detect device capabilities");
    }
  };

  const initializeNetworkMonitoring = () => {
    networkSubscription.current = NetInfo.addEventListener(async (state) => {
      if (state.isConnected) {
        const profile = await networkProfiler.measureNetworkCondition();
        if (profile) {
          handleNetworkChange(profile);
        }
      } else {
        setState((prev) => ({ ...prev, networkQuality: "poor" }));
      }
    });
  };

  const handleNetworkChange = async (profile: NetworkProfile) => {
    setState((prev) => ({ ...prev, networkQuality: profile.quality }));

    // Map to NetworkQualityTier for video config
    const qualityTierMap: Record<NetworkQuality, NetworkQualityTier> = {
      excellent: NetworkQualityTier.EXCELLENT,
      good: NetworkQualityTier.GOOD,
      fair: NetworkQualityTier.FAIR,
      poor: NetworkQualityTier.POOR,
    };

    videoPerformanceConfig.setNetworkQuality(qualityTierMap[profile.quality]);

    // Auto-optimize quality if enabled
    if (enableAutoOptimization && currentVideoUri) {
      if (profile.quality === "excellent") {
        const canUpgrade = await videoQualitySelector.canUpgradeQuality(currentVideoUri);
        if (canUpgrade) {
          await optimizeCurrentVideo();
        }
      } else if (profile.quality === "poor") {
        const shouldDowngrade = await videoQualitySelector.shouldDowngradeQuality(currentVideoUri);
        if (shouldDowngrade) {
          setState((prev) => ({ ...prev, currentVideoQuality: "360p" }));
        }
      }
    }
  };

  const startMemoryMonitoring = () => {
    memoryMonitorInterval.current = setInterval(async () => {
      try {
        const memoryInfo = await environmentDetector.getMemoryInfo();
        const usedMemory = memoryInfo.totalMemory - memoryInfo.availableMemory;
        const memoryPressure = usedMemory / memoryInfo.totalMemory;

        setState((prev) => ({ ...prev, memoryPressure }));

        // Trigger cache cleanup if memory pressure is high
        if (memoryPressure > 0.8) {
          await videoCacheManager.forceCleanup();
        }
      } catch (error) {
        console.error("Memory monitoring failed:", error);
      }
    }, 10000); // Check every 10 seconds
  };

  const initializeBackgroundProcessing = async () => {
    // Queue some initial optimization jobs
    await videoBackgroundQueue.enqueueJob(JobType.CACHE_OPTIMIZATION, {}, JobPriority.LOW);

    // Update queue stats periodically
    queueStatsInterval.current = setInterval(() => {
      const queueStats = videoBackgroundQueue.getQueueStats();
      setState((prev) => ({
        ...prev,
        backgroundQueueStats: {
          pending: queueStats.pending,
          processing: queueStats.processing,
          completed: queueStats.completed,
        },
      }));
    }, 5000);
  };

  const startAnalyticsCollection = () => {
    analyticsInterval.current = setInterval(async () => {
      try {
        const cacheStats = videoCacheManager.getDeviceAwareCacheStats();

        setState((prev) => ({
          ...prev,
          cacheStats: {
            size: cacheStats.size,
            count: cacheStats.count,
            hitRate: cacheStats.hitRate,
          },
        }));

        // Send analytics data (placeholder)
        if (debugMode) {
          console.log("Performance Analytics:", {
            deviceTier: state.deviceTier,
            networkQuality: state.networkQuality,
            cacheHitRate: cacheStats.hitRate,
            memoryPressure: state.memoryPressure,
          });
        }
      } catch (error) {
        console.error("Analytics collection failed:", error);
      }
    }, 30000); // Every 30 seconds
  };

  const initializeAppStateMonitoring = () => {
    appStateSubscription.current = AppState.addEventListener("change", (nextAppState) => {
      if (appStateRef.current === "background" && nextAppState === "active") {
        // App came to foreground - refresh state
        updatePerformanceState();
      }
      appStateRef.current = nextAppState;
    });
  };

  const updatePerformanceState = async () => {
    try {
      const [networkProfile, cacheStats, queueStats] = await Promise.all([
        networkProfiler.getCurrentProfile(),
        videoCacheManager.getDeviceAwareCacheStats(),
        Promise.resolve(videoBackgroundQueue.getQueueStats()),
      ]);

      const perfConfig = videoPerformanceConfig.getDynamicConfig();

      setState((prev) => ({
        ...prev,
        networkQuality: networkProfile?.quality ?? "fair",
        cacheStats: {
          size: cacheStats.size,
          count: cacheStats.count,
          hitRate: cacheStats.hitRate,
        },
        backgroundQueueStats: {
          pending: queueStats.pending,
          processing: queueStats.processing,
          completed: queueStats.completed,
        },
        adaptiveSettings: {
          preloadCount: perfConfig.preloadProfile.preloadWindowSize,
          cacheSize: perfConfig.cacheConfig.maxCacheSize,
          qualityMode: prev.adaptiveSettings.qualityMode,
        },
      }));
    } catch (error) {
      console.error("Failed to update performance state:", error);
    }
  };

  const optimizeCurrentVideo = async () => {
    if (!currentVideoUri) {
      console.warn("No current video URI provided for optimization");
      return;
    }

    setState((prev) => ({ ...prev, isOptimizing: true }));

    try {
      const qualityResult = await videoQualitySelector.selectVideoQuality(currentVideoUri);
      setState((prev) => ({
        ...prev,
        currentVideoQuality: qualityResult.selectedQuality,
        isOptimizing: false,
      }));
    } catch (error) {
      console.error("Video optimization failed:", error);
      setState((prev) => ({ ...prev, isOptimizing: false }));
    }
  };

  const addError = (error: string) => {
    setState((prev) => ({
      ...prev,
      errors: [...prev.errors.slice(-4), error], // Keep last 5 errors
    }));
  };

  const cleanup = () => {
    if (networkSubscription.current) {
      networkSubscription.current();
      networkSubscription.current = null;
    }
    if (memoryMonitorInterval.current) {
      clearInterval(memoryMonitorInterval.current);
      memoryMonitorInterval.current = null;
    }
    if (analyticsInterval.current) {
      clearInterval(analyticsInterval.current);
      analyticsInterval.current = null;
    }
    if (appStateSubscription.current) {
      appStateSubscription.current.remove();
      appStateSubscription.current = null;
    }
    if (queueStatsInterval.current) {
      clearInterval(queueStatsInterval.current);
      queueStatsInterval.current = null;
    }
  };

  // Actions
  const actions: VideoPerformanceActions = {
    optimizeVideoQuality: async (videoUri: string) => {
      setState((prev) => ({ ...prev, isOptimizing: true }));

      try {
        const qualityResult = await videoQualitySelector.selectVideoQuality(videoUri);

        setState((prev) => ({
          ...prev,
          currentVideoQuality: qualityResult.selectedQuality,
          isOptimizing: false,
        }));

        // Queue background optimization
        if (enableBackgroundProcessing) {
          await videoBackgroundQueue.enqueueJob(
            JobType.QUALITY_VARIANT_GENERATION,
            { uri: videoUri, videoId: "generated" },
            JobPriority.NORMAL,
          );
        }
      } catch (error) {
        console.error("Quality optimization failed:", error);
        addError("Quality optimization failed");
        setState((prev) => ({ ...prev, isOptimizing: false }));
      }
    },

    preloadVideos: async (videoUris: string[], priority = "normal") => {
      try {
        await videoCacheManager.preloadVideos(videoUris, priority);

        // Queue background preloading for additional optimization
        if (enableBackgroundProcessing) {
          await videoBackgroundQueue.enqueueJob(
            JobType.VIDEO_PRELOADING,
            { uris: videoUris, priority },
            JobPriority.LOW,
          );
        }
      } catch (error) {
        console.error("Video preloading failed:", error);
        addError("Video preloading failed");
      }
    },

    clearCache: async () => {
      try {
        await videoCacheManager.clearCache();
        await updatePerformanceState();
      } catch (error) {
        console.error("Cache clearing failed:", error);
        addError("Cache clearing failed");
      }
    },

    forceQuality: (quality: "360p" | "720p" | "1080p") => {
      setState((prev) => ({
        ...prev,
        currentVideoQuality: quality,
        adaptiveSettings: {
          ...prev.adaptiveSettings,
          qualityMode: "manual",
        },
      }));
    },

    toggleSaveDataMode: () => {
      setState((prev) => {
        const newMode = prev.adaptiveSettings.qualityMode === "save-data" ? "auto" : "save-data";

        if (newMode === "save-data") {
          // Force low quality in save data mode
          return {
            ...prev,
            currentVideoQuality: "360p",
            adaptiveSettings: {
              ...prev.adaptiveSettings,
              qualityMode: newMode,
              preloadCount: 2,
              cacheSize: 100 * 1024 * 1024, // 100MB
            },
          };
        } else {
          return {
            ...prev,
            adaptiveSettings: {
              ...prev.adaptiveSettings,
              qualityMode: newMode,
            },
          };
        }
      });
    },

    runPerformanceTest: async (): Promise<PerformanceTestResult> => {
      try {
        const [networkProfile, memoryInfo] = await Promise.all([
          networkProfiler.measureNetworkCondition(),
          environmentDetector.getMemoryInfo(),
        ]);

        const deviceScore = calculateDeviceScore(memoryInfo);
        const issues: string[] = [];

        if (!networkProfile) {
          throw new Error("Network test failed");
        }

        if (networkProfile.bandwidth < 2) {
          issues.push("Low bandwidth detected");
        }
        if (networkProfile.latency > 100) {
          issues.push("High latency detected");
        }
        const memoryUsageRatio = (memoryInfo.totalMemory - memoryInfo.availableMemory) / memoryInfo.totalMemory;
        if (memoryUsageRatio > 0.8) {
          issues.push("High memory usage");
        }

        const recommendedQuality = determineRecommendedQuality(networkProfile.bandwidth, deviceScore);

        return {
          bandwidth: networkProfile.bandwidth,
          latency: networkProfile.latency,
          deviceScore,
          recommendedQuality,
          issues,
        };
      } catch (error) {
        console.error("Performance test failed:", error);
        throw error;
      }
    },

    getRecommendedSettings: (): PerformanceRecommendations => {
      const perfConfig = videoPerformanceConfig.getDynamicConfig();

      let cacheStrategy: "aggressive" | "balanced" | "conservative";
      if (state.deviceTier === DevicePerformanceTier.HIGH) {
        cacheStrategy = "aggressive";
      } else if (state.deviceTier === DevicePerformanceTier.MID) {
        cacheStrategy = "balanced";
      } else {
        cacheStrategy = "conservative";
      }

      return {
        quality: state.currentVideoQuality,
        preloadCount: perfConfig.preloadProfile.preloadWindowSize,
        cacheStrategy,
        features: {
          autoQualityUpgrade: perfConfig.features.autoQualityUpgrade,
          backgroundProcessing: perfConfig.features.backgroundTranscoding,
          aggressivePreloading: perfConfig.features.aggressivePreloading,
        },
      };
    },
  };

  return [state, actions];
}

// Helper functions
function calculateDeviceScore(memoryInfo: any): number {
  const memoryGB = memoryInfo.totalMemory / (1024 * 1024 * 1024);
  const memoryScore = Math.min(100, (memoryGB / 8) * 100);

  // Simple scoring based on available memory
  return memoryScore;
}

function determineRecommendedQuality(bandwidth: number, deviceScore: number): "360p" | "720p" | "1080p" {
  const combinedScore = (bandwidth / 20) * 50 + (deviceScore / 100) * 50;

  if (combinedScore >= 70) return "1080p";
  if (combinedScore >= 40) return "720p";
  return "360p";
}

/**
 * Video Recorder Hook with Real-time Effects
 * Comprehensive dual-mode support for Expo Go and development builds
 * Features: permissions, recording, face blur, pitch shifting, live captions
 */

import { useState, useRef, useCallback, useEffect } from "react";
import { Platform } from "react-native";
import { CameraView, CameraType } from "expo-camera";
import * as Audio from "expo-audio";
import { env } from "../utils/env";
import { useMediaPermissions } from "./useMediaPermissions";
import { unifiedVideoProcessingService, ProcessingMode } from "../services/UnifiedVideoProcessingService";
import type { ProcessedVideo, VideoProcessingOptions } from "../services/IAnonymiser";

export interface VideoRecorderState {
  isRecording: boolean;
  isProcessing: boolean;
  recordingTime: number;
  processingProgress: number;
  processingStatus: string;
  hasPermissions: boolean;
  isInitialized: boolean;
  error?: string;
}

export interface VideoRecorderOptions {
  maxDuration?: number;
  quality?: "high" | "medium" | "low";
  enableFaceBlur?: boolean;
  enableVoiceChange?: boolean;
  enableLiveCaptions?: boolean;
  voiceEffect?: "deep" | "light";
  processingMode?: ProcessingMode;
  autoProcessAfterRecording?: boolean;
  onRecordingStart?: () => void;
  onRecordingStop?: (uri: string) => void;
  onProcessingComplete?: (processed: ProcessedVideo) => void;
  onError?: (error: string) => void;
}

export interface VideoRecorderControls {
  startRecording: () => Promise<void>;
  stopRecording: () => Promise<void>;
  pauseRecording: () => Promise<void>;
  resumeRecording: () => Promise<void>;
  toggleCamera: () => void;
  reset: () => void;
  cleanup: () => void;
  startProcessing: () => Promise<void>;
}

export interface VideoRecorderData {
  cameraRef: React.RefObject<CameraView | null>;
  facing: CameraType;
  liveTranscription: string;
  processedVideo?: ProcessedVideo;
}

/**
 * Comprehensive video recorder hook with real-time effects
 */
export const useVideoRecorder = (options: VideoRecorderOptions = {}) => {
  const {
    maxDuration = 60,
    quality = "medium",
    enableFaceBlur = true,
    enableVoiceChange = true,
    enableLiveCaptions = true,
    voiceEffect = "deep",
    processingMode = ProcessingMode.HYBRID,
    autoProcessAfterRecording = true,
    onRecordingStart,
    onRecordingStop,
    onProcessingComplete,
    onError,
  } = options;

  // Refs
  const cameraRef = useRef<CameraView>(null);
  const recordingPromiseRef = useRef<Promise<{ uri: string }> | null>(null);
  const recordingRef = useRef<any | null>(null);
  const timerRef = useRef<ReturnType<typeof setInterval> | null>(null);
  const audioRecorderRef = useRef<any | null>(null);
  const speechRecognitionRef = useRef<any>(null);
  const isRecordingRef = useRef<boolean>(false);

  // State
  const [facing, setFacing] = useState<CameraType>("front");
  const [recordingTime, setRecordingTime] = useState(0);
  const [isRecording, setIsRecording] = useState(false);
  const [isPaused, setIsPaused] = useState(false);

  // Keep ref in sync with state
  useEffect(() => {
    isRecordingRef.current = isRecording;
  }, [isRecording]);

  const [isProcessing, setIsProcessing] = useState(false);
  const [processingProgress, setProcessingProgress] = useState(0);
  const [processingStatus, setProcessingStatus] = useState("");
  const [liveTranscription, setLiveTranscription] = useState("");
  const [processedVideo, setProcessedVideo] = useState<ProcessedVideo | undefined>();
  const [error, setError] = useState<string | undefined>();
  const [isInitialized, setIsInitialized] = useState(false);
  const [recordedVideoUri, setRecordedVideoUri] = useState<string | null>(null);

  // Permissions
  const { permissionState, requestVideoPermissions, hasVideoPermissions } = useMediaPermissions({
    autoRequest: false,
    showAlerts: true,
  });

  // Initialize audio session for real-time effects
  useEffect(() => {
    const initializeAudio = async () => {
      try {
        await Audio.setAudioModeAsync({
          allowsRecording: true,
          playsInSilentMode: true,
          interruptionModeAndroid: "duckOthers",
        });
      } catch (error) {
        console.warn("Failed to initialize audio session:", error);
      }
    };

    initializeAudio();
    setIsInitialized(true);

    return () => {
      cleanup();
    };
  }, []);

  // Timer effect for recording duration
  useEffect(() => {
    if (isRecording && !isPaused) {
      timerRef.current = setInterval(() => {
        setRecordingTime((prev) => prev + 1);
      }, 1000);
    } else if (timerRef.current) {
      clearInterval(timerRef.current);
      timerRef.current = null;
    }

    return () => {
      if (timerRef.current) {
        clearInterval(timerRef.current);
      }
    };
  }, [isRecording, isPaused]);

  // Auto-stop recording when max duration reached
  useEffect(() => {
    if (recordingTime >= maxDuration && isRecording) {
      stopRecordingRef.current?.();
    }
  }, [recordingTime, maxDuration, isRecording]);

  const handleRecordingComplete = useCallback(
    async (videoUri: string) => {
      try {
        console.log(" Recording completed:", videoUri);
        setRecordedVideoUri(videoUri);
        onRecordingStop?.(videoUri);

        // Always call onRecordingStop callback regardless of autoProcessAfterRecording
        // This ensures the UI can show the "Next" button
        if (onRecordingStop) {
          onRecordingStop(videoUri);
        }

        // Only start processing automatically if enabled
        if (autoProcessAfterRecording) {
          await startVideoProcessing(videoUri);
        }
      } catch (error) {
        const errorMessage = error instanceof Error ? error.message : "Failed to handle recording completion";
        setError(errorMessage);
        onError?.(errorMessage);
      }
    },
    [onRecordingStop, autoProcessAfterRecording, onError],
  );

  // Initialize speech recognition for live captions (if supported)
  useEffect(() => {
    if (enableLiveCaptions && Platform.OS === "ios") {
      // iOS has built-in speech recognition
      initializeSpeechRecognition();
    }

    return () => {
      if (speechRecognitionRef.current) {
        speechRecognitionRef.current.stop();
      }
    };
  }, [enableLiveCaptions]);

  const initializeSpeechRecognition = async () => {
    try {
      // Note: This would use a speech recognition library in production
      // For now, we'll simulate live transcription
      console.log(" Initializing speech recognition for live captions");
    } catch (error) {
      console.warn("Speech recognition initialization failed:", error);
    }
  };

  const startRecording = useCallback(async () => {
    if (!cameraRef.current || isRecording) return;

    try {
      setError(undefined);

      // Check permissions
      if (!hasVideoPermissions) {
        const granted = await requestVideoPermissions();
        if (!granted) {
          throw new Error("Camera and microphone permissions are required");
        }
      }

      // Start live transcription if enabled
      if (enableLiveCaptions) {
        setLiveTranscription("");
        startLiveTranscription();
      }

      // Start recording asynchronously
      setIsRecording(true);
      setRecordingTime(0);
      setIsPaused(false);

      onRecordingStart?.();

      console.log(" Starting camera recording...");
      // Type assertion to handle potential undefined return
      recordingPromiseRef.current = cameraRef.current.recordAsync({
        maxDuration,
      }) as Promise<{ uri: string }>;
    } catch (error) {
      console.error(" Recording error:", error);
      const errorMessage = error instanceof Error ? error.message : "Failed to start recording";
      setError(errorMessage);
      onError?.(errorMessage);
      setIsRecording(false);
      setRecordingTime(0);
      recordingPromiseRef.current = null;
    }
  }, [
    cameraRef,
    isRecording,
    hasVideoPermissions,
    requestVideoPermissions,
    maxDuration,
    enableLiveCaptions,
    onRecordingStart,
    onError,
  ]);

  const stopRecording = useCallback(async () => {
    if (!cameraRef.current || !isRecording || !recordingPromiseRef.current) return;

    try {
      setIsRecording(false);
      setIsPaused(false);

      // Stop the recording
      cameraRef.current.stopRecording();

      // Wait for the promise to resolve
      const video = await recordingPromiseRef.current;
      recordingPromiseRef.current = null;

      // Stop live transcription
      stopLiveTranscription();

      if (timerRef.current) {
        clearInterval(timerRef.current);
        timerRef.current = null;
      }

      console.log(" Recording stopped:", video);
      if (video?.uri) {
        await handleRecordingComplete(video.uri);
      } else {
        console.warn(" No video URI received from recording");
        setError("Recording failed - no video file created");
      }
    } catch (error) {
      console.error(" Stop recording error:", error);
      const errorMessage = error instanceof Error ? error.message : "Failed to stop recording";
      setError(errorMessage);
      onError?.(errorMessage);
      recordingPromiseRef.current = null;
    }
  }, [cameraRef, isRecording, onError, handleRecordingComplete]);

  // Refs for async operations
  const stopRecordingRef = useRef<typeof stopRecording>(() => Promise.resolve());
  useEffect(() => {
    stopRecordingRef.current = stopRecording;
  }, [stopRecording]);

  const pauseRecording = useCallback(async () => {
    if (!isRecording || isPaused) return;

    try {
      // Note: Camera recording pause/resume not directly supported
      // This would require custom implementation with multiple segments
      setIsPaused(true);
      if (timerRef.current) {
        clearInterval(timerRef.current);
        timerRef.current = null;
      }
    } catch (error) {
      console.warn("Failed to pause recording:", error);
    }
  }, [isRecording, isPaused]);

  const resumeRecording = useCallback(async () => {
    if (!isRecording || !isPaused) return;

    try {
      setIsPaused(false);
      // Restart timer
      timerRef.current = setInterval(() => {
        setRecordingTime((prev) => prev + 1);
      }, 1000);
    } catch (error) {
      console.warn("Failed to resume recording:", error);
    }
  }, [isRecording, isPaused]);

  const startVideoProcessing = useCallback(
    async (videoUri?: string) => {
      const uriToProcess = videoUri || recordedVideoUri;
      if (!uriToProcess) {
        throw new Error("No video to process");
      }

      try {
        console.log(" Starting video processing for:", uriToProcess);

        // Start processing
        setIsProcessing(true);
        setProcessingProgress(0);
        setProcessingStatus("Starting video processing...");

        const processingOptions: VideoProcessingOptions = {
          enableFaceBlur,
          enableVoiceChange,
          enableTranscription: true,
          quality,
          voiceEffect,
          onProgress: (progress, status) => {
            setProcessingProgress(progress);
            setProcessingStatus(status);
          },
        };

        let processed: ProcessedVideo;

        if (env.expoGo) {
          console.log(" Processing video in Expo Go mode");
          // Expo Go mode - try local processing first, fallback to server
          try {
            processed = await unifiedVideoProcessingService.processVideo(uriToProcess, {
              ...processingOptions,
              mode: ProcessingMode.LOCAL,
            });
          } catch (localError) {
            console.warn("Local processing failed in Expo Go, trying server:", localError);
            try {
              processed = await unifiedVideoProcessingService.processVideo(uriToProcess, {
                ...processingOptions,
                mode: ProcessingMode.SERVER,
              });
            } catch (serverError) {
              console.warn("Server processing failed, using fallback:", serverError);
              // Fallback: create a basic processed video object
              processed = {
                uri: uriToProcess,
                width: 1920,
                height: 1080,
                duration: 30,
                size: 0,
                transcription: "Transcription not available in Expo Go",
                faceBlurApplied: enableFaceBlur,
                voiceChangeApplied: enableVoiceChange,
              } as ProcessedVideo;
            }
          }
        } else {
          // Development build - use dual mode
          processed = await unifiedVideoProcessingService.processVideo(uriToProcess, {
            ...processingOptions,
            mode: processingMode,
          });
        }

        console.log(" Video processing completed successfully:", processed);
        setProcessedVideo(processed);
        setIsProcessing(false);
        setProcessingProgress(100);
        setProcessingStatus("Processing complete!");

        console.log(" Calling onProcessingComplete callback...");
        if (onProcessingComplete) {
          onProcessingComplete(processed);
          console.log(" onProcessingComplete callback called");
        } else {
          console.warn(" No onProcessingComplete callback provided");
        }
      } catch (error) {
        console.error(" Video processing failed:", error);
        console.error(" Error details:", {
          message: error instanceof Error ? error.message : "Unknown error",
          stack: error instanceof Error ? error.stack : undefined,
          error,
        });
        const errorMessage = error instanceof Error ? error.message : "Video processing failed";
        setError(errorMessage);
        if (onError) {
          onError(errorMessage);
          console.log(" onError callback called with:", errorMessage);
        } else {
          console.warn(" No onError callback provided");
        }
        setIsProcessing(false);
        setProcessingProgress(0);
        setProcessingStatus("");
      }
    },
    [
      enableFaceBlur,
      enableVoiceChange,
      quality,
      voiceEffect,
      processingMode,
      onProcessingComplete,
      onError,
      recordedVideoUri,
    ],
  );

  const startLiveTranscription = useCallback(async () => {
    if (!enableLiveCaptions) return;

    try {
      if (env.expoGo) {
        // Expo Go - simulate live transcription
        simulateLiveTranscription();
      } else {
        // Development build - use real speech recognition
        await startRealTimeSpeechRecognition();
      }
    } catch (error) {
      console.warn("Failed to start live transcription:", error);
    }
  }, [enableLiveCaptions]);

  const stopLiveTranscription = useCallback(() => {
    if (speechRecognitionRef.current) {
      speechRecognitionRef.current.stop();
    }
    setLiveTranscription("");
  }, []);

  const simulateLiveTranscription = () => {
    // Simulate live transcription for demo purposes
    const phrases = [
      "I'm recording my anonymous confession...",
      "This is something I've never shared before...",
      "I need to get this off my chest...",
      "Here's my story...",
    ];

    let currentIndex = 0;
    const interval = setInterval(() => {
      if (currentIndex < phrases.length && isRecordingRef.current) {
        setLiveTranscription(phrases[currentIndex]);
        currentIndex++;
      } else {
        clearInterval(interval);
      }
    }, 3000);

    // Clear interval when recording stops
    return () => clearInterval(interval);
  };

  const startRealTimeSpeechRecognition = async () => {
    try {
      // This would integrate with a speech recognition service
      // For now, we'll use the existing transcribe-audio API periodically
      console.log(" Starting real-time speech recognition");

      // Placeholder for real implementation
      // Would use libraries like @react-native-voice/voice or similar
    } catch (error) {
      console.warn("Real-time speech recognition failed:", error);
      // Fallback to simulation
      simulateLiveTranscription();
    }
  };

  const toggleCamera = useCallback(() => {
    setFacing((current) => (current === "back" ? "front" : "back"));
  }, []);

  const reset = useCallback(() => {
    setIsRecording(false);
    setIsPaused(false);
    setRecordingTime(0);
    setIsProcessing(false);
    setProcessingProgress(0);
    setProcessingStatus("");
    setLiveTranscription("");
    setProcessedVideo(undefined);
    setError(undefined);

    if (timerRef.current) {
      clearInterval(timerRef.current);
      timerRef.current = null;
    }

    stopLiveTranscription();
  }, []);

  const cleanup = useCallback(() => {
    reset();

    if (recordingRef.current) {
      recordingRef.current = null;
    }

    if (audioRecorderRef.current) {
      audioRecorderRef.current = null;
    }
  }, [reset]);

  // State object
  const state: VideoRecorderState = {
    isRecording,
    isProcessing,
    recordingTime,
    processingProgress,
    processingStatus,
    hasPermissions: permissionState.camera && permissionState.microphone,
    isInitialized,
    error,
  };

  // Controls object
  const controls: VideoRecorderControls = {
    startRecording,
    stopRecording,
    pauseRecording,
    resumeRecording,
    toggleCamera,
    reset,
    cleanup,
    startProcessing: startVideoProcessing,
  };

  // Data object
  const data: VideoRecorderData = {
    cameraRef,
    facing,
    liveTranscription,
    processedVideo,
  };

  return {
    state,
    controls,
    data,
    // Convenience getters
    isRecording,
    isProcessing,
    recordingTime,
    hasPermissions: hasVideoPermissions,
    error,
    requestPermissions: requestVideoPermissions,
    permissionState,
  };
};

import { create } from "zustand";
import { persist, createJSONStorage } from "zustand/middleware";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { registerStoreCleanup } from "../utils/storeCleanup";
import { trackStoreOperation } from "../utils/storePerformanceMonitor";
import {
  VideoPlayerInterface,
  VideoPlayerState,
  VideoPlayerCapabilities,
  VideoPlaybackState,
} from "../types/videoPlayer";
import { BaseVideoError, VideoErrorCode, VideoErrorSeverity } from "../types/videoErrors";
import {
  disposeVideoPlayer,
  scheduleVideoPlayerDisposal,
  DisposalStrategy,
  DisposalConfig,
} from "../utils/videoPlayerDisposal";

interface VideoPlayerRef {
  id: string;
  player: VideoPlayerInterface;
  state: VideoPlayerState;
  capabilities: VideoPlayerCapabilities;
  playbackState?: VideoPlaybackState;
  lastActivity: number;
  disposalAttempts: number;
  errorCount: number;
}

// Comment 3: Use plain POJO for serializable error
interface SerializableError {
  code: VideoErrorCode;
  message: string;
  severity: VideoErrorSeverity;
  at: number;
}

interface PlayerMetadata {
  isPlaying: boolean;
  lastError?: SerializableError;
  recoveryAttempts: number;
  lastRecoveryTime?: number;
}

interface GlobalVideoState {
  videoPlayers: Map<string, VideoPlayerRef>;
  playersMeta: Record<string, PlayerMetadata>;
  currentTab: string;
  hermesCompatMode: boolean;
  cleanupInterval?: NodeJS.Timeout;
  errorMetrics: {
    disposalErrors: number;
    recoverySuccesses: number;
    totalErrors: number;
  };

  // Actions
  registerVideoPlayer: (id: string, player: VideoPlayerInterface, capabilities?: VideoPlayerCapabilities) => void;
  unregisterVideoPlayer: (id: string) => Promise<void>;
  pauseAllVideos: () => Promise<void>;
  resumeVideosForTab: (tabName: string) => Promise<void>;
  setCurrentTab: (tabName: string) => void;
  updatePlayerState: (id: string, state: Partial<VideoPlaybackState>) => void;
  recoverPlayer: (id: string) => Promise<boolean>;
  cleanupStalePlayers: () => Promise<void>;
  updateErrorMetrics: (type: "disposal" | "recovery" | "general", success: boolean) => void;
}

// Hermes detection utility
const isHermesRuntime = (): boolean => {
  try {
    // @ts-ignore - HermesInternal is a global object in Hermes runtime
    return typeof HermesInternal !== "undefined";
  } catch {
    return false;
  }
};

// Comment 1: Map disposal strategy types
const mapDisposalStrategy = (type: "immediate" | "delayed" | "forced"): DisposalStrategy => {
  switch (type) {
    case "immediate":
      return DisposalStrategy.GRACEFUL;
    case "delayed":
      return DisposalStrategy.SCHEDULED;
    case "forced":
      return DisposalStrategy.FORCED;
    default:
      return DisposalStrategy.GRACEFUL;
  }
};

export const useGlobalVideoStore = create<GlobalVideoState>()(
  persist(
    (set, get) => ({
      videoPlayers: new Map(),
      playersMeta: {},
      currentTab: "Home",
      hermesCompatMode: isHermesRuntime(),
      cleanupInterval: undefined,
      errorMetrics: {
        disposalErrors: 0,
        recoverySuccesses: 0,
        totalErrors: 0,
      },

      registerVideoPlayer: (id, player, capabilities) => {
        const t0 = Date.now();
        const { videoPlayers, playersMeta } = get();
        const newPlayers = new Map(videoPlayers);

        // Clean up existing player if it exists
        const existingPlayer = videoPlayers.get(id);
        if (existingPlayer?.player) {
          // Comment 1 & 8: Use centralized disposal utility with schedule
          scheduleVideoPlayerDisposal(id, existingPlayer.player, 0, {
            strategy: DisposalStrategy.SCHEDULED,
            timeout: 1000,
            retries: 2,
          });
        }

        // Create new player reference with enhanced metadata
        const playerRef: VideoPlayerRef = {
          id,
          player,
          state: VideoPlayerState.Idle,
          capabilities: capabilities || {
            canPlay: true,
            canPause: true,
            canSeek: false,
            canSetVolume: true,
            canSetPlaybackRate: false,
            supportsFullscreen: false,
            supportsPiP: false,
          },
          lastActivity: Date.now(),
          disposalAttempts: 0,
          errorCount: 0,
        };

        // Preserve existing metadata
        const isPlaying = playersMeta[id]?.isPlaying ?? false;
        newPlayers.set(id, playerRef);

        set({
          videoPlayers: newPlayers,
          playersMeta: {
            ...playersMeta,
            [id]: {
              isPlaying,
              recoveryAttempts: 0,
            },
          },
        });

        trackStoreOperation("globalVideoStore", "registerVideoPlayer", Date.now() - t0);

        // Start cleanup interval if not running
        if (!get().cleanupInterval) {
          const interval = setInterval(() => {
            get().cleanupStalePlayers();
          }, 30000); // Run every 30 seconds
          set({ cleanupInterval: interval });
        }
      },

      // Comment 2: Always remove player from store, force dispose on failure
      unregisterVideoPlayer: async (id) => {
        const t0 = Date.now();
        const { videoPlayers, playersMeta } = get();
        const playerRef = videoPlayers.get(id);

        // Always remove from store first
        const newPlayers = new Map(videoPlayers);
        newPlayers.delete(id);
        const { [id]: _removed, ...restMeta } = playersMeta;

        set({
          videoPlayers: newPlayers,
          playersMeta: restMeta,
        });

        // Attempt disposal if player exists
        if (playerRef?.player) {
          try {
            // Comment 1: Use centralized disposal utility
            const result = await disposeVideoPlayer(id, playerRef.player, {
              strategy: DisposalStrategy.GRACEFUL,
              timeout: 500,
              retries: 3,
            });

            if (result.success) {
              get().updateErrorMetrics("disposal", true);
            } else {
              // Comment 2: Force disposal in background on failure
              disposeVideoPlayer(id, playerRef.player, {
                strategy: DisposalStrategy.FORCED,
                retries: 1,
                timeout: 100,
              }).then(
                () => get().updateErrorMetrics("disposal", true),
                () => get().updateErrorMetrics("disposal", false),
              );
            }
          } catch (error) {
            get().updateErrorMetrics("disposal", false);

            // Comment 2: Force disposal in background
            disposeVideoPlayer(id, playerRef.player, {
              strategy: DisposalStrategy.FORCED,
              retries: 1,
              timeout: 100,
            }).catch(() => {
              // Ignore errors, player is already removed from store
            });
          }
        }

        trackStoreOperation("globalVideoStore", "unregisterVideoPlayer", Date.now() - t0);
      },

      pauseAllVideos: async () => {
        const t0 = Date.now();
        const { videoPlayers, playersMeta } = get();

        const pauseOperations = Array.from(videoPlayers.entries()).map(async ([id, playerRef]) => {
          try {
            if (playerRef.player) {
              if (typeof playerRef.player.pause === "function") {
                await playerRef.player.pause();
              }
              if (typeof playerRef.player.setMuted === "function") {
                playerRef.player.setMuted(true);
              }
            }
            return { id, success: true };
          } catch (error) {
            get().updateErrorMetrics("general", false);
            if (__DEV__) console.warn(` Failed to pause video ${id}:`, error);
            return { id, success: false };
          }
        });

        const results = await Promise.allSettled(pauseOperations);

        // Update metadata for all players
        const nextMeta: Record<string, PlayerMetadata> = {};
        Object.keys(playersMeta).forEach((k) => {
          nextMeta[k] = { ...playersMeta[k], isPlaying: false };
        });
        set({ playersMeta: nextMeta });

        trackStoreOperation("globalVideoStore", "pauseAllVideos", Date.now() - t0);
      },

      resumeVideosForTab: async (tabName) => {
        const t0 = Date.now();
        const { videoPlayers, currentTab, playersMeta } = get();

        if (tabName === "Videos" && currentTab === "Videos") {
          const resumeOperations = Array.from(videoPlayers.entries()).map(async ([id, playerRef]) => {
            try {
              if (playerRef.player && playersMeta[id]?.isPlaying) {
                if (typeof playerRef.player.setMuted === "function") {
                  playerRef.player.setMuted(false);
                }
                if (typeof playerRef.player.play === "function") {
                  await playerRef.player.play();
                }
              }
              return { id, success: true };
            } catch (error) {
              get().updateErrorMetrics("general", false);

              // Attempt recovery
              const recovered = await get().recoverPlayer(id);
              if (!recovered && __DEV__) {
                console.warn(` Failed to resume video ${id}:`, error);
              }
              return { id, success: recovered };
            }
          });

          await Promise.allSettled(resumeOperations);
        }

        trackStoreOperation("globalVideoStore", "resumeVideosForTab", Date.now() - t0);
      },

      setCurrentTab: (tabName) => {
        const { currentTab } = get();
        if (currentTab !== tabName) {
          if (tabName !== "Videos") {
            get().pauseAllVideos();
          }
          set({ currentTab: tabName });
          if (tabName === "Videos") {
            requestAnimationFrame(() => get().resumeVideosForTab(tabName));
          }
        }
      },

      // Comment 7: Improved state derivation logic
      updatePlayerState: (id, state) => {
        const { videoPlayers, playersMeta } = get();
        const playerRef = videoPlayers.get(id);

        if (playerRef) {
          const newPlayers = new Map(videoPlayers);
          const updatedRef: VideoPlayerRef = {
            ...playerRef,
            playbackState: { ...playerRef.playbackState, ...state },
            lastActivity: Date.now(),
          };

          // Only derive state when both values are meaningful
          if (
            state.currentTime !== undefined &&
            state.duration !== undefined &&
            state.currentTime > 0 &&
            state.duration > 0
          ) {
            updatedRef.state = state.currentTime > 0 ? VideoPlayerState.Playing : VideoPlayerState.Idle;
          }

          newPlayers.set(id, updatedRef);

          set({
            videoPlayers: newPlayers,
            playersMeta: {
              ...playersMeta,
              [id]: {
                ...playersMeta[id],
                isPlaying: state.isPlaying ?? playersMeta[id]?.isPlaying ?? false,
              },
            },
          });
        } else {
          set({
            playersMeta: {
              ...playersMeta,
              [id]: {
                ...playersMeta[id],
                isPlaying: state.isPlaying ?? false,
              },
            },
          });
        }
      },

      recoverPlayer: async (id) => {
        const { videoPlayers, playersMeta } = get();
        const playerRef = videoPlayers.get(id);

        if (!playerRef?.player) return false;

        const metadata = playersMeta[id];
        if (metadata?.recoveryAttempts >= 3) {
          return false; // Too many recovery attempts
        }

        try {
          const playerInstance = playerRef.player as any;
          if (typeof playerInstance?.reset === "function") {
            await playerInstance.reset();
          }

          // Update recovery metrics
          set({
            playersMeta: {
              ...playersMeta,
              [id]: {
                ...metadata,
                recoveryAttempts: (metadata?.recoveryAttempts || 0) + 1,
                lastRecoveryTime: Date.now(),
              },
            },
          });

          get().updateErrorMetrics("recovery", true);
          return true;
        } catch (error) {
          get().updateErrorMetrics("recovery", false);

          const message = error instanceof Error ? error.message : String(error);

          if (error instanceof BaseVideoError) {
            set({
              playersMeta: {
                ...playersMeta,
                [id]: {
                  ...playersMeta[id],
                  lastError: {
                    code: error.code,
                    message,
                    severity: error.severity || VideoErrorSeverity.ERROR,
                    at: Date.now(),
                  },
                },
              },
            });
          } else {
            set({
              playersMeta: {
                ...playersMeta,
                [id]: {
                  ...playersMeta[id],
                  lastError: {
                    code: VideoErrorCode.Unknown,
                    message,
                    severity: VideoErrorSeverity.ERROR,
                    at: Date.now(),
                  },
                },
              },
            });
          }
          return false;
        }
      },

      cleanupStalePlayers: async () => {
        const { videoPlayers } = get();
        const now = Date.now();
        const staleThreshold = 5 * 60 * 1000; // 5 minutes

        const stalePlayerIds: string[] = [];

        videoPlayers.forEach((playerRef, id) => {
          if (now - playerRef.lastActivity > staleThreshold) {
            stalePlayerIds.push(id);
          }
        });

        for (const id of stalePlayerIds) {
          await get().unregisterVideoPlayer(id);
        }
      },

      updateErrorMetrics: (type, success) => {
        const { errorMetrics } = get();
        const newMetrics = { ...errorMetrics };

        switch (type) {
          case "disposal":
            if (!success) newMetrics.disposalErrors++;
            break;
          case "recovery":
            if (success) newMetrics.recoverySuccesses++;
            break;
          case "general":
            if (!success) newMetrics.totalErrors++;
            break;
        }

        set({ errorMetrics: newMetrics });
      },
    }),
    {
      name: "global-video-store",
      storage: createJSONStorage(() => AsyncStorage),
      partialize: (state) => ({
        currentTab: state.currentTab,
        playersMeta: state.playersMeta,
        errorMetrics: state.errorMetrics,
      }),
      version: 2,
    },
  ),
);

// Centralized cleanup registration with enhanced disposal
registerStoreCleanup("globalVideoStore", async () => {
  const state = useGlobalVideoStore.getState();
  const { videoPlayers } = state;

  // Clear cleanup interval
  if (state.cleanupInterval) {
    clearInterval(state.cleanupInterval);
  }

  // Comment 1: Use centralized disposal utility
  const disposalPromises = Array.from(videoPlayers.entries()).map(([id, playerRef]) =>
    disposeVideoPlayer(id, playerRef.player, {
      strategy: DisposalStrategy.FORCED,
      timeout: 100,
      retries: 1,
    }),
  );

  await Promise.allSettled(disposalPromises);

  useGlobalVideoStore.setState({
    videoPlayers: new Map(),
    cleanupInterval: undefined,
  });
});

