// Real-time subscription channels
let repliesChannel: any = null;
let typingChannel: any = null;
let reconnectTimer: any = null;
let reconnectAttempts = 0;
let typingTimers: Record<string, NodeJS.Timeout> = {};

export const useReplyStore = create<ReplyState>()(
  persist(
    (set, get) => ({
      replies: {},
      pagination: {},
      loading: false,
      error: null,
      typingUsers: {},
      drafts: {},
      searchResults: {},
      searchQuery: "",
      offlineQueue: [],
      connectionStatus: "online",

      loadReplies: async (confessionId: string, parentId?: string) => {
        set({ loading: true, error: null });
        try {
          trackStoreOperation("replyStore", "loadReplies", Date.now());

          if (!isValidForDatabase(confessionId)) {
            set((state) => ({
              replies: { ...state.replies, [confessionId]: [] },
              loading: false,
            }));
            return;
          }

          const {
            data: { user },
          } = await supabase.auth.getUser();

          const INITIAL_LIMIT = 20;
          let query = supabase
            .from("replies")
            .select(
              `
              *,
              reactions:comment_reactions(*),
              replies:replies!parent_id(count)
            `,
            )
            .eq("confession_id", confessionId)
            .order("created_at", { ascending: false })
            .limit(INITIAL_LIMIT);

          if (parentId) {
            query = query.eq("parent_id", parentId);
          } else {
            query = query.is("parent_id", null);
          }

          const { data, error } = await wrapWithRetry(async () => query)();

          if (error) throw error;

          const rows = (data ?? []) as {
            id: string;
            confession_id: string;
            user_id?: string | null;
            content: string;
            is_anonymous: boolean;
            likes?: number | null;
            created_at: string;
            replies?: { count: number }[] | null;
            reactions?: { type: string }[] | null;
            edited_at?: string | null;
            deleted_at?: string | null;
            flagged?: boolean;
            flag_reason?: string | null;
            parent_id?: string | null;
          }[];

          // Get user reactions if authenticated
          let userReactions: Record<string, ReactionType[]> = {};
          if (user && rows.length > 0) {
            const replyIds = rows.map((reply) => reply.id);
            // Comment reactions table not yet implemented
            const reactionsData: any[] = [];

            if (reactionsData) {
              reactionsData.forEach((r) => {
                if (!userReactions[r.reply_id]) userReactions[r.reply_id] = [];
                userReactions[r.reply_id].push(r.type as ReactionType);
              });
            }
          }

          // Get user likes
          let userLikes: string[] = [];
          if (user && rows.length > 0) {
            const replyIds = rows.map((reply) => reply.id);
            const { data: likesData } = await supabase
              .from("user_likes")
              .select("reply_id")
              .eq("user_id", user.id)
              .in("reply_id", replyIds);

            const likeRows = (likesData ?? []) as { reply_id: string | null }[];
            userLikes = likeRows.map((like) => like.reply_id).filter((id): id is string => Boolean(id));
          }

          const replies: Reply[] = rows.map((item) => {
            const reactionCounts: Record<ReactionType, number> = {
              heart: 0,
              laugh: 0,
              sad: 0,
              angry: 0,
              wow: 0,
              thumbs_up: 0,
            };

            const reply: Reply = {
              id: item.id,
              confessionId: item.confession_id,
              userId: item.user_id || undefined,
              content: item.content,
              isAnonymous: item.is_anonymous,
              likes: item.likes || 0,
              isLiked: userLikes.includes(item.id),
              timestamp: new Date(item.created_at).getTime(),
              parentId: item.parent_id || undefined,
              replyCount: item.replies?.[0]?.count || 0,
              reactions: [],
              reactionCounts,
              userReactions: userReactions[item.id] || [],
              edited_at: item.edited_at || undefined,
              deleted_at: item.deleted_at || undefined,
              flagged: item.flagged,
              flag_reason: item.flag_reason || undefined,
            };

            return reply;
          });

          const hasMore = rows.length >= INITIAL_LIMIT;
          const lastCreatedAt = rows[rows.length - 1]?.created_at;

          set((state) => ({
            replies: {
              ...state.replies,
              [confessionId]: replies,
            },
            pagination: {
              ...state.pagination,
              [confessionId]: {
                hasMore,
                lastCreatedAt,
                isLoadingMore: false,
                isLoading: false,
                totalCount: replies.length,
              },
            },
            loading: false,
          }));

          // Track analytics
          VideoDataService.trackVideoEvent("comments_loaded", {
            confession_id: confessionId,
            count: replies.length,
            has_parent: !!parentId,
          });
        } catch (error) {
          console.error("Error loading replies:", error);
          set({
            error: error instanceof Error ? error.message : "Failed to load comments",
            loading: false,
          });
        }
      },

      loadMoreReplies: async (confessionId: string) => {
        const state = get();
        const page = state.pagination[confessionId];
        if (!page || page.isLoadingMore || !page.hasMore) return;

        set({
          pagination: {
            ...state.pagination,
            [confessionId]: { ...page, isLoadingMore: true },
          },
        });

        try {
          const LIMIT = 10;
          let query = supabase
            .from("replies")
            .select(
              `
              *,
              reactions:comment_reactions(*),
              replies:replies!parent_id(count)
            `,
            )
            .eq("confession_id", confessionId)
            .is("parent_id", null)
            .order("created_at", { ascending: false })
            .limit(LIMIT);

          if (page.lastCreatedAt) {
            query = query.lt("created_at", page.lastCreatedAt);
          }

          const { data, error } = await wrapWithRetry(async () => query)();
          if (error) throw error;

          const newReplies: Reply[] = (data || []).map((item) => ({
            id: item.id,
            confessionId: item.confession_id,
            userId: item.user_id || undefined,
            content: item.content,
            isAnonymous: item.is_anonymous,
            likes: item.likes || 0,
            timestamp: new Date(item.created_at).getTime(),
            parentId: (item as any).parent_id,
            replyCount: item.replies?.[0]?.count || 0,
            reactions: item.reactions || [],
          }));

          const hasMore = (data?.length || 0) >= LIMIT;
          const lastCreatedAt = data?.[data.length - 1]?.created_at || page.lastCreatedAt;

          set((curr) => ({
            replies: {
              ...curr.replies,
              [confessionId]: [...(curr.replies[confessionId] || []), ...newReplies],
            },
            pagination: {
              ...curr.pagination,
              [confessionId]: {
                hasMore,
                lastCreatedAt,
                isLoadingMore: false,
                totalCount: (curr.replies[confessionId]?.length || 0) + newReplies.length,
              },
            },
          }));
        } catch (error) {
          set((curr) => ({
            error: error instanceof Error ? error.message : "Failed to load more comments",
            pagination: {
              ...curr.pagination,
              [confessionId]: { ...(curr.pagination[confessionId] || {}), isLoadingMore: false },
            },
          }));
        }
      },

      addReply: async (confessionId: string, content: string, isAnonymous = true, parentId?: string | null) => {
        const state = get();

        // Save to offline queue if offline
        if (state.connectionStatus === "offline") {
          set((state) => ({
            offlineQueue: [
              ...state.offlineQueue,
              {
                type: "add",
                payload: { confessionId, content, isAnonymous, parentId },
                timestamp: Date.now(),
              },
            ],
          }));
          return;
        }

        set({ loading: true, error: null });
        try {
          if (!isValidForDatabase(confessionId)) {
            throw new Error("Cannot add replies to sample confessions");
          }

          const {
            data: { user },
          } = await supabase.auth.getUser();

          // Optimistic update
          const tempId = `temp-${Date.now()}`;
          const optimisticReply: Reply = {
            id: tempId,
            confessionId,
            userId: user?.id,
            content: content.trim(),
            isAnonymous,
            likes: 0,
            isLiked: false,
            timestamp: Date.now(),
            parentId,
            reactions: [],
            reactionCounts: { heart: 0, laugh: 0, sad: 0, angry: 0, wow: 0, thumbs_up: 0 },
            userReactions: [],
          };

          set((state) => ({
            replies: {
              ...state.replies,
              [confessionId]: [optimisticReply, ...(state.replies[confessionId] || [])],
            },
          }));

          const { data, error } = await supabase
            .from("replies")
            .insert({
              confession_id: confessionId,
              user_id: user?.id,
              content: content.trim(),
              is_anonymous: isAnonymous,
              parent_id: parentId,
            })
            .select()
            .single();

          if (error) throw error;

          const newReply: Reply = {
            id: data.id,
            confessionId: data.confession_id,
            userId: data.user_id || undefined,
            content: data.content,
            isAnonymous: data.is_anonymous,
            likes: data.likes || 0,
            isLiked: false,
            timestamp: new Date(data.created_at).getTime(),
            parentId: data.parent_id,
            reactions: [],
            reactionCounts: { heart: 0, laugh: 0, sad: 0, angry: 0, wow: 0, thumbs_up: 0 },
            userReactions: [],
          };

          // Replace optimistic update with real data
          set((state) => ({
            replies: {
              ...state.replies,
              [confessionId]: state.replies[confessionId].map((r) => (r.id === tempId ? newReply : r)),
            },
            loading: false,
          }));

          // Clear draft after successful submission
          get().clearDraft(confessionId);

          // Trigger cache invalidation
          invalidateCache("reply_created", { replyId: newReply.id, confessionId });

          // Track analytics
          VideoDataService.trackVideoEvent("comment_added", {
            confession_id: confessionId,
            parent_id: parentId,
            is_anonymous: isAnonymous,
            content_length: content.length,
          });
        } catch (error) {
          // Remove optimistic update on error
          set((state) => ({
            replies: {
              ...state.replies,
              [confessionId]: state.replies[confessionId].filter((r) => !r.id.startsWith("temp-")),
            },
            error: error instanceof Error ? error.message : "Failed to add comment",
            loading: false,
          }));
          throw error;
        }
      },

      deleteReply: async (replyId: string, confessionId?: string) => {
        set({ loading: true, error: null });
        try {
          const {
            data: { user },
          } = await supabase.auth.getUser();
          if (!user) throw new Error("User not authenticated");

          const { error } = await supabase.from("replies").delete().eq("id", replyId).eq("user_id", user.id);

          if (error) throw error;

          // Update all confession replies that might contain this reply
          set((state) => {
            const newReplies = { ...state.replies };
            Object.keys(newReplies).forEach((cId) => {
              newReplies[cId] = newReplies[cId].filter((r) => r.id !== replyId);
            });
            return { replies: newReplies, loading: false };
          });

          VideoDataService.trackVideoEvent("comment_deleted", { confession_id: "" }); // TODO: Add confession_id
        } catch (error) {
          set({
            error: error instanceof Error ? error.message : "Failed to delete comment",
            loading: false,
          });
          throw error;
        }
      },

      editReply: async (replyId: string, content: string) => {
        set({ loading: true, error: null });
        try {
          const {
            data: { user },
          } = await supabase.auth.getUser();
          if (!user) throw new Error("User not authenticated");

          const { data, error } = await supabase
            .from("replies")
            .update({
              content: content.trim(),
              edited_at: new Date().toISOString(),
            })
            .eq("id", replyId)
            .eq("user_id", user.id)
            .select()
            .single();

          if (error) throw error;

          // Update in all confessions
          set((state) => {
            const newReplies = { ...state.replies };
            Object.keys(newReplies).forEach((cId) => {
              newReplies[cId] = newReplies[cId].map((r) =>
                r.id === replyId ? { ...r, content: data.content, edited_at: data.edited_at } : r,
              );
            });
            return { replies: newReplies, loading: false };
          });

          VideoDataService.trackVideoEvent("comment_edited", { confession_id: "" }); // TODO: Add confession_id
        } catch (error) {
          set({
            error: error instanceof Error ? error.message : "Failed to edit comment",
            loading: false,
          });
          throw error;
        }
      },

      toggleReplyLike: async (replyId: string, confessionId: string) => {
        return debouncedOperation(`reply-like-${replyId}`, async () => {
          const state = get();
          const replies = state.replies[confessionId] || [];
          const reply = replies.find((r) => r.id === replyId);

          if (!reply) return;

          const {
            data: { user },
          } = await supabase.auth.getUser();
          if (!user) return;

          const newIsLiked = !reply.isLiked;
          const optimisticLikes = newIsLiked ? reply.likes + 1 : Math.max(0, reply.likes - 1);

          // Optimistic update
          set((state) => ({
            replies: {
              ...state.replies,
              [confessionId]: replies.map((r) =>
                r.id === replyId ? { ...r, likes: optimisticLikes, isLiked: newIsLiked } : r,
              ),
            },
          }));

          try {
            if (newIsLiked) {
              await supabase.from("user_likes").insert({ user_id: user.id, reply_id: replyId });
              await supabase.from("replies").update({ likes: optimisticLikes }).eq("id", replyId);
            } else {
              await supabase.from("user_likes").delete().eq("user_id", user.id).eq("reply_id", replyId);
              await supabase.from("replies").update({ likes: optimisticLikes }).eq("id", replyId);
            }

            VideoDataService.trackVideoEvent(newIsLiked ? "comment_liked" : "comment_unliked", {
              reply_id: replyId,
              confession_id: confessionId,
            });
          } catch (error) {
            // Revert optimistic update
            set((state) => ({
              replies: {
                ...state.replies,
                [confessionId]: replies,
              },
              error: error instanceof Error ? error.message : "Failed to update like",
            }));
          }
        });
      },

      // Threading actions
      loadThreadReplies: async (parentId: string) => {
        try {
          const { data, error } = await supabase
            .from("replies")
            .select("*")
            .eq("parent_id", parentId)
            .order("created_at", { ascending: true });

          if (error) throw error;

          // Update parent reply with children
          set((state) => {
            const newReplies = { ...state.replies };
            Object.keys(newReplies).forEach((cId) => {
              newReplies[cId] = newReplies[cId].map((r) => {
                if (r.id === parentId) {
                  return {
                    ...r,
                    replies: data.map((d: any) => ({
                      id: d.id,
                      confessionId: d.confession_id,
                      content: d.content,
                      userId: d.user_id,
                      isAnonymous: d.is_anonymous,
                      likes: d.likes,
                      timestamp: new Date(d.created_at).getTime(),
                      parentId: d.parent_id,
                    })),
                  };
                }
                return r;
              });
            });
            return { replies: newReplies };
          });
        } catch (error) {
          console.error("Error loading thread replies:", error);
        }
      },

      collapseThread: (replyId: string) => {
        set((state) => {
          const newReplies = { ...state.replies };
          Object.keys(newReplies).forEach((cId) => {
            newReplies[cId] = newReplies[cId].map((r) => (r.id === replyId ? { ...r, replies: undefined } : r));
          });
          return { replies: newReplies };
        });
      },

      expandThread: (replyId: string) => {
        get().loadThreadReplies(replyId);
      },

      // Reaction actions
      addReaction: async (replyId: string, type: ReactionType) => {
        try {
          const {
            data: { user },
          } = await supabase.auth.getUser();
          if (!user) return;

          const { error } = await supabase
            .from("comment_reactions")
            .insert({ reply_id: replyId, user_id: user.id, type });

          if (error) throw error;

          // Update local state
          set((state) => {
            const newReplies = { ...state.replies };
            Object.keys(newReplies).forEach((cId) => {
              newReplies[cId] = newReplies[cId].map((r) => {
                if (r.id === replyId) {
                  const newReactionCounts = { ...r.reactionCounts };
                  newReactionCounts[type] = (newReactionCounts[type] || 0) + 1;
                  return {
                    ...r,
                    reactionCounts: newReactionCounts,
                    userReactions: [...(r.userReactions || []), type],
                  };
                }
                return r;
              });
            });
            return { replies: newReplies };
          });

          VideoDataService.trackVideoEvent("comment_reaction_added", {
            confession_id: "", // TODO: Add confession_id
            reaction_type: type,
          });
        } catch (error) {
          console.error("Error adding reaction:", error);
        }
      },

      removeReaction: async (replyId: string, type: ReactionType) => {
        try {
          const {
            data: { user },
          } = await supabase.auth.getUser();
          if (!user) return;

          const { error } = await supabase
            .from("comment_reactions")
            .delete()
            .eq("reply_id", replyId)
            .eq("user_id", user.id)
            .eq("type", type);

          if (error) throw error;

          // Update local state
          set((state) => {
            const newReplies = { ...state.replies };
            Object.keys(newReplies).forEach((cId) => {
              newReplies[cId] = newReplies[cId].map((r) => {
                if (r.id === replyId) {
                  const newReactionCounts = { ...r.reactionCounts };
                  newReactionCounts[type] = Math.max(0, (newReactionCounts[type] || 0) - 1);
                  return {
                    ...r,
                    reactionCounts: newReactionCounts,
                    userReactions: (r.userReactions || []).filter((rt) => rt !== type),
                  };
                }
                return r;
              });
            });
            return { replies: newReplies };
          });

          VideoDataService.trackVideoEvent("comment_reaction_removed", {
            confession_id: "", // TODO: Add confession_id
            reaction_type: type,
          });
        } catch (error) {
          console.error("Error removing reaction:", error);
        }
      },

      // Search actions
      searchComments: async (confessionId: string, query: string) => {
        set({ loading: true, searchQuery: query });
        try {
          const { data, error } = await supabase
            .from("replies")
            .select("*")
            .eq("confession_id", confessionId)
            .textSearch("content", query)
            .order("created_at", { ascending: false });

          if (error) throw error;

          const searchResults: Reply[] = (data || []).map((item) => ({
            id: item.id,
            confessionId: item.confession_id,
            userId: item.user_id || undefined,
            content: item.content,
            isAnonymous: item.is_anonymous,
            likes: item.likes || 0,
            timestamp: new Date(item.created_at).getTime(),
            parentId: (item as any).parent_id,
          }));

          set((state) => ({
            searchResults: { ...state.searchResults, [confessionId]: searchResults },
            loading: false,
          }));

          VideoDataService.trackVideoEvent("comments_searched", {
            confession_id: confessionId,
            query,
            results_count: searchResults.length,
          });
        } catch (error) {
          set({ error: error instanceof Error ? error.message : "Search failed", loading: false });
        }
      },

      clearSearch: () => {
        set({ searchResults: {}, searchQuery: "" });
      },

      // Moderation actions
      reportComment: async (replyId: string, reason: string) => {
        try {
          const {
            data: { user },
          } = await supabase.auth.getUser();
          if (!user) return;

          // Comment reports table not yet implemented
          const error = null;
          // await supabase
          //   .from("comment_reports")
          //   .insert({ reply_id: replyId, user_id: user.id, reason });

          if (error) throw error;

          VideoDataService.trackVideoEvent("comment_reported", { confession_id: "", reason }); // TODO: Add confession_id
        } catch (error) {
          console.error("Error reporting comment:", error);
        }
      },

      flagComment: async (replyId: string, reason: string) => {
        try {
          const { error } = await supabase
            .from("replies")
            .update({ flagged: true, flag_reason: reason })
            .eq("id", replyId);

          if (error) throw error;

          // Update local state
          set((state) => {
            const newReplies = { ...state.replies };
            Object.keys(newReplies).forEach((cId) => {
              newReplies[cId] = newReplies[cId].map((r) =>
                r.id === replyId ? { ...r, flagged: true, flag_reason: reason } : r,
              );
            });
            return { replies: newReplies };
          });
        } catch (error) {
          console.error("Error flagging comment:", error);
        }
      },

      // Typing indicators
      startTyping: async (confessionId: string, parentId?: string) => {
        const {
          data: { user },
        } = await supabase.auth.getUser();
        if (!user) return;

        const key = `${confessionId}-${parentId || "main"}`;

        // Clear existing timer
        if (typingTimers[key]) {
          clearTimeout(typingTimers[key]);
        }

        // Broadcast typing event
        if (typingChannel) {
          typingChannel.send({
            type: "broadcast",
            event: "typing",
            payload: { confessionId, parentId, userId: user.id },
          });
        }

        // Auto-stop typing after 3 seconds
        typingTimers[key] = setTimeout(() => {
          get().stopTyping(confessionId, parentId);
        }, 3000);
      },

      stopTyping: async (confessionId: string, parentId?: string) => {
        const {
          data: { user },
        } = await supabase.auth.getUser();
        if (!user) return;

        const key = `${confessionId}-${parentId || "main"}`;

        // Clear timer
        if (typingTimers[key]) {
          clearTimeout(typingTimers[key]);
          delete typingTimers[key];
        }

        // Broadcast stop typing event
        if (typingChannel) {
          typingChannel.send({
            type: "broadcast",
            event: "stop_typing",
            payload: { confessionId, parentId, userId: user.id },
          });
        }
      },

      // Draft management
      saveDraft: (confessionId: string, content: string, parentId?: string) => {
        const draft: CommentDraft = {
          id: `draft-${confessionId}`,
          confessionId,
          content,
          parentId,
          timestamp: Date.now(),
        };

        set((state) => ({
          drafts: { ...state.drafts, [confessionId]: draft },
        }));
      },

      loadDraft: (confessionId: string) => {
        const state = get();
        return state.drafts[confessionId] || null;
      },

      clearDraft: (confessionId: string) => {
        set((state) => {
          const newDrafts = { ...state.drafts };
          delete newDrafts[confessionId];
          return { drafts: newDrafts };
        });
      },

      // Offline support
      processOfflineQueue: async () => {
        const state = get();
        if (state.connectionStatus !== "online" || state.offlineQueue.length === 0) return;

        const queue = [...state.offlineQueue];
        set({ offlineQueue: [] });

        for (const item of queue) {
          try {
            switch (item.type) {
              case "add":
                await get().addReply(
                  item.payload.confessionId,
                  item.payload.content,
                  item.payload.isAnonymous,
                  item.payload.parentId,
                );
                break;
              case "edit":
                await get().editReply(item.payload.replyId, item.payload.content);
                break;
              case "delete":
                await get().deleteReply(item.payload.replyId);
                break;
              case "react":
                await get().addReaction(item.payload.replyId, item.payload.type);
                break;
            }
          } catch (error) {
            console.error("Error processing offline queue item:", error);
            // Re-add to queue if failed
            set((state) => ({
              offlineQueue: [...state.offlineQueue, item],
            }));
          }
        }
      },

      setConnectionStatus: (status) => {
        set({ connectionStatus: status });
        if (status === "online") {
          get().processOfflineQueue();
        }
      },

      // Real-time subscriptions
      subscribeToReplies: (confessionId: string) => {
        if (repliesChannel) return;

        repliesChannel = supabase
          .channel(`replies:${confessionId}`)
          .on(
            "postgres_changes",
            { event: "INSERT", schema: "public", table: "replies", filter: `confession_id=eq.${confessionId}` },
            (payload) => {
              const reply = payload.new as DatabaseReplyRecord;
              set((state) => {
                const existing = state.replies[confessionId] || [];
                if (existing.find((r) => r.id === reply.id)) return state;

                const newReply: Reply = {
                  id: reply.id,
                  confessionId: reply.confession_id,
                  userId: reply.user_id || undefined,
                  content: reply.content,
                  isAnonymous: reply.is_anonymous,
                  likes: reply.likes || 0,
                  timestamp: new Date(reply.created_at).getTime(),
                  parentId: reply.parent_id,
                };

                return {
                  replies: {
                    ...state.replies,
                    [confessionId]: [newReply, ...existing].slice(0, 200),
                  },
                };
              });
            },
          )
          .on("postgres_changes", { event: "UPDATE", schema: "public", table: "replies" }, (payload) => {
            const reply = payload.new as DatabaseReplyRecord;
            set((state) => {
              const newReplies = { ...state.replies };
              if (newReplies[confessionId]) {
                newReplies[confessionId] = newReplies[confessionId].map((r) =>
                  r.id === reply.id ? { ...r, likes: reply.likes || r.likes, content: reply.content } : r,
                );
              }
              return { replies: newReplies };
            });
          })
          .subscribe((status) => {
            if (status === "SUBSCRIBED") {
              reconnectAttempts = 0;
              set({ connectionStatus: "online" });
            }
            if (status === "CHANNEL_ERROR" || status === "CLOSED") {
              set({ connectionStatus: "reconnecting" });
              const delay = Math.min(30000, 1000 * Math.pow(2, reconnectAttempts++));
              reconnectTimer = setTimeout(() => {
                get().unsubscribeFromReplies();
                get().subscribeToReplies(confessionId);
              }, delay);
            }
          });
      },

      unsubscribeFromReplies: () => {
        if (repliesChannel) {
          repliesChannel.unsubscribe();
          repliesChannel = null;
        }
        if (reconnectTimer) {
          clearTimeout(reconnectTimer);
          reconnectTimer = null;
        }
      },

      subscribeToTypingIndicators: (confessionId: string) => {
        if (typingChannel) return;

        typingChannel = supabase
          .channel(`typing:${confessionId}`)
          .on("broadcast", { event: "typing" }, (payload) => {
            const { userId, parentId } = payload.payload;
            set((state) => {
              const key = parentId || "main";
              const typing = state.typingUsers[confessionId] || [];
              const newTyping = typing.filter((t) => t.userId !== userId);
              newTyping.push({ userId, replyId: parentId, timestamp: Date.now() });
              return {
                typingUsers: {
                  ...state.typingUsers,
                  [confessionId]: newTyping,
                },
              };
            });
          })
          .on("broadcast", { event: "stop_typing" }, (payload) => {
            const { userId } = payload.payload;
            set((state) => {
              const typing = state.typingUsers[confessionId] || [];
              return {
                typingUsers: {
                  ...state.typingUsers,
                  [confessionId]: typing.filter((t) => t.userId !== userId),
                },
              };
            });
          })
          .subscribe();
      },

      unsubscribeFromTypingIndicators: () => {
        if (typingChannel) {
          typingChannel.unsubscribe();
          typingChannel = null;
        }
        // Clear all typing timers
        Object.values(typingTimers).forEach((timer) => clearTimeout(timer));
        typingTimers = {};
      },

      // Bulk actions
      bulkDeleteReplies: async (replyIds: string[], confessionId: string) => {
        set({ loading: true, error: null });
        try {
          const {
            data: { user },
          } = await supabase.auth.getUser();
          if (!user) throw new Error("User not authenticated");

          const { error } = await supabase.from("replies").delete().in("id", replyIds).eq("user_id", user.id);

          if (error) throw error;

          set((state) => ({
            replies: {
              ...state.replies,
              [confessionId]: (state.replies[confessionId] || []).filter((r) => !replyIds.includes(r.id)),
            },
            loading: false,
          }));
        } catch (error) {
          set({ error: error instanceof Error ? error.message : "Failed to delete comments", loading: false });
          throw error;
        }
      },

      bulkToggleReplyLikes: async (replyIds: string[], confessionId: string, like: boolean) => {
        const state = get();
        const replies = state.replies[confessionId] || [];

        // Optimistic batch update
        set({
          replies: {
            ...state.replies,
            [confessionId]: replies.map((r) =>
              replyIds.includes(r.id)
                ? { ...r, likes: like ? r.likes + 1 : Math.max(0, r.likes - 1), isLiked: like }
                : r,
            ),
          },
        });

        try {
          const {
            data: { user },
          } = await supabase.auth.getUser();
          if (!user) return;

          if (like) {
            const rows = replyIds.map((id) => ({ user_id: user.id, reply_id: id }));
            const { error } = await supabase.from("user_likes").insert(rows);
            if (error) throw error;
          } else {
            const { error } = await supabase
              .from("user_likes")
              .delete()
              .eq("user_id", user.id)
              .in("reply_id", replyIds);
            if (error) throw error;
          }
        } catch (error) {
          // Revert on error
          set({ replies: { ...state.replies, [confessionId]: replies } });
          set({ error: error instanceof Error ? error.message : "Failed to update likes" });
        }
      },

      clearError: () => {
        set({ error: null });
      },

      getRepliesForConfession: (confessionId: string) => {
        const state = get();
        return state.replies[confessionId] ?? [];
      },
    }),
    {
      name: "reply-storage",
      storage: createJSONStorage(() => AsyncStorage),
      partialize: (state) => ({
        replies: state.replies,
        drafts: state.drafts,
        offlineQueue: state.offlineQueue,
      }),
    },
  ),
);

// Cleanup function
const cleanupSubscriptions = () => {
  const store = useReplyStore.getState();
  store.unsubscribeFromReplies();
  store.unsubscribeFromTypingIndicators();
};

// Register cleanup
registerStoreCleanup("replyStore", cleanupSubscriptions);

import { create } from "zustand";
import { persist, createJSONStorage } from "zustand/middleware";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { supabase } from "../lib/supabase";
import type { Confession } from "../types/confession";
import { offlineQueue, OFFLINE_ACTIONS } from "../utils/offlineQueue";
import { trackStoreOperation } from "../utils/storePerformanceMonitor";
import { normalizeConfessions, normalizeConfession } from "../utils/confessionNormalizer";

interface SavedState {
  savedConfessionIds: string[];
  savedConfessions: Confession[];
  isLoading: boolean;
  isLoadingMore: boolean;
  hasMore: boolean;
  error: string | null;
  lastFetchTime: number | null;

  // Actions
  saveConfession: (confessionId: string) => Promise<void>;
  unsaveConfession: (confessionId: string) => Promise<void>;
  batchSaveConfessions: (confessionIds: string[]) => Promise<void>;
  batchUnsaveConfessions: (confessionIds: string[]) => Promise<void>;
  isSaved: (confessionId: string) => boolean;
  loadSavedConfessions: (refresh?: boolean) => Promise<void>;
  loadMoreSavedConfessions: () => Promise<void>;
  clearAllSaved: () => Promise<void>;
  clearError: () => void;
}

const ITEMS_PER_PAGE = 20;

export const useSavedStore = create<SavedState>()(
  persist(
    (set, get) => ({
      savedConfessionIds: [],
      savedConfessions: [],
      isLoading: false,
      isLoadingMore: false,
      hasMore: true,
      error: null,
      lastFetchTime: null,

      saveConfession: async (confessionId: string) => {
        try {
          const state = get();
          if (state.savedConfessionIds.includes(confessionId)) return;

          // Optimistically update the UI
          set({
            savedConfessionIds: [...state.savedConfessionIds, confessionId],
            error: null,
          });

          // Check if online, if not queue the action
          if (!offlineQueue.getNetworkStatus()) {
            await offlineQueue.enqueue(OFFLINE_ACTIONS.SAVE_CONFESSION, { confessionId });
            return;
          }

          // Try to save to backend
          const {
            data: { user },
          } = await supabase.auth.getUser();
          if (user) {
            const { error } = await supabase
              .from("user_saved_confessions" as any)
              .insert({ user_id: user.id, confession_id: confessionId });

            if (error) {
              // Only enqueue for retry if the error is retryable (not unique constraint violations, etc.)
              const isRetryable = !error.code || !["23505", "23503", "23502"].includes(error.code); // Common non-retryable Postgres error codes
              if (isRetryable) {
                await offlineQueue.enqueue(OFFLINE_ACTIONS.SAVE_CONFESSION, { confessionId });
              }
              throw error;
            }
          }
        } catch (error) {
          // Revert optimistic update on error only if not queued
          if (offlineQueue.getNetworkStatus()) {
            set((state) => ({
              savedConfessionIds: state.savedConfessionIds.filter((id) => id !== confessionId),
              error: error instanceof Error ? error.message : "Failed to save confession",
            }));
          }
        }
      },

      unsaveConfession: async (confessionId: string) => {
        try {
          const state = get();

          // Optimistically update the UI
          set({
            savedConfessionIds: state.savedConfessionIds.filter((id) => id !== confessionId),
            savedConfessions: state.savedConfessions.filter((confession) => confession.id !== confessionId),
            error: null,
          });

          // Check if online, if not queue the action
          if (!offlineQueue.getNetworkStatus()) {
            await offlineQueue.enqueue(OFFLINE_ACTIONS.UNSAVE_CONFESSION, { confessionId });
            return;
          }

          // Try to remove from backend
          const {
            data: { user },
          } = await supabase.auth.getUser();
          if (user) {
            const { error } = await supabase
              .from("user_saved_confessions" as any)
              .delete()
              .eq("user_id", user.id)
              .eq("confession_id", confessionId);

            if (error) {
              // If online operation fails, queue it for retry
              await offlineQueue.enqueue(OFFLINE_ACTIONS.UNSAVE_CONFESSION, { confessionId });
              throw error;
            }
          }
        } catch (error) {
          // Revert optimistic update on error only if not queued
          if (offlineQueue.getNetworkStatus()) {
            set((state) => ({
              savedConfessionIds: [...state.savedConfessionIds, confessionId],
              error: error instanceof Error ? error.message : "Failed to unsave confession",
            }));
          }
        }
      },

      batchSaveConfessions: async (confessionIds: string[]) => {
        if (!confessionIds.length) return;
        try {
          const state = get();
          const toSave = confessionIds.filter((id) => !state.savedConfessionIds.includes(id));
          if (toSave.length === 0) return;
          set({ savedConfessionIds: [...state.savedConfessionIds, ...toSave] });
          if (!offlineQueue.getNetworkStatus()) {
            await Promise.all(
              toSave.map((id) => offlineQueue.enqueue(OFFLINE_ACTIONS.SAVE_CONFESSION, { confessionId: id })),
            );
            return;
          }
          const {
            data: { user },
          } = await supabase.auth.getUser();
          if (user) {
            const rows = toSave.map((id) => ({ user_id: user.id, confession_id: id }));
            const { error } = await supabase.from("user_saved_confessions" as any).insert(rows as any);
            if (error) throw error;
          }
        } catch (err) {
          set((state) => ({
            savedConfessionIds: state.savedConfessionIds.filter((id) => !confessionIds.includes(id)),
            error: err instanceof Error ? err.message : "Failed to save confessions",
          }));
        }
      },

      batchUnsaveConfessions: async (confessionIds: string[]) => {
        if (!confessionIds.length) return;
        try {
          const state = get();
          set({
            savedConfessionIds: state.savedConfessionIds.filter((id) => !confessionIds.includes(id)),
            savedConfessions: state.savedConfessions.filter((c) => !confessionIds.includes(c.id)),
          });
          if (!offlineQueue.getNetworkStatus()) {
            await Promise.all(
              confessionIds.map((id) => offlineQueue.enqueue(OFFLINE_ACTIONS.UNSAVE_CONFESSION, { confessionId: id })),
            );
            return;
          }
          const {
            data: { user },
          } = await supabase.auth.getUser();
          if (user) {
            const { error } = await supabase
              .from("user_saved_confessions" as any)
              .delete()
              .eq("user_id", user.id)
              .in("confession_id", confessionIds);
            if (error) throw error;
          }
        } catch (err) {
          // revert
          set((state) => ({
            savedConfessionIds: [...new Set([...state.savedConfessionIds, ...confessionIds])],
            error: err instanceof Error ? err.message : "Failed to unsave confessions",
          }));
        }
      },

      isSaved: (confessionId: string) => {
        const state = get();
        return state.savedConfessionIds.includes(confessionId);
      },

      loadSavedConfessions: async (refresh = false) => {
        const state = get();

        // Skip if already loading or recently fetched (unless refreshing)
        if (state.isLoading || (!refresh && state.lastFetchTime && Date.now() - state.lastFetchTime < 30000)) {
          return;
        }

        set({ isLoading: true, error: null });

        try {
          if (state.savedConfessionIds.length === 0) {
            set({
              savedConfessions: [],
              isLoading: false,
              hasMore: false,
              lastFetchTime: Date.now(),
            });
            return;
          }

          // Get current user first
          const {
            data: { user },
          } = await supabase.auth.getUser();

          if (!user?.id) {
            throw new Error("User not authenticated");
          }

          // Fetch saved confessions from Supabase
          const { data: confessions, error } = await supabase
            .from("confessions")
            .select(
              `
              *,
              confession_likes!left(user_id)
            `,
            )
            .in("id", state.savedConfessionIds)
            .eq("confession_likes.user_id", user.id)
            .order("created_at", { ascending: false })
            .limit(ITEMS_PER_PAGE);

          if (error) throw error;

          // Process confessions using normalizer for consistent field mapping
          let processedConfessions: Confession[] = [];
          try {
            const normalizedConfessions = await normalizeConfessions(confessions || []);

            // Add like status from the join
            processedConfessions = normalizedConfessions.map((confession) => {
              const row = confessions?.find((r: any) => r.id === confession.id);
              return {
                ...confession,
                isLiked: (row?.confession_likes?.length ?? 0) > 0,
              };
            });
          } catch (normalizationError) {
            if (__DEV__) {
              console.error("Failed to normalize saved confessions:", normalizationError);
            }
            // Fallback to basic processing if normalization fails
            processedConfessions = (confessions || []).map((row: any) => ({
              id: row.id,
              type: row.type || "text",
              content: row.content || "",
              videoUri: row.video_uri ?? row.video_url ?? undefined,
              transcription: row.transcription ?? undefined,
              timestamp: row.created_at ? new Date(row.created_at).getTime() : Date.now(),
              isAnonymous: !!row.is_anonymous,
              likes: row.likes ?? 0,
              views: row.views ?? 0,
              isLiked: (row?.confession_likes?.length ?? 0) > 0,
            }));
          }

          set({
            savedConfessions: processedConfessions,
            isLoading: false,
            hasMore: processedConfessions.length === ITEMS_PER_PAGE,
            lastFetchTime: Date.now(),
          });
        } catch (error) {
          set({
            error: error instanceof Error ? error.message : "Failed to load saved confessions",
            isLoading: false,
          });
        }
      },

      loadMoreSavedConfessions: async () => {
        const state = get();

        if (state.isLoadingMore || !state.hasMore || state.savedConfessions.length === 0) {
          return;
        }

        set({ isLoadingMore: true, error: null });

        try {
          const remainingIds = state.savedConfessionIds.filter(
            (id) => !state.savedConfessions.some((confession) => confession.id === id),
          );

          if (remainingIds.length === 0) {
            set({ isLoadingMore: false, hasMore: false });
            return;
          }

          // Get current user first
          const {
            data: { user },
          } = await supabase.auth.getUser();

          if (!user?.id) {
            throw new Error("User not authenticated");
          }

          const { data: confessions, error } = await supabase
            .from("confessions")
            .select(
              `
              *,
              confession_likes!left(user_id)
            `,
            )
            .in("id", remainingIds.slice(0, ITEMS_PER_PAGE))
            .eq("confession_likes.user_id", user.id)
            .order("created_at", { ascending: false });

          if (error) throw error;

          // Process confessions using normalizer for consistent field mapping
          let processedConfessions: Confession[] = [];
          try {
            const normalizedConfessions = await normalizeConfessions(confessions || []);

            // Add like status from the join
            processedConfessions = normalizedConfessions.map((confession) => {
              const row = confessions?.find((r: any) => r.id === confession.id);
              return {
                ...confession,
                isLiked: (row?.confession_likes?.length ?? 0) > 0,
              };
            });
          } catch (normalizationError) {
            if (__DEV__) {
              console.error("Failed to normalize more saved confessions:", normalizationError);
            }
            // Fallback to basic processing if normalization fails
            processedConfessions = (confessions || []).map((row: any) => ({
              id: row.id,
              type: row.type || "text",
              content: row.content || "",
              videoUri: row.video_uri ?? row.video_url ?? undefined,
              transcription: row.transcription ?? undefined,
              timestamp: row.created_at ? new Date(row.created_at).getTime() : Date.now(),
              isAnonymous: !!row.is_anonymous,
              likes: row.likes ?? 0,
              views: row.views ?? 0,
              isLiked: (row?.confession_likes?.length ?? 0) > 0,
            }));
          }

          set({
            savedConfessions: [...state.savedConfessions, ...processedConfessions],
            isLoadingMore: false,
            hasMore: remainingIds.length > ITEMS_PER_PAGE,
          });
        } catch (error) {
          set({
            error: error instanceof Error ? error.message : "Failed to load more saved confessions",
            isLoadingMore: false,
          });
        }
      },

      clearAllSaved: async () => {
        try {
          set({
            savedConfessionIds: [],
            savedConfessions: [],
            hasMore: false,
            error: null,
          });

          // TODO: Clear from backend when user accounts are implemented
        } catch (error) {
          set({
            error: error instanceof Error ? error.message : "Failed to clear saved confessions",
          });
        }
      },

      clearError: () => {
        set({ error: null });
      },
    }),
    {
      name: "saved-storage",
      storage: createJSONStorage(() => AsyncStorage),
      partialize: (state) => ({
        savedConfessionIds: state.savedConfessionIds,
      }),
    },
  ),
);

import { create } from "zustand";
import { persist, createJSONStorage } from "zustand/middleware";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { supabase } from "../lib/supabase";
import type { RealtimeChannel } from "@supabase/supabase-js";
import { Confession } from "../types/confession";
import { HashtagData, TrendingSecret } from "../utils/trending";
import { trackStoreOperation } from "../utils/storePerformanceMonitor";
import { normalizeConfessions, normalizeConfession } from "../utils/confessionNormalizer";

// Memoized hashtag extraction with proper key generation
const hashtagRegex = /#[\w\u00c0-\u024f\u1e00-\u1eff]+/gi;
const hashtagMemo = new Map<string, string[]>();

// Simple hash function for generating consistent keys
const simpleHash = (str: string): string => {
  let hash = 0;
  for (let i = 0; i < str.length; i++) {
    const char = str.charCodeAt(i);
    hash = (hash << 5) - hash + char;
    hash = hash & hash; // Convert to 32-bit integer
  }
  return hash.toString();
};

const extractHashtags = (text: string): string[] => {
  if (!text) return [];

  // Use full text for short strings, hash for longer ones
  const key = text.length <= 200 ? text : simpleHash(text);

  const cached = hashtagMemo.get(key);
  if (cached) return cached;

  const matches = text.match(hashtagRegex);
  const lower = matches ? matches.map((t) => t.toLowerCase()) : [];

  hashtagMemo.set(key, lower);
  return lower;
};

async function rpcWithRetry<T>(fn: () => Promise<{ data: T | null; error: any }>, attempts = 2) {
  let lastErr: any;
  for (let i = 0; i < attempts; i++) {
    const { data, error } = await fn();
    if (!error) return { data, error };
    lastErr = error;
  }
  return { data: null as T | null, error: lastErr };
}

interface LoadTrendingOptions {
  force?: boolean;
  skipRealtimeInit?: boolean;
}

interface TrendingState {
  // Data
  trendingHashtags: HashtagData[];
  trendingSecrets: TrendingSecret[];
  searchResults: Confession[];

  // UI State
  isLoading: boolean;
  isRefreshing: boolean;
  error: string | null;
  lastUpdated: number | null;
  realtimeInitialized: boolean;
  lastHashtagParams: { hours: number; limit: number } | null;
  lastSecretParams: { hours: number; limit: number } | null;

  // Cache settings
  cacheExpiry: number; // 5 minutes in milliseconds

  // Actions
  loadTrendingHashtags: (hours?: number, limit?: number, options?: LoadTrendingOptions) => Promise<void>;
  loadTrendingSecrets: (hours?: number, limit?: number, options?: LoadTrendingOptions) => Promise<void>;
  searchByHashtag: (hashtag: string) => Promise<void>;
  refreshAll: (hours?: number) => Promise<void>;
  clearSearch: () => void;
  clearError: () => void;
}

let trendingRealtimeChannel: RealtimeChannel | null = null;
let trendingRefreshTimer: NodeJS.Timeout | null = null;

const initializeTrendingRealtime = (_set: (partial: Partial<TrendingState>) => void, get: () => TrendingState) => {
  if (trendingRealtimeChannel) return;

  trendingRealtimeChannel = supabase
    .channel("trending-confessions")
    .on("postgres_changes", { event: "*", schema: "public", table: "confessions" }, () => {
      if (trendingRefreshTimer) return;

      trendingRefreshTimer = setTimeout(async () => {
        trendingRefreshTimer = null;
        const state = get();
        const { lastHashtagParams, lastSecretParams } = state;

        try {
          if (lastHashtagParams) {
            await state.loadTrendingHashtags(lastHashtagParams.hours, lastHashtagParams.limit, {
              force: true,
              skipRealtimeInit: true,
            });
          }

          if (lastSecretParams) {
            await state.loadTrendingSecrets(lastSecretParams.hours, lastSecretParams.limit, {
              force: true,
              skipRealtimeInit: true,
            });
          }
        } catch (error) {
          if (__DEV__) {
            console.warn("Trending realtime refresh failed:", error);
          }
        }
      }, 1200);
    })
    .subscribe((status) => {
      if (__DEV__) {
        console.log("Trending realtime channel status:", status);
      }
    });
};

export const useTrendingStore = create<TrendingState>()(
  persist(
    (set, get) => ({
      // Initial state
      trendingHashtags: [],
      trendingSecrets: [],
      searchResults: [],
      isLoading: false,
      isRefreshing: false,
      error: null,
      lastUpdated: null,
      realtimeInitialized: false,
      lastHashtagParams: null,
      lastSecretParams: null,
      cacheExpiry: 5 * 60 * 1000, // 5 minutes

      loadTrendingHashtags: async (hours = 24, limit = 10, options: LoadTrendingOptions = {}) => {
        const state = get();
        const t0 = Date.now();

        if (!options.skipRealtimeInit && !state.realtimeInitialized) {
          initializeTrendingRealtime((partial) => set(partial), get);
          set({ realtimeInitialized: true });
        }

        if (
          !options.force &&
          state.lastHashtagParams &&
          state.lastHashtagParams.hours === hours &&
          state.lastHashtagParams.limit === limit &&
          state.lastUpdated &&
          Date.now() - state.lastUpdated < state.cacheExpiry &&
          state.trendingHashtags.length > 0
        ) {
          return;
        }

        set({
          error: null,
          ...(options.skipRealtimeInit ? {} : { isLoading: true }),
        });

        try {
          // Try to use database function first
          const { data: functionData, error: functionError } = await rpcWithRetry(
            async () =>
              await supabase.rpc("get_trending_hashtags", {
                hours_back: hours,
                limit_count: limit,
              }),
          );

          if (!functionError && functionData) {
            const hashtags: HashtagData[] = (functionData as any[]).map((item: any) => ({
              hashtag: item.hashtag,
              count: parseInt(item.count),
              percentage: parseFloat(item.percentage),
            }));

            set({
              trendingHashtags: hashtags,
              isLoading: false,
              lastUpdated: Date.now(),
              lastHashtagParams: { hours, limit },
            });
            return;
          }

          // Fallback to client-side calculation
          if (__DEV__) {
            console.log("Using client-side hashtag calculation");
          }
          const cutoffTime = new Date(Date.now() - hours * 60 * 60 * 1000).toISOString();

          const { data: confessions, error } = await supabase
            .from("confessions")
            .select("content, transcription")
            .gte("created_at", cutoffTime)
            .order("created_at", { ascending: false });

          if (error) throw error;

          // Client-side hashtag extraction and counting
          const hashtagCounts: Record<string, number> = {};

          confessions?.forEach((confession) => {
            const contentHashtags = extractHashtags(confession.content || "");
            const transcriptionHashtags = confession.transcription ? extractHashtags(confession.transcription) : [];
            [...contentHashtags, ...transcriptionHashtags].forEach((hashtag) => {
              hashtagCounts[hashtag] = (hashtagCounts[hashtag] || 0) + 1;
            });
          });

          const totalHashtags = Object.values(hashtagCounts).reduce((sum, count) => sum + count, 0);
          const hashtags: HashtagData[] = Object.entries(hashtagCounts)
            .map(([hashtag, count]) => ({
              hashtag,
              count,
              percentage: totalHashtags > 0 ? (count / totalHashtags) * 100 : 0,
            }))
            .sort((a, b) => b.count - a.count)
            .slice(0, limit);

          set({
            trendingHashtags: hashtags,
            isLoading: false,
            lastUpdated: Date.now(),
            lastHashtagParams: { hours, limit },
          });
          trackStoreOperation("trendingStore", "loadTrendingHashtags", Date.now() - t0);
        } catch (error) {
          set({
            error: error instanceof Error ? error.message : "Failed to load trending hashtags",
            isLoading: false,
          });
        }
      },

      loadTrendingSecrets: async (hours = 24, limit = 10, options: LoadTrendingOptions = {}) => {
        const state = get();
        const t0 = Date.now();

        if (!options.skipRealtimeInit && !state.realtimeInitialized) {
          initializeTrendingRealtime((partial) => set(partial), get);
          set({ realtimeInitialized: true });
        }

        if (
          !options.force &&
          state.lastSecretParams &&
          state.lastSecretParams.hours === hours &&
          state.lastSecretParams.limit === limit &&
          state.lastUpdated &&
          Date.now() - state.lastUpdated < state.cacheExpiry &&
          state.trendingSecrets.length > 0
        ) {
          return;
        }

        set({
          error: null,
          ...(options.skipRealtimeInit ? {} : { isLoading: true }),
        });

        try {
          // Try to use database function first
          const { data: functionData, error: functionError } = await rpcWithRetry(
            async () =>
              await supabase.rpc("get_trending_secrets", {
                hours_back: hours,
                limit_count: limit,
              }),
          );

          if (!functionError && functionData) {
            // Process confessions using normalizer for consistent field mapping
            let secrets: TrendingSecret[] = [];
            try {
              const normalizedConfessions = await normalizeConfessions(functionData as any[]);
              secrets = normalizedConfessions.map((confession, index) => ({
                confession: {
                  ...confession,
                  isLiked: false,
                },
                engagementScore: parseFloat((functionData as any[])[index].engagement_score),
              }));
            } catch (normalizationError) {
              if (__DEV__) {
                console.error("Failed to normalize trending secrets from RPC:", normalizationError);
              }
              // Fallback to basic processing
              secrets = (functionData as any[]).map((item: any) => ({
                confession: {
                  id: item.id,
                  type: item.type as "text" | "video",
                  content: item.content,
                  videoUri: item.video_uri || item.video_url || undefined,
                  transcription: item.transcription,
                  timestamp: new Date(item.created_at).getTime(),
                  isAnonymous: item.is_anonymous,
                  likes: item.likes,
                  views: item.views || 0,
                  isLiked: false,
                },
                engagementScore: parseFloat(item.engagement_score),
              }));
            }

            set({
              trendingSecrets: secrets,
              isLoading: false,
              lastUpdated: Date.now(),
              lastSecretParams: { hours, limit },
            });
            return;
          }

          // Fallback to client-side calculation
          if (__DEV__) {
            console.log("Using client-side trending calculation");
          }
          const cutoffTime = new Date(Date.now() - hours * 60 * 60 * 1000).toISOString();

          const { data: confessions, error } = await supabase
            .from("confessions")
            .select("*")
            .gte("created_at", cutoffTime)
            .order("likes", { ascending: false })
            .limit(limit * 2); // Get more to account for engagement scoring

          if (error) throw error;

          // Client-side engagement calculation
          const calculateEngagementScore = (likes: number, createdAt: string): number => {
            const hoursOld = (Date.now() - new Date(createdAt).getTime()) / (1000 * 60 * 60);
            const decayFactor = Math.exp(-hoursOld / 24); // Half-life of 24 hours
            return likes * decayFactor;
          };

          // Process confessions using normalizer for consistent field mapping
          let secrets: TrendingSecret[] = [];
          try {
            const normalizedConfessions = await normalizeConfessions(confessions || []);
            secrets = normalizedConfessions
              .map((confession, index) => {
                const originalRow = confessions?.[index];
                return {
                  confession: {
                    ...confession,
                    isLiked: false,
                  },
                  engagementScore: calculateEngagementScore(
                    originalRow?.likes || 0,
                    originalRow?.created_at || new Date().toISOString(),
                  ),
                };
              })
              .sort((a, b) => b.engagementScore - a.engagementScore)
              .slice(0, limit);
          } catch (normalizationError) {
            if (__DEV__) {
              console.error("Failed to normalize trending secrets fallback:", normalizationError);
            }
            // Fallback to basic processing
            secrets = (confessions || [])
              .map((confession) => ({
                confession: {
                  id: confession.id,
                  type: confession.type as "text" | "video",
                  content: confession.content,
                  videoUri: confession.video_uri || confession.video_url || undefined,
                  transcription: confession.transcription || undefined,
                  timestamp: new Date(confession.created_at).getTime(),
                  isAnonymous: confession.is_anonymous,
                  likes: confession.likes,
                  views: confession.views || 0,
                  isLiked: false,
                },
                engagementScore: calculateEngagementScore(confession.likes, confession.created_at),
              }))
              .sort((a, b) => b.engagementScore - a.engagementScore)
              .slice(0, limit);
          }

          set({
            trendingSecrets: secrets,
            isLoading: false,
            lastUpdated: Date.now(),
            lastSecretParams: { hours, limit },
          });
          trackStoreOperation("trendingStore", "loadTrendingSecrets", Date.now() - t0);
        } catch (error) {
          set({
            error: error instanceof Error ? error.message : "Failed to load trending secrets",
            isLoading: false,
          });
        }
      },

      searchByHashtag: async (hashtag: string) => {
        set({ isLoading: true, error: null });
        const t0 = Date.now();

        try {
          // Try to use database function first
          const { data: functionData, error: functionError } = await supabase.rpc("search_confessions_by_hashtag", {
            search_hashtag: hashtag,
          });

          if (!functionError && functionData) {
            // Process confessions using normalizer for consistent field mapping
            let results: Confession[] = [];
            try {
              results = await normalizeConfessions(functionData as any[]);
              results = results.map((confession) => ({ ...confession, isLiked: false }));
            } catch (normalizationError) {
              if (__DEV__) {
                console.error("Failed to normalize hashtag search results from RPC:", normalizationError);
              }
              // Fallback to basic processing
              results = (functionData as any[]).map((item: any) => ({
                id: item.id,
                type: item.type as "text" | "video",
                content: item.content,
                videoUri: item.video_uri || item.video_url || undefined,
                transcription: item.transcription,
                timestamp: new Date(item.created_at).getTime(),
                isAnonymous: item.is_anonymous,
                likes: item.likes,
                views: item.views || 0,
                isLiked: false,
              }));
            }

            set({
              searchResults: results,
              isLoading: false,
            });
            return;
          }

          // Fallback to client-side search
          if (__DEV__) {
            console.log("Using client-side hashtag search");
          }
          const normalizedHashtag = hashtag.toLowerCase().startsWith("#")
            ? hashtag.toLowerCase()
            : `#${hashtag.toLowerCase()}`;

          const { data: confessions, error } = await supabase
            .from("confessions")
            .select("*")
            .order("created_at", { ascending: false });

          if (error) throw error;

          // Process confessions using normalizer for consistent field mapping
          let results: Confession[] = [];
          try {
            const filteredConfessions = (confessions || []).filter((confession) => {
              const contentHashtags = extractHashtags(confession.content || "");
              const transcriptionHashtags = confession.transcription ? extractHashtags(confession.transcription) : [];
              const allHashtags = [...contentHashtags, ...transcriptionHashtags];
              return allHashtags.includes(normalizedHashtag);
            });

            results = await normalizeConfessions(filteredConfessions);
            results = results.map((confession) => ({ ...confession, isLiked: false }));
          } catch (normalizationError) {
            if (__DEV__) {
              console.error("Failed to normalize hashtag search results fallback:", normalizationError);
            }
            // Fallback to basic processing
            results = (confessions || [])
              .filter((confession) => {
                const contentHashtags = extractHashtags(confession.content || "");
                const transcriptionHashtags = confession.transcription ? extractHashtags(confession.transcription) : [];
                const allHashtags = [...contentHashtags, ...transcriptionHashtags];
                return allHashtags.includes(normalizedHashtag);
              })
              .map((confession) => ({
                id: confession.id,
                type: confession.type as "text" | "video",
                content: confession.content,
                videoUri: confession.video_uri || confession.video_url || undefined,
                transcription: confession.transcription || undefined,
                timestamp: new Date(confession.created_at).getTime(),
                isAnonymous: confession.is_anonymous,
                likes: confession.likes,
                views: confession.views || 0,
                isLiked: false,
              }));
          }

          set({
            searchResults: results,
            isLoading: false,
          });
          trackStoreOperation("trendingStore", "searchByHashtag", Date.now() - t0);
        } catch (error) {
          set({
            error: error instanceof Error ? error.message : "Failed to search hashtag",
            isLoading: false,
          });
        }
      },

      refreshAll: async (hours = 24) => {
        set({ isRefreshing: true });
        try {
          await Promise.all([
            get().loadTrendingHashtags(hours, undefined, { force: true }),
            get().loadTrendingSecrets(hours, undefined, { force: true }),
          ]);
        } finally {
          set({ isRefreshing: false });
        }
      },

      clearSearch: () => {
        set({ searchResults: [] });
      },

      clearError: () => {
        set({ error: null });
      },
    }),
    {
      name: "trending-storage",
      storage: createJSONStorage(() => AsyncStorage),
      // Only persist data, not loading states
      partialize: (state) => ({
        trendingHashtags: state.trendingHashtags,
        trendingSecrets: state.trendingSecrets,
        lastUpdated: state.lastUpdated,
        lastHashtagParams: state.lastHashtagParams,
        lastSecretParams: state.lastSecretParams,
      }),
    },
  ),
);

/**
 * End-to-end smoke test for video pipeline
 * Tests: record  process  upload  playback flow
 *
 * This is a development-only test that can be run manually to verify
 * the complete video processing pipeline works correctly.
 */

import { supabase } from "../../lib/supabase";
import { ensureSignedVideoUrl, uploadVideoToSupabase } from "../storage";

interface SmokeTestResult {
  success: boolean;
  step: string;
  error?: string;
  details?: any;
}

/**
 * Test signed URL generation for video paths
 */
export async function testSignedUrlGeneration(): Promise<SmokeTestResult> {
  try {
    // Test with a mock storage path
    const mockPath = "confessions/test-user/test-video.mp4";
    const signedUrl = await ensureSignedVideoUrl(mockPath);

    if (!signedUrl) {
      return {
        success: false,
        step: "signed-url-generation",
        error: "Failed to generate signed URL",
      };
    }

    // Verify URL format
    const isValidUrl = signedUrl.signedUrl.startsWith("https://") && signedUrl.signedUrl.includes("supabase");
    if (!isValidUrl) {
      return {
        success: false,
        step: "signed-url-generation",
        error: "Generated URL format is invalid",
        details: { signedUrl: signedUrl.signedUrl },
      };
    }

    return {
      success: true,
      step: "signed-url-generation",
      details: { signedUrl: signedUrl.signedUrl.substring(0, 50) + "..." },
    };
  } catch (error) {
    return {
      success: false,
      step: "signed-url-generation",
      error: error instanceof Error ? error.message : String(error),
    };
  }
}

/**
 * Test Edge Function connectivity and response format
 */
export async function testEdgeFunctionConnectivity(): Promise<SmokeTestResult> {
  try {
    const { data, error } = await supabase.functions.invoke("process-video", {
      body: {
        videoPath: "confessions/test-user/test-video.mp4",
        options: {
          enableFaceBlur: false,
          enableVoiceChange: false,
          enableTranscription: true,
          quality: "medium",
        },
      },
    });

    if (error) {
      return {
        success: false,
        step: "edge-function-connectivity",
        error: error.message,
      };
    }

    // Verify response format
    if (!data || typeof data !== "object") {
      return {
        success: false,
        step: "edge-function-connectivity",
        error: "Invalid response format from Edge Function",
        details: { data },
      };
    }

    // Check for expected fields
    const hasRequiredFields = "success" in data && "storagePath" in data;
    if (!hasRequiredFields) {
      return {
        success: false,
        step: "edge-function-connectivity",
        error: "Response missing required fields (success, storagePath)",
        details: { data },
      };
    }

    return {
      success: true,
      step: "edge-function-connectivity",
      details: {
        success: data.success,
        hasStoragePath: !!data.storagePath,
        message: data.message,
      },
    };
  } catch (error) {
    return {
      success: false,
      step: "edge-function-connectivity",
      error: error instanceof Error ? error.message : String(error),
    };
  }
}

/**
 * Test database connectivity and confession insertion
 */
export async function testDatabaseConnectivity(): Promise<SmokeTestResult> {
  try {
    // Get current user
    const {
      data: { user },
      error: authError,
    } = await supabase.auth.getUser();
    if (authError || !user) {
      return {
        success: false,
        step: "database-connectivity",
        error: "User not authenticated",
      };
    }

    // Test read access
    const { data: confessions, error: readError } = await supabase
      .from("confessions")
      .select("id, created_at")
      .limit(1);

    if (readError) {
      return {
        success: false,
        step: "database-connectivity",
        error: `Read access failed: ${readError.message}`,
      };
    }

    return {
      success: true,
      step: "database-connectivity",
      details: {
        userId: user.id,
        canRead: true,
        sampleCount: confessions?.length || 0,
      },
    };
  } catch (error) {
    return {
      success: false,
      step: "database-connectivity",
      error: error instanceof Error ? error.message : String(error),
    };
  }
}

/**
 * Run complete video pipeline smoke test
 */
export async function runVideoSmokeTest(): Promise<{
  success: boolean;
  results: SmokeTestResult[];
  summary: string;
}> {
  if (!__DEV__) {
    return {
      success: false,
      results: [],
      summary: "Smoke tests only run in development mode",
    };
  }

  const results: SmokeTestResult[] = [];

  // Test 1: Database connectivity
  const dbTest = await testDatabaseConnectivity();
  results.push(dbTest);

  // Test 2: Signed URL generation
  const urlTest = await testSignedUrlGeneration();
  results.push(urlTest);

  // Test 3: Edge Function connectivity
  const edgeTest = await testEdgeFunctionConnectivity();
  results.push(edgeTest);

  const successCount = results.filter((r) => r.success).length;
  const totalCount = results.length;
  const allPassed = successCount === totalCount;

  const summary = `Video pipeline smoke test: ${successCount}/${totalCount} tests passed`;

  return {
    success: allPassed,
    results,
    summary,
  };
}

/**
 * Log smoke test results to console (development only)
 */
export function logSmokeTestResults(testResults: Awaited<ReturnType<typeof runVideoSmokeTest>>): void {
  if (!__DEV__) return;

  console.log("\n Video Pipeline Smoke Test Results");
  console.log("=====================================");

  testResults.results.forEach((result, index) => {
    const icon = result.success ? "" : "";
    console.log(`${icon} ${index + 1}. ${result.step}`);

    if (result.error) {
      console.log(`   Error: ${result.error}`);
    }

    if (result.details) {
      console.log(`   Details:`, result.details);
    }
  });

  console.log("\n" + testResults.summary);

  if (testResults.success) {
    console.log(" All tests passed! Video pipeline is working correctly.");
  } else {
    console.log("  Some tests failed. Check the details above.");
  }

  console.log("=====================================\n");
}

