// Accessibility helpers for common patterns
export const AccessibilityHelpers = {
  formatCount: (count: number, singular: string, plural?: string): string => {
    const pluralForm = plural || `${singular}s`;
    return `${count} ${count === 1 ? singular : pluralForm}`;
  },

  formatTime: (timestamp: number): string => {
    const now = Date.now();
    const diff = now - timestamp;
    const minutes = Math.floor(diff / 60000);
    const hours = Math.floor(minutes / 60);
    const days = Math.floor(hours / 24);

    if (days > 0) return `${days} ${days === 1 ? "day" : "days"} ago`;
    if (hours > 0) return `${hours} ${hours === 1 ? "hour" : "hours"} ago`;
    if (minutes > 0) return `${minutes} ${minutes === 1 ? "minute" : "minutes"} ago`;
    return "Just now";
  },

  getLoadingMessage: (context: string): string => {
    return `Loading ${context}, please wait`;
  },

  getErrorMessage: (error: string, context?: string): string => {
    return context ? `Error in ${context}: ${error}` : `Error: ${error}`;
  },

  getSuccessMessage: (action: string): string => {
    return `${action} completed successfully`;
  },
};

/**
 * Standardized error handling utilities for Zustand stores
 * Provides consistent error processing, logging, and state management
 */

import {
  isSupabaseError,
  getSupabaseErrorCode,
  getSupabaseErrorMessage,
  isRetryableSupabaseError,
  logSupabaseError,
} from "../types/supabaseError";

export interface StandardError {
  code: string;
  message: string;
  timestamp?: number;
  context?: string;
  isRetryable?: boolean;
  originalError?: unknown;
  details?: Record<string, any>;
  statusCode?: number;
}

export interface ErrorHandlingOptions {
  /** Whether to throw the error after setting state (default: false) */
  shouldThrow?: boolean;
  /** Additional context for debugging */
  context?: string;
  /** Whether to log the error in development (default: true) */
  shouldLog?: boolean;
  /** Custom error message override */
  customMessage?: string;
}

/**
 * Processes an error into a standardized format
 */
export const processError = (error: unknown, context?: string, customMessage?: string): StandardError => {
  let code = "UNKNOWN_ERROR";
  let message = customMessage || "An unknown error occurred";
  let isRetryable = false;

  // Handle Supabase errors first
  if (isSupabaseError(error)) {
    code = getSupabaseErrorCode(error) || "SUPABASE_ERROR";
    const supabaseMessage = translateSupabaseError(code);
    message = customMessage || supabaseMessage || getSupabaseErrorMessage(error);
    isRetryable = isRetryableSupabaseError(error);

    // Log Supabase error with context
    if (context) {
      logSupabaseError(error, context);
    }
  } else if (error instanceof Error) {
    message = customMessage || error.message;

    // Handle auth errors
    if (error.name === "AuthError") {
      // Preserve the specific auth error code
      code = (error as any).code || "AUTH_ERROR";
    }

    // Handle network errors
    if (
      error.name === "NetworkError" ||
      error.message.toLowerCase().includes("network") ||
      error.message.toLowerCase().includes("fetch") ||
      error.message.toLowerCase().includes("connection") ||
      error.message.toLowerCase().includes("unreachable") ||
      (error as any).code === "NETWORK_ERROR"
    ) {
      code = "NETWORK_ERROR";
      message = customMessage || "Network connection failed. Please check your internet connection.";
      isRetryable = true;
    }

    // Handle timeout errors
    if (error.message.includes("timeout")) {
      code = "TIMEOUT_ERROR";
      message = customMessage || "Request timed out. Please try again.";
      isRetryable = true;
    }
  }

  return {
    code,
    message,
    timestamp: Date.now(),
    context,
    isRetryable,
    originalError: error,
  };
};

/**
 * Standardized error handler for Zustand stores
 * Sets error state and optionally throws based on configuration
 */
export const handleStoreError = <T extends { error: StandardError | null; isLoading?: boolean }>(
  setState: (partial: Partial<T>) => void,
  error: unknown,
  options: ErrorHandlingOptions = {},
): never | void => {
  const { shouldThrow = false, context, shouldLog = true, customMessage } = options;

  const processedError = processError(error, context, customMessage);

  // Log error in development
  if (__DEV__ && shouldLog) {
    console.error(`[Store Error] ${context || "Unknown context"}:`, {
      code: processedError.code,
      message: processedError.message,
      originalError: error,
    });
  }

  // Set error state
  setState({
    error: processedError,
    isLoading: false,
  } as Partial<T>);

  // Throw if requested
  if (shouldThrow) {
    throw error;
  }
};

/**
 * Clears error state in a standardized way
 */
export const clearStoreError = <T extends { error: StandardError | null }>(
  setState: (partial: Partial<T>) => void,
): void => {
  setState({ error: null } as Partial<T>);
};

/**
 * Wraps async store operations with standardized error handling
 */
export const withErrorHandling = async <T extends { error: StandardError | null; isLoading?: boolean }>(
  setState: (partial: Partial<T>) => void,
  operation: () => Promise<void>,
  options: ErrorHandlingOptions = {},
): Promise<void> => {
  try {
    // Clear previous errors and set loading
    setState({ error: null, isLoading: true } as Partial<T>);

    await operation();

    // Clear loading state on success
    setState({ isLoading: false } as Partial<T>);
  } catch (error) {
    handleStoreError(setState, error, options);
  }
};

/**
 * Enhanced Supabase error code translations
 */
export function translateSupabaseError(code?: string): string | undefined {
  switch (code) {
    case "invalid_login_credentials":
      return "Incorrect email or password. Please try again.";
    case "user_not_found":
      return "No account found with this email address.";
    case "user_already_exists":
      return "An account with this email already exists.";
    case "invalid_email":
      return "Please enter a valid email address.";
    case "weak_password":
      return "Password must be at least 6 characters long.";
    case "network_error":
      return "Network error. Please check your connection and try again.";
    case "email_not_confirmed":
      return "Please verify your email address before signing in.";
    case "too_many_requests":
      return "Too many attempts. Please wait a moment and try again.";
    case "signup_disabled":
      return "New registrations are temporarily disabled.";
    case "invalid_credentials":
      return "Invalid email or password. Please check your credentials.";
    case "email_address_invalid":
      return "Please enter a valid email address.";
    case "password_too_short":
      return "Password must be at least 6 characters long.";
    case "email_address_not_authorized":
      return "This email address is not authorized to sign up.";
    case "captcha_failed":
      return "Security verification failed. Please try again.";
    case "over_email_send_rate_limit":
      return "Too many emails sent. Please wait before requesting another.";
    case "invalid_request":
      return "Invalid request. Please check your information and try again.";
    case "session_not_found":
      return "Your session has expired. Please sign in again.";
    case "refresh_token_not_found":
      return "Session expired. Please sign in again.";
    case "invalid_refresh_token":
      return "Session expired. Please sign in again.";
    default:
      return undefined;
  }
}

/**
 * Get screen-specific error message with fallback to generic messages
 */
export const getScreenSpecificMessage = (error: StandardError, screenName: string): string => {
  // Import screen error messages
  const { getScreenErrorMessage } = require("./screenErrorMessages");

  const screenError = getScreenErrorMessage(error, screenName);
  if (screenError.message !== error.message) {
    // Include suggestion if available
    return screenError.suggestion ? `${screenError.message} ${screenError.suggestion}` : screenError.message;
  }

  // Fall back to generic user-friendly message
  return getUserFriendlyMessage(error);
};

/**
 * User-friendly error messages for common error codes
 */
export const getUserFriendlyMessage = (error: StandardError, screenContext?: string): string => {
  // If screen context provided, try screen-specific messages first
  if (screenContext) {
    return getScreenSpecificMessage(error, screenContext);
  }

  // First try to get Supabase-specific error message
  const supabaseMessage = translateSupabaseError(error.code);
  if (supabaseMessage) {
    return supabaseMessage;
  }

  // Fall back to generic error codes
  switch (error.code) {
    case "NETWORK_ERROR":
      return "Please check your internet connection and try again.";
    case "TIMEOUT_ERROR":
      return "The request took too long. Please try again.";
    case "AUTH_ERROR":
      return "Authentication failed. Please sign in again.";
    case "PERMISSION_DENIED":
      return "You don't have permission to perform this action.";
    case "NOT_FOUND":
      return "The requested item could not be found.";
    case "VALIDATION_ERROR":
      return "Please check your input and try again.";
    case "RATE_LIMIT_EXCEEDED":
      return "Too many requests. Please wait a moment and try again.";
    case "INVALID_CREDENTIALS":
      return "Invalid email or password. Please check your credentials and try again.";
    case "EMAIL_NOT_CONFIRMED":
      return "Please check your email and click the confirmation link before signing in.";
    case "TOO_MANY_REQUESTS":
      return "Too many sign-in attempts. Please wait a moment and try again.";
    case "USER_EXISTS":
      return "An account with this email already exists. Please sign in instead.";
    case "WEAK_PASSWORD":
      return "Password must be at least 6 characters long.";
    case "INVALID_EMAIL":
      return "Please enter a valid email address.";
    case "PASSWORD_MISMATCH":
      return "Passwords do not match.";
    case "MISSING_PASSWORD":
      return "Please enter your password.";
    case "SIGNIN_ERROR":
      return "Sign in failed. Please try again.";
    default:
      return error.message;
  }
};

/**
 * App health monitoring system
 * Tracks performance, errors, and user experience metrics
 */

import * as React from "react";
import { AppState, AppStateStatus } from "react-native";
import * as Device from "expo-device";
import * as Application from "expo-application";
import NetInfo from "@react-native-community/netinfo";

export interface HealthMetrics {
  performance: {
    appStartTime: number;
    screenLoadTimes: Record<string, number>;
    apiResponseTimes: Record<string, number[]>;
    memoryUsage: number[];
    crashCount: number;
  };
  user: {
    sessionDuration: number;
    screenViews: Record<string, number>;
    interactions: Record<string, number>;
    errors: {
      timestamp: number;
      error: string;
      screen: string;
      userId?: string;
    }[];
  };
  system: {
    deviceInfo: any;
    appVersion: string;
    networkStatus: string;
    batteryLevel?: number;
    storageUsage: number;
  };
}

class HealthMonitor {
  private metrics: HealthMetrics;
  private sessionStartedAt: number;
  private lastScreenChangeAt: number | null = null;
  private currentScreen: string = "Unknown";
  private isMonitoring: boolean = false;
  private reportingInterval: NodeJS.Timeout | null = null;
  private appStateListener: ReturnType<typeof AppState.addEventListener> | null = null;
  private netInfoUnsubscribe: (() => void) | null = null;

  constructor() {
    this.sessionStartedAt = Date.now();
    this.metrics = this.initializeMetrics();
  }

  private initializeMetrics(): HealthMetrics {
    return {
      performance: {
        appStartTime: Date.now(),
        screenLoadTimes: {},
        apiResponseTimes: {},
        memoryUsage: [],
        crashCount: 0,
      },
      user: {
        sessionDuration: 0,
        screenViews: {},
        interactions: {},
        errors: [],
      },
      system: {
        deviceInfo: {},
        appVersion: Application.nativeApplicationVersion || "unknown",
        networkStatus: "unknown",
        storageUsage: 0,
      },
    };
  }

  /**
   * Start monitoring app health
   */
  async startMonitoring() {
    if (this.isMonitoring) return;

    this.isMonitoring = true;

    // Collect initial system info
    await this.collectSystemInfo();

    // Set up periodic monitoring
    this.setupPeriodicMonitoring();

    // Set up app state monitoring
    this.setupAppStateMonitoring();

    // Set up network monitoring
    this.setupNetworkMonitoring();

    if (__DEV__) {
      console.log(" Health monitoring started");
    }
  }

  /**
   * Stop monitoring
   */
  stopMonitoring() {
    this.isMonitoring = false;

    if (this.reportingInterval) {
      clearInterval(this.reportingInterval);
      this.reportingInterval = null;
    }

    if (this.appStateListener) {
      this.appStateListener.remove();
      this.appStateListener = null;
    }

    if (this.netInfoUnsubscribe) {
      this.netInfoUnsubscribe();
      this.netInfoUnsubscribe = null;
    }

    if (__DEV__) {
      console.log(" Health monitoring stopped");
    }
  }

  /**
   * Track screen navigation
   */
  trackScreenView(screenName: string) {
    const now = Date.now();

    // Record screen load time if we have a previous screen change
    if (this.lastScreenChangeAt !== null) {
      const loadTime = now - this.lastScreenChangeAt;
      this.metrics.performance.screenLoadTimes[screenName] = loadTime;
    }

    // Update last screen change time for next screen
    this.lastScreenChangeAt = now;

    // Update screen view count
    this.metrics.user.screenViews[screenName] = (this.metrics.user.screenViews[screenName] || 0) + 1;

    this.currentScreen = screenName;

    if (__DEV__) {
      console.log(` Screen view: ${screenName}`);
    }
  }

  /**
   * Track user interactions
   */
  trackInteraction(action: string, details?: any) {
    this.metrics.user.interactions[action] = (this.metrics.user.interactions[action] || 0) + 1;

    if (__DEV__ && details) {
      console.log(` Interaction: ${action}`, details);
    }
  }

  /**
   * Track API performance
   */
  trackApiCall(endpoint: string, responseTime: number, success: boolean) {
    if (!this.metrics.performance.apiResponseTimes[endpoint]) {
      this.metrics.performance.apiResponseTimes[endpoint] = [];
    }

    this.metrics.performance.apiResponseTimes[endpoint].push(responseTime);

    // Keep only last 100 response times per endpoint
    if (this.metrics.performance.apiResponseTimes[endpoint].length > 100) {
      this.metrics.performance.apiResponseTimes[endpoint].shift();
    }

    if (__DEV__) {
      console.log(` API ${success ? "" : ""}: ${endpoint} (${responseTime}ms)`);
    }
  }

  /**
   * Track errors
   */
  trackError(error: Error, screen?: string, userId?: string) {
    this.metrics.user.errors.push({
      timestamp: Date.now(),
      error: error.message,
      screen: screen || this.currentScreen,
      userId,
    });

    // Keep only last 50 errors
    if (this.metrics.user.errors.length > 50) {
      this.metrics.user.errors.shift();
    }

    if (__DEV__) {
      console.error(" Error tracked:", error.message, "on", screen || this.currentScreen);
    }
  }

  /**
   * Track app crashes
   */
  trackCrash() {
    this.metrics.performance.crashCount++;

    if (__DEV__) {
      console.error(" Crash tracked");
    }
  }

  /**
   * Get current health metrics
   */
  getMetrics(): HealthMetrics {
    // Update session duration
    this.metrics.user.sessionDuration = Date.now() - this.sessionStartedAt;

    return { ...this.metrics };
  }

  /**
   * Get health summary
   */
  getHealthSummary() {
    const metrics = this.getMetrics();

    // Calculate averages
    const avgApiResponseTimes: Record<string, number> = {};
    for (const [endpoint, times] of Object.entries(metrics.performance.apiResponseTimes)) {
      avgApiResponseTimes[endpoint] = times.reduce((a, b) => a + b, 0) / times.length;
    }

    const avgMemoryUsage =
      metrics.performance.memoryUsage.length > 0
        ? metrics.performance.memoryUsage.reduce((a, b) => a + b, 0) / metrics.performance.memoryUsage.length
        : 0;

    return {
      sessionDuration: metrics.user.sessionDuration,
      totalScreenViews: Object.values(metrics.user.screenViews).reduce((a, b) => a + b, 0),
      totalInteractions: Object.values(metrics.user.interactions).reduce((a, b) => a + b, 0),
      totalErrors: metrics.user.errors.length,
      crashCount: metrics.performance.crashCount,
      avgApiResponseTime:
        Object.values(avgApiResponseTimes).reduce((a, b) => a + b, 0) / Object.keys(avgApiResponseTimes).length || 0,
      avgMemoryUsage,
      networkStatus: metrics.system.networkStatus,
      appVersion: metrics.system.appVersion,
    };
  }

  /**
   * Export metrics for reporting
   */
  exportMetrics() {
    const metrics = this.getMetrics();
    const summary = this.getHealthSummary();

    return {
      timestamp: new Date().toISOString(),
      summary,
      detailed: metrics,
    };
  }

  private async collectSystemInfo() {
    try {
      // Device info
      this.metrics.system.deviceInfo = {
        deviceName: Device.deviceName,
        deviceType: Device.deviceType,
        osName: Device.osName,
        osVersion: Device.osVersion,
        modelName: Device.modelName,
        totalMemory: Device.totalMemory,
      };

      // Network status
      const netInfo = await NetInfo.fetch();
      this.metrics.system.networkStatus = `${netInfo.type}-${netInfo.isConnected ? "connected" : "disconnected"}`;
    } catch (error) {
      console.error("Failed to collect system info:", error);
    }
  }

  private setupPeriodicMonitoring() {
    this.reportingInterval = setInterval(() => {
      this.collectMemoryUsage();

      // Log health summary in development
      if (__DEV__) {
        const summary = this.getHealthSummary();
        console.log(" Health Summary:", summary);
      }
    }, 30000); // Every 30 seconds
  }

  private onAppStateChange = (nextAppState: AppStateStatus) => {
    this.trackInteraction("app_state_change", { state: nextAppState });

    if (nextAppState === "background") {
      // App went to background - good time to report metrics
      void this.reportMetrics();
    }
  };

  private setupAppStateMonitoring() {
    this.appStateListener = AppState.addEventListener("change", this.onAppStateChange);
  }

  private setupNetworkMonitoring() {
    this.netInfoUnsubscribe = NetInfo.addEventListener((state) => {
      const status = `${state.type}-${state.isConnected ? "connected" : "disconnected"}`;
      this.metrics.system.networkStatus = status;
      this.trackInteraction("network_change", { status });
    });
  }

  private collectMemoryUsage() {
    try {
      if (global.performance && (global.performance as any).memory) {
        const memory = (global.performance as any).memory;
        const usedMB = memory.usedJSHeapSize / 1024 / 1024;

        this.metrics.performance.memoryUsage.push(usedMB);

        // Keep only last 100 memory readings
        if (this.metrics.performance.memoryUsage.length > 100) {
          this.metrics.performance.memoryUsage.shift();
        }
      }
    } catch (error) {
      // Memory API not available, skip silently
    }
  }

  private async reportMetrics() {
    if (!this.isMonitoring) return;

    try {
      const report = this.exportMetrics();

      // In a real app, you would send this to your analytics service
      if (__DEV__) {
        console.log(" Health Report:", report);
      }

      // TODO: Send to analytics service
      // await analyticsService.reportHealth(report);
    } catch (error) {
      console.error("Failed to report metrics:", error);
    }
  }
}

// Singleton instance
export const healthMonitor = new HealthMonitor();

/**
 * React hook for health monitoring
 */
export function useHealthMonitor() {
  React.useEffect(() => {
    healthMonitor.startMonitoring();

    return () => {
      healthMonitor.stopMonitoring();
    };
  }, []);

  return {
    trackScreenView: healthMonitor.trackScreenView.bind(healthMonitor),
    trackInteraction: healthMonitor.trackInteraction.bind(healthMonitor),
    trackApiCall: healthMonitor.trackApiCall.bind(healthMonitor),
    trackError: healthMonitor.trackError.bind(healthMonitor),
    getMetrics: healthMonitor.getMetrics.bind(healthMonitor),
    getHealthSummary: healthMonitor.getHealthSummary.bind(healthMonitor),
  };
}

import { Platform } from "react-native";

/**
 * Hermes compatibility test utilities
 * These functions help verify that Hermes-specific fixes are working correctly
 */

export interface HermesTestResult {
  constructorTest: boolean;
  errorBoundaryTest: boolean;
  videoDisposalTest: boolean;
  timestamp: string;
}

/**
 * Test constructor handling in Hermes
 */
export function testConstructorHandling(): boolean {
  try {
    // Test class constructor that previously failed in Hermes
    class TestClass {
      private testProp: string;

      constructor(props: { test: string }) {
        // This pattern was causing issues in Hermes
        this.testProp = props.test;
      }

      getTestProp(): string {
        return this.testProp;
      }
    }

    const instance = new TestClass({ test: "hermes-test" });
    return instance.getTestProp() === "hermes-test";
  } catch (error) {
    if (__DEV__) {
      console.warn("Constructor test failed:", error);
    }
    return false;
  }
}

/**
 * Test error boundary functionality
 */
export function testErrorBoundaryHandling(): boolean {
  try {
    // Simulate error boundary state management
    const errorBoundaryState = {
      hasError: false,
      error: null,
      errorInfo: null,
      errorId: "",
    };

    // Test state updates that were problematic
    const updatedState = {
      ...errorBoundaryState,
      hasError: true,
      error: new Error("Test error"),
      errorId: `error_${Date.now()}`,
    };

    return updatedState.hasError && updatedState.error !== null;
  } catch (error) {
    if (__DEV__) {
      console.warn("Error boundary test failed:", error);
    }
    return false;
  }
}

/**
 * Test video disposal patterns
 */
export function testVideoDisposalHandling(): boolean {
  try {
    // Mock video player object
    const mockPlayer = {
      playing: false,
      pause: () => Promise.resolve(),
      unload: () => Promise.resolve(),
    };

    // Test disposal pattern that was causing warnings
    const disposePlayer = async () => {
      try {
        if (mockPlayer.playing !== undefined && typeof mockPlayer.pause === "function") {
          await mockPlayer.pause();

          if (typeof mockPlayer.unload === "function") {
            await mockPlayer.unload();
          }
        }
        return true;
      } catch (error) {
        // Should handle errors gracefully
        return false;
      }
    };

    // Execute disposal test
    disposePlayer();
    return true;
  } catch (error) {
    if (__DEV__) {
      console.warn("Video disposal test failed:", error);
    }
    return false;
  }
}

/**
 * Run all Hermes compatibility tests
 */
export function runHermesCompatibilityTests(): HermesTestResult {
  const result: HermesTestResult = {
    constructorTest: testConstructorHandling(),
    errorBoundaryTest: testErrorBoundaryHandling(),
    videoDisposalTest: testVideoDisposalHandling(),
    timestamp: new Date().toISOString(),
  };

  if (__DEV__) {
    console.group(" Hermes Compatibility Test Results");
    console.log("Constructor Test:", result.constructorTest ? " PASS" : " FAIL");
    console.log("Error Boundary Test:", result.errorBoundaryTest ? " PASS" : " FAIL");
    console.log("Video Disposal Test:", result.videoDisposalTest ? " PASS" : " FAIL");
    console.log("Timestamp:", result.timestamp);
    console.groupEnd();
  }

  return result;
}

