/**
 * Keyboard utilities for improved keyboard handling across the app
 * Provides consistent keyboard behavior and proper cleanup
 */

import { Keyboard, Platform, Dimensions } from "react-native";
import { useSafeAreaInsets } from "react-native-safe-area-context";
import { useEffect, useState } from "react";

export interface KeyboardInfo {
  isVisible: boolean;
  height: number;
  duration: number;
  easing: string;
}

/**
 * Hook to track keyboard visibility and height
 */
export const useKeyboard = () => {
  const [keyboardInfo, setKeyboardInfo] = useState<KeyboardInfo>({
    isVisible: false,
    height: 0,
    duration: 0,
    easing: "",
  });

  useEffect(() => {
    const showEvent = Platform.OS === "ios" ? "keyboardWillShow" : "keyboardDidShow";
    const hideEvent = Platform.OS === "ios" ? "keyboardWillHide" : "keyboardDidHide";

    const onKeyboardShow = (event: any) => {
      setKeyboardInfo({
        isVisible: true,
        height: event.endCoordinates.height,
        duration: event.duration || 250,
        easing: event.easing || "keyboard",
      });
    };

    const onKeyboardHide = (event: any) => {
      setKeyboardInfo({
        isVisible: false,
        height: 0,
        duration: event.duration || 250,
        easing: event.easing || "keyboard",
      });
    };

    const showSubscription = Keyboard.addListener(showEvent, onKeyboardShow);
    const hideSubscription = Keyboard.addListener(hideEvent, onKeyboardHide);

    return () => {
      showSubscription?.remove();
      hideSubscription?.remove();
    };
  }, []);

  return keyboardInfo;
};

/**
 * Enhanced keyboard dismiss function with haptic feedback
 */
export const dismissKeyboard = (withHaptics = false) => {
  Keyboard.dismiss();

  if (withHaptics && Platform.OS === "ios") {
    // Add subtle haptic feedback on keyboard dismiss
    import("expo-haptics")
      .then(({ impactAsync, ImpactFeedbackStyle }) => {
        impactAsync(ImpactFeedbackStyle.Light);
      })
      .catch(() => {
        // Haptics not available - silently fail
      });
  }
};

/**
 * Get appropriate KeyboardAvoidingView behavior for platform
 */
export const getKeyboardBehavior = () => {
  return Platform.OS === "ios" ? "padding" : "height";
};

/**
 * Get keyboard vertical offset for different screen types
 */
export const getKeyboardVerticalOffset = (screenType: "modal" | "screen" | "bottomSheet" = "screen") => {
  if (Platform.OS === "android") return 0;

  switch (screenType) {
    case "modal":
      return 0;
    case "bottomSheet":
      return 0;
    case "screen":
    default:
      return 0; // Let SafeAreaView handle the offset
  }
};

/**
 * Hook for keyboard-aware bottom padding
 */
export const useKeyboardPadding = (additionalPadding = 0) => {
  const keyboard = useKeyboard();
  const insets = useSafeAreaInsets();

  return keyboard.isVisible ? keyboard.height + additionalPadding : insets.bottom + additionalPadding;
};

/**
 * Calculate safe keyboard height considering safe area
 */
export const useSafeKeyboardHeight = () => {
  const keyboard = useKeyboard();
  const _insets = useSafeAreaInsets();

  if (!keyboard.isVisible) return 0;

  // On iOS, keyboard height already accounts for safe area
  if (Platform.OS === "ios") {
    return keyboard.height;
  }

  // On Android, we might need to adjust
  return keyboard.height;
};

/**
 * Keyboard configuration for different input types
 */
export const KEYBOARD_CONFIGS = {
  text: {
    keyboardType: "default" as const,
    autoCapitalize: "sentences" as const,
    autoCorrect: true,
    spellCheck: true,
  },
  email: {
    keyboardType: "email-address" as const,
    autoCapitalize: "none" as const,
    autoCorrect: false,
    spellCheck: false,
  },
  password: {
    keyboardType: "default" as const,
    autoCapitalize: "none" as const,
    autoCorrect: false,
    spellCheck: false,
    secureTextEntry: true,
  },
  numeric: {
    keyboardType: "numeric" as const,
    autoCapitalize: "none" as const,
    autoCorrect: false,
    spellCheck: false,
  },
  search: {
    keyboardType: "default" as const,
    autoCapitalize: "none" as const,
    autoCorrect: false,
    spellCheck: false,
    returnKeyType: "search" as const,
  },
  comment: {
    keyboardType: "default" as const,
    autoCapitalize: "sentences" as const,
    autoCorrect: true,
    spellCheck: true,
    returnKeyType: "send" as const,
  },
} as const;

/**
 * Enhanced TextInput props with keyboard optimizations
 */
export const getOptimizedTextInputProps = (type: keyof typeof KEYBOARD_CONFIGS) => {
  const config = KEYBOARD_CONFIGS[type];

  return {
    ...config,
    blurOnSubmit: type === "search" || type === "comment",
    enablesReturnKeyAutomatically: true,
    keyboardAppearance: "dark" as const,
    selectionColor: "#1D9BF0",
    underlineColorAndroid: "transparent",
  };
};

/**
 * Keyboard-aware scroll view props
 */
export const getKeyboardAwareScrollProps = () => {
  return {
    keyboardShouldPersistTaps: "handled" as const,
    keyboardDismissMode: Platform.OS === "ios" ? ("interactive" as const) : ("on-drag" as const),
    showsVerticalScrollIndicator: false,
    contentInsetAdjustmentBehavior: "automatic" as const,
  };
};

/**
 * Hook for keyboard-aware modal positioning
 */
export const useKeyboardAwareModal = () => {
  const keyboard = useKeyboard();
  const screenHeight = Dimensions.get("window").height;

  const modalOffset = keyboard.isVisible ? Math.max(0, (keyboard.height - screenHeight * 0.3) / 2) : 0;

  return {
    isKeyboardVisible: keyboard.isVisible,
    keyboardHeight: keyboard.height,
    modalOffset,
    animationDuration: keyboard.duration,
  };
};

/**
 * Utility to handle keyboard events in components
 */
export const createKeyboardHandler = (onShow?: (height: number) => void, onHide?: () => void) => {
  const showEvent = Platform.OS === "ios" ? "keyboardWillShow" : "keyboardDidShow";
  const hideEvent = Platform.OS === "ios" ? "keyboardWillHide" : "keyboardDidHide";

  const showSubscription = Keyboard.addListener(showEvent, (event) => {
    onShow?.(event.endCoordinates.height);
  });

  const hideSubscription = Keyboard.addListener(hideEvent, () => {
    onHide?.();
  });

  return () => {
    showSubscription?.remove();
    hideSubscription?.remove();
  };
};

/**
 * Debounced keyboard dismiss for rapid interactions
 */
let dismissTimeout: NodeJS.Timeout;

export const debouncedKeyboardDismiss = (delay = 100) => {
  clearTimeout(dismissTimeout);
  dismissTimeout = setTimeout(() => {
    Keyboard.dismiss();
  }, delay);
};

import {
  withTiming,
  withSpring,
  withSequence,
  withDelay,
  withRepeat,
  Easing,
  cancelAnimation,
  runOnJS,
  SharedValue,
  AnimatedStyle,
  WithTimingConfig,
  WithSpringConfig,
} from "react-native-reanimated";
import { AccessibilityInfo } from "react-native";
import * as Haptics from "expo-haptics";

export type TransitionPreset =
  | "fade"
  | "slide"
  | "scale"
  | "fadeSlide"
  | "bounce"
  | "smooth"
  | "error"
  | "success"
  | "loading";

export type TransitionScenario =
  | "initialLoad"
  | "refresh"
  | "loadMore"
  | "errorRecovery"
  | "contentReady"
  | "networkReconnect";

interface TransitionConfig {
  duration?: number;
  delay?: number;
  easing?: typeof Easing.linear;
  springConfig?: WithSpringConfig;
  reducedMotion?: boolean;
}

interface StaggerConfig {
  itemCount: number;
  baseDelay?: number;
  staggerDelay?: number;
  reverse?: boolean;
}

interface AnimationPerformance {
  startTime: number;
  endTime?: number;
  frameCount: number;
  averageFPS?: number;
}

export class LoadingTransitions {
  private static performanceMetrics: Map<string, AnimationPerformance> = new Map();
  private static activeAnimations: Set<string> = new Set();
  private static reducedMotionEnabled: boolean = false;

  static {
    AccessibilityInfo.isReduceMotionEnabled().then((enabled) => {
      this.reducedMotionEnabled = enabled || false;
    });

    AccessibilityInfo.addEventListener("reduceMotionChanged", (enabled) => {
      this.reducedMotionEnabled = enabled;
    });
  }

  static getPresetConfig(preset: TransitionPreset): Required<TransitionConfig> {
    const configs: Record<TransitionPreset, Required<TransitionConfig>> = {
      fade: {
        duration: 300,
        delay: 0,
        easing: Easing.out(Easing.quad),
        springConfig: { damping: 15, stiffness: 100 },
        reducedMotion: false,
      },
      slide: {
        duration: 400,
        delay: 0,
        easing: Easing.out(Easing.cubic),
        springConfig: { damping: 20, stiffness: 90 },
        reducedMotion: false,
      },
      scale: {
        duration: 350,
        delay: 0,
        easing: Easing.out(Easing.back(1.2)),
        springConfig: { damping: 12, stiffness: 180 },
        reducedMotion: false,
      },
      fadeSlide: {
        duration: 450,
        delay: 0,
        easing: Easing.out(Easing.cubic),
        springConfig: { damping: 18, stiffness: 100 },
        reducedMotion: false,
      },
      bounce: {
        duration: 500,
        delay: 0,
        easing: Easing.bounce,
        springConfig: { damping: 8, stiffness: 200, overshootClamping: false },
        reducedMotion: false,
      },
      smooth: {
        duration: 600,
        delay: 0,
        easing: Easing.inOut(Easing.ease),
        springConfig: { damping: 25, stiffness: 80 },
        reducedMotion: false,
      },
      error: {
        duration: 300,
        delay: 0,
        easing: Easing.out(Easing.exp),
        springConfig: { damping: 10, stiffness: 150 },
        reducedMotion: false,
      },
      success: {
        duration: 400,
        delay: 0,
        easing: Easing.out(Easing.elastic(1.2)),
        springConfig: { damping: 15, stiffness: 120 },
        reducedMotion: false,
      },
      loading: {
        duration: 1000,
        delay: 0,
        easing: Easing.linear,
        springConfig: { damping: 20, stiffness: 100 },
        reducedMotion: false,
      },
    };

    const config = configs[preset];

    if (this.reducedMotionEnabled) {
      config.duration = Math.min(config.duration, 200);
      config.easing = Easing.linear;
    }

    return config;
  }

  static getScenarioConfig(scenario: TransitionScenario): TransitionConfig {
    const configs: Record<TransitionScenario, TransitionConfig> = {
      initialLoad: {
        duration: 500,
        delay: 100,
        easing: Easing.out(Easing.cubic),
      },
      refresh: {
        duration: 400,
        delay: 0,
        easing: Easing.out(Easing.quad),
      },
      loadMore: {
        duration: 300,
        delay: 50,
        easing: Easing.out(Easing.ease),
      },
      errorRecovery: {
        duration: 350,
        delay: 200,
        easing: Easing.out(Easing.exp),
      },
      contentReady: {
        duration: 450,
        delay: 0,
        easing: Easing.out(Easing.cubic),
      },
      networkReconnect: {
        duration: 600,
        delay: 100,
        easing: Easing.inOut(Easing.ease),
      },
    };

    return configs[scenario];
  }

  static fadeIn(value: SharedValue<number>, config?: TransitionConfig, onComplete?: () => void): void {
    const { duration, delay, easing } = {
      ...this.getPresetConfig("fade"),
      ...config,
    };

    const animationId = `fade-${Date.now()}`;
    this.startPerformanceTracking(animationId);

    value.value = withDelay(
      delay,
      withTiming(1, { duration, easing }, (finished) => {
        if (finished) {
          this.endPerformanceTracking(animationId);
          if (onComplete) runOnJS(onComplete)();
        }
      }),
    );
  }

  static fadeOut(value: SharedValue<number>, config?: TransitionConfig, onComplete?: () => void): void {
    const { duration, delay, easing } = {
      ...this.getPresetConfig("fade"),
      ...config,
    };

    value.value = withDelay(
      delay,
      withTiming(0, { duration, easing }, (finished) => {
        if (finished && onComplete) runOnJS(onComplete)();
      }),
    );
  }

  static slideIn(
    value: SharedValue<number>,
    from: number = 100,
    config?: TransitionConfig,
    onComplete?: () => void,
  ): void {
    const { duration, delay, easing } = {
      ...this.getPresetConfig("slide"),
      ...config,
    };

    value.value = from;
    value.value = withDelay(
      delay,
      withTiming(0, { duration, easing }, (finished) => {
        if (finished && onComplete) runOnJS(onComplete)();
      }),
    );
  }

  static scaleIn(value: SharedValue<number>, config?: TransitionConfig, onComplete?: () => void): void {
    const { duration, delay, springConfig } = {
      ...this.getPresetConfig("scale"),
      ...config,
    };

    value.value = 0.8;
    value.value = withDelay(
      delay,
      withSpring(1, springConfig, (finished) => {
        if (finished && onComplete) runOnJS(onComplete)();
      }),
    );
  }

  static createStaggeredAnimation(
    values: SharedValue<number>[],
    targetValue: number,
    config: StaggerConfig & TransitionConfig,
  ): void {
    const {
      itemCount,
      baseDelay = 0,
      staggerDelay = 50,
      reverse = false,
      duration = 400,
      easing = Easing.out(Easing.cubic),
    } = config;

    const items = reverse ? values.slice().reverse() : values;

    items.forEach((value, index) => {
      const delay = baseDelay + index * staggerDelay;
      value.value = withDelay(delay, withTiming(targetValue, { duration, easing }));
    });
  }

  static createSequentialAnimation(value: SharedValue<number>, sequence: number[], config?: TransitionConfig): void {
    const { duration = 300, easing = Easing.linear } = config || {};

    const animations = sequence.map((target) => withTiming(target, { duration: duration / sequence.length, easing }));

    value.value = withSequence(...animations);
  }

  static createLoadingPulse(value: SharedValue<number>, config?: TransitionConfig): () => void {
    const { duration = 1000 } = config || {};

    value.value = withRepeat(
      withSequence(withTiming(0.6, { duration: duration / 2 }), withTiming(0.3, { duration: duration / 2 })),
      -1,
      false,
    );

    return () => cancelAnimation(value);
  }

  static createShimmerEffect(
    translateX: SharedValue<number>,
    screenWidth: number,
    config?: TransitionConfig,
  ): () => void {
    const { duration = 1500 } = config || {};

    translateX.value = withRepeat(
      withTiming(screenWidth * 2, {
        duration,
        easing: Easing.linear,
      }),
      -1,
      false,
    );

    return () => cancelAnimation(translateX);
  }

  static createErrorShake(value: SharedValue<number>, config?: TransitionConfig, onComplete?: () => void): void {
    const amplitude = 10;

    value.value = withSequence(
      withTiming(-amplitude, { duration: 50 }),
      withTiming(amplitude, { duration: 100 }),
      withTiming(-amplitude, { duration: 100 }),
      withTiming(amplitude, { duration: 100 }),
      withTiming(0, { duration: 50 }, (finished) => {
        if (finished && onComplete) {
          runOnJS(onComplete)();
          runOnJS(() => Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Medium))();
        }
      }),
    );
  }

  static createSuccessBounce(value: SharedValue<number>, config?: TransitionConfig, onComplete?: () => void): void {
    const { springConfig } = {
      ...this.getPresetConfig("success"),
      ...config,
    };

    value.value = 0.8;
    value.value = withSpring(1, springConfig, (finished) => {
      if (finished && onComplete) {
        runOnJS(onComplete)();
        runOnJS(() => Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Light))();
      }
    });
  }

  static cleanupAnimation(value: SharedValue<any>): void {
    cancelAnimation(value);
  }

  static cleanupAllAnimations(): void {
    this.activeAnimations.clear();
    this.performanceMetrics.clear();
  }

  private static startPerformanceTracking(id: string): void {
    if (__DEV__) {
      this.activeAnimations.add(id);
      this.performanceMetrics.set(id, {
        startTime: Date.now(),
        frameCount: 0,
      });
    }
  }

  private static endPerformanceTracking(id: string): void {
    if (__DEV__) {
      this.activeAnimations.delete(id);
      const metrics = this.performanceMetrics.get(id);
      if (metrics) {
        metrics.endTime = Date.now();
        const duration = metrics.endTime - metrics.startTime;
        metrics.averageFPS = (metrics.frameCount / duration) * 1000;

        if (metrics.averageFPS < 50) {
          console.warn(`[LoadingTransitions] Low FPS detected: ${metrics.averageFPS.toFixed(2)} for animation ${id}`);
        }
      }
    }
  }

  static getPerformanceMetrics(): Map<string, AnimationPerformance> {
    return new Map(this.performanceMetrics);
  }

  static createOptimizedWorklet(animationFn: () => void): void {
    "worklet";
    animationFn();
  }

  static shouldUseReducedMotion(): boolean {
    return this.reducedMotionEnabled;
  }

  static async triggerHapticForTransition(type: TransitionPreset): Promise<void> {
    const hapticMap: Partial<Record<TransitionPreset, Haptics.ImpactFeedbackStyle>> = {
      error: Haptics.ImpactFeedbackStyle.Medium,
      success: Haptics.ImpactFeedbackStyle.Light,
      bounce: Haptics.ImpactFeedbackStyle.Light,
    };

    const hapticStyle = hapticMap[type];
    if (hapticStyle) {
      await Haptics.impactAsync(hapticStyle);
    }
  }
}

export function withSmartEasing(value: number, config: WithTimingConfig & { smart?: boolean }): number {
  "worklet";

  if (config.smart && LoadingTransitions.shouldUseReducedMotion()) {
    return withTiming(value, {
      ...config,
      duration: Math.min(config.duration || 300, 200),
      easing: Easing.linear,
    });
  }

  return withTiming(value, config);
}

export function createAnimatedStyleForTransition(
  preset: TransitionPreset,
  value: SharedValue<number>,
): AnimatedStyle<any> {
  "worklet";

  const styles: Record<TransitionPreset, AnimatedStyle<any>> = {
    fade: { opacity: value.value },
    slide: { transform: [{ translateY: value.value }] },
    scale: { transform: [{ scale: value.value }] },
    fadeSlide: {
      opacity: value.value,
      transform: [{ translateY: (1 - value.value) * 20 }],
    },
    bounce: {
      transform: [{ scale: value.value }],
    },
    smooth: {
      opacity: value.value,
    },
    error: {
      transform: [{ translateX: value.value }],
    },
    success: {
      transform: [{ scale: value.value }],
    },
    loading: {
      opacity: value.value,
    },
  };

  return styles[preset] || styles.fade;
}

/**
 * Generic LRU (Least Recently Used) Cache implementation
 * Supports both memory and size-based eviction policies
 */

export interface CacheEntry<T> {
  key: string;
  value: T;
  timestamp: number;
  accessCount: number;
  size?: number;
  lastAccessTime?: number;
  score?: number;
  compressed?: boolean;
  metadata?: Record<string, any>;
}

export interface CacheOptions {
  maxSize?: number; // Maximum number of entries
  maxMemory?: number; // Maximum memory usage in bytes
  ttl?: number; // Time to live in milliseconds
  onEvict?: (key: string, value: any) => void | Promise<void>;
  getSizeOf?: (value: any) => number;
  enableCompression?: boolean;
  enableStatistics?: boolean;
  scoreFunction?: (entry: CacheEntry<any>) => number;
  persistToDisk?: boolean;
  warmupEntries?: string[];
}

export class LRUCache<T> {
  private cache = new Map<string, CacheEntry<T>>();
  private accessOrder: string[] = [];
  private currentMemoryUsage = 0;
  private readonly options: Required<CacheOptions>;
  private statistics = {
    hits: 0,
    misses: 0,
    evictions: 0,
    compressions: 0,
    avgAccessTime: 0,
    totalAccessTime: 0,
    accessCount: 0,
  };
  private batchOperationQueue: Map<string, { operation: "set" | "delete"; value?: T }> = new Map();
  private batchTimer?: ReturnType<typeof setTimeout>;
  private cleanupScheduled = false;

  constructor(options: CacheOptions = {}) {
    this.options = {
      maxSize: options.maxSize ?? 100,
      maxMemory: options.maxMemory ?? 50 * 1024 * 1024, // 50MB default
      ttl: options.ttl ?? 30 * 60 * 1000, // 30 minutes default
      onEvict: options.onEvict ?? (() => {}),
      getSizeOf:
        options.getSizeOf ??
        ((value: any) => {
          if (typeof value === "string") return value.length * 2; // UTF-16
          if (typeof value === "object") return JSON.stringify(value).length * 2;
          return 64; // Default size for primitives
        }),
      enableCompression: options.enableCompression ?? false,
      enableStatistics: options.enableStatistics ?? true,
      scoreFunction: options.scoreFunction ?? this.defaultScoreFunction,
      persistToDisk: options.persistToDisk ?? false,
      warmupEntries: options.warmupEntries ?? [],
    };

    // Warm up cache with predefined entries
    if (this.options.warmupEntries.length > 0) {
      this.warmupCache();
    }
  }

  /**
   * Get a value from the cache
   */
  get(key: string): T | undefined {
    const startTime = Date.now();
    const entry = this.cache.get(key);

    if (!entry) {
      if (this.options.enableStatistics) {
        this.statistics.misses++;
      }
      return undefined;
    }

    // Check if entry has expired
    if (this.isExpired(entry)) {
      this.delete(key);
      if (this.options.enableStatistics) {
        this.statistics.misses++;
      }
      return undefined;
    }

    // Update access information
    const now = Date.now();
    entry.timestamp = now;
    entry.lastAccessTime = now;
    entry.accessCount++;
    entry.score = this.options.scoreFunction(entry);

    // Move to end of access order (most recently used)
    this.updateAccessOrder(key);

    // Update statistics
    if (this.options.enableStatistics) {
      this.statistics.hits++;
      const accessTime = Date.now() - startTime;
      this.statistics.totalAccessTime += accessTime;
      this.statistics.accessCount++;
      this.statistics.avgAccessTime = this.statistics.totalAccessTime / this.statistics.accessCount;
    }

    return entry.value;
  }

  /**
   * Set a value in the cache
   */
  set(key: string, value: T): void {
    const existingEntry = this.cache.get(key);
    let size = this.options.getSizeOf(value);
    let compressedValue = value;

    // Apply compression if enabled and beneficial
    if (this.options.enableCompression && size > 1024) {
      const compressed = this.compress(value);
      if (compressed.size < size * 0.8) {
        compressedValue = compressed.value as T;
        size = compressed.size;
        if (this.options.enableStatistics) {
          this.statistics.compressions++;
        }
      }
    }

    // If updating existing entry, adjust memory usage
    if (existingEntry) {
      this.currentMemoryUsage -= existingEntry.size || 0;
    }

    // Check if we need to evict before adding new entry
    const tempMemoryUsage = this.currentMemoryUsage + size;
    if (tempMemoryUsage > this.options.maxMemory || this.cache.size >= this.options.maxSize) {
      this.evictIfNecessary();
    }

    // Create new entry
    const now = Date.now();
    const entry: CacheEntry<T> = {
      key,
      value: compressedValue,
      timestamp: now,
      lastAccessTime: now,
      accessCount: 1,
      size,
      compressed: compressedValue !== value,
      score: 0,
    };
    entry.score = this.options.scoreFunction(entry);

    // Update memory usage and add to cache
    this.currentMemoryUsage += size;
    this.cache.set(key, entry);
    this.updateAccessOrder(key);
  }

  /**
   * Delete a specific key from the cache
   */
  delete(key: string): boolean {
    const entry = this.cache.get(key);

    if (!entry) {
      return false;
    }

    // Update memory usage
    this.currentMemoryUsage -= entry.size || 0;

    // Remove from cache and access order
    this.cache.delete(key);
    this.removeFromAccessOrder(key);

    // Call eviction callback
    const evictResult = this.options.onEvict(key, entry.value);
    if (evictResult instanceof Promise) {
      evictResult.catch((err) => console.error("Eviction callback error:", err));
    }

    if (this.options.enableStatistics) {
      this.statistics.evictions++;
    }

    return true;
  }

  /**
   * Check if a key exists in the cache
   */
  has(key: string): boolean {
    const entry = this.cache.get(key);
    return entry !== undefined && !this.isExpired(entry);
  }

  /**
   * Clear all entries from the cache
   */
  clear(): void {
    // Call eviction callback for all entries
    this.cache.forEach((entry, key) => {
      this.options.onEvict(key, entry.value);
    });

    this.cache.clear();
    this.accessOrder = [];
    this.currentMemoryUsage = 0;
  }

  /**
   * Get cache statistics
   */
  getStats() {
    const _now = Date.now();
    let expiredCount = 0;

    this.cache.forEach((entry) => {
      if (this.isExpired(entry)) {
        expiredCount++;
      }
    });

    const hitRate =
      this.statistics.hits + this.statistics.misses > 0
        ? this.statistics.hits / (this.statistics.hits + this.statistics.misses)
        : 0;

    return {
      size: this.cache.size,
      memoryUsage: this.currentMemoryUsage,
      maxSize: this.options.maxSize,
      maxMemory: this.options.maxMemory,
      expiredCount,
      hitRate,
      hits: this.statistics.hits,
      misses: this.statistics.misses,
      evictions: this.statistics.evictions,
      compressions: this.statistics.compressions,
      avgAccessTime: this.statistics.avgAccessTime,
      memoryEfficiency: this.cache.size > 0 ? this.currentMemoryUsage / (this.cache.size * 1024) : 0,
    };
  }

  /**
   * Get all keys in the cache (excluding expired ones)
   */
  keys(): string[] {
    const validKeys: string[] = [];

    this.cache.forEach((entry, key) => {
      if (!this.isExpired(entry)) {
        validKeys.push(key);
      }
    });

    return validKeys;
  }

  /**
   * Get all values in the cache (excluding expired ones)
   */
  values(): T[] {
    const validValues: T[] = [];

    this.cache.forEach((entry) => {
      if (!this.isExpired(entry)) {
        validValues.push(entry.value);
      }
    });

    return validValues;
  }

  /**
   * Cleanup expired entries - now with lazy cleanup option
   */
  cleanup(): number {
    if (this.cleanupScheduled) {
      return 0; // Cleanup already scheduled
    }

    // Schedule lazy cleanup
    if (this.cache.size < this.options.maxSize * 0.9) {
      this.cleanupScheduled = true;
      setImmediate(() => {
        this.performCleanup();
        this.cleanupScheduled = false;
      });
      return 0;
    }

    // Immediate cleanup for high usage
    return this.performCleanup();
  }

  private performCleanup(): number {
    let removedCount = 0;
    const _now = Date.now();

    const keysToDelete: string[] = [];
    this.cache.forEach((entry, key) => {
      if (this.isExpired(entry)) {
        keysToDelete.push(key);
      }
    });

    keysToDelete.forEach((key) => {
      this.delete(key);
      removedCount++;
    });

    return removedCount;
  }

  private isExpired(entry: CacheEntry<T>): boolean {
    return Date.now() - entry.timestamp > this.options.ttl;
  }

  private updateAccessOrder(key: string): void {
    // Remove from current position
    this.removeFromAccessOrder(key);
    // Add to end (most recently used)
    this.accessOrder.push(key);
  }

  private removeFromAccessOrder(key: string): void {
    const index = this.accessOrder.indexOf(key);
    if (index > -1) {
      this.accessOrder.splice(index, 1);
    }
  }

  private evictIfNecessary(): void {
    // Use intelligent eviction with scoring
    const entries = Array.from(this.cache.entries());

    // Sort by score (lower score = more likely to evict)
    entries.sort((a, b) => {
      const scoreA = a[1].score || this.options.scoreFunction(a[1]);
      const scoreB = b[1].score || this.options.scoreFunction(b[1]);
      return scoreA - scoreB;
    });

    // Evict by size limit
    while (this.cache.size >= this.options.maxSize && entries.length > 0) {
      const [key] = entries.shift()!;
      this.delete(key);
    }

    // Evict by memory limit
    while (this.currentMemoryUsage > this.options.maxMemory && entries.length > 0) {
      const [key] = entries.shift()!;
      this.delete(key);
    }

    // Clean up expired entries
    this.cleanup();
  }

  private calculateHitRate(): number {
    if (this.cache.size === 0) return 0;

    let totalRequests = 0;
    let cacheHits = 0;

    this.cache.forEach((entry) => {
      totalRequests += entry.accessCount;
      // Every access after the first is a cache hit
      if (entry.accessCount > 1) {
        cacheHits += entry.accessCount - 1;
      }
    });

    return totalRequests > 0 ? cacheHits / totalRequests : 0;
  }

  /**
   * Batch operations for better performance
   */
  batchSet(entries: [string, T][]): void {
    entries.forEach(([key, value]) => {
      this.batchOperationQueue.set(key, { operation: "set", value });
    });
    this.processBatchOperations();
  }

  batchDelete(keys: string[]): void {
    keys.forEach((key) => {
      this.batchOperationQueue.set(key, { operation: "delete" });
    });
    this.processBatchOperations();
  }

  private processBatchOperations(): void {
    if (this.batchTimer) {
      clearTimeout(this.batchTimer);
    }

    this.batchTimer = setTimeout(() => {
      this.batchOperationQueue.forEach(({ operation, value }, key) => {
        if (operation === "set" && value !== undefined) {
          this.set(key, value);
        } else if (operation === "delete") {
          this.delete(key);
        }
      });
      this.batchOperationQueue.clear();
    }, 10); // Process batch after 10ms
  }

  /**
   * Warm up cache with predefined entries
   */
  async warmup(keys: string[], loadFunction: (key: string) => Promise<T>): Promise<void> {
    const warmupPromises = keys.map(async (key) => {
      try {
        const value = await loadFunction(key);
        this.set(key, value);
      } catch (error) {
        console.error(`Failed to warm up cache for key ${key}:`, error);
      }
    });

    await Promise.allSettled(warmupPromises);
  }

  private warmupCache(): void {
    // Implementation for initial warmup from options
    console.log("Cache warmup initiated with", this.options.warmupEntries.length, "entries");
  }

  /**
   * Default scoring function for intelligent eviction
   */
  private defaultScoreFunction(entry: CacheEntry<any>): number {
    const now = Date.now();
    const age = now - entry.timestamp;
    const recency = now - (entry.lastAccessTime || entry.timestamp);
    const frequency = entry.accessCount;
    const size = entry.size || 1;

    // Higher score = keep in cache
    // Consider frequency, recency, and size
    const frequencyScore = Math.log(frequency + 1) * 100;
    const recencyScore = Math.max(0, 1000 - recency / 1000);
    const sizeScore = Math.max(0, 100 - size / 10000);
    const ageScore = Math.max(0, 1000 - age / 10000);

    return frequencyScore + recencyScore + sizeScore + ageScore;
  }

  /**
   * Simple compression for text data
   */
  private compress(value: any): { value: any; size: number } {
    // Compression disabled for React Native - Buffer not available
    // In production, use a RN-compatible compression library
    if (this.options.enableCompression && typeof value === "string" && value.length > 1024) {
      // For now, just return the original value
      // TODO: Integrate react-native-compression or similar
      if (__DEV__) {
        console.log("Compression requested but not available in React Native");
      }
    }
    return {
      value,
      size: this.options.getSizeOf(value),
    };
  }

  /**
   * Debug utilities
   */
  debug(): void {
    if (__DEV__) {
      console.log("Cache Debug Info:");
      console.log("- Size:", this.cache.size, "/", this.options.maxSize);
      console.log(
        "- Memory:",
        (this.currentMemoryUsage / 1024 / 1024).toFixed(2),
        "MB /",
        (this.options.maxMemory / 1024 / 1024).toFixed(2),
        "MB",
      );
      console.log("- Statistics:", this.statistics);
      console.log("- Top 5 by score:");

      const entries = Array.from(this.cache.entries())
        .map(([key, entry]) => ({ key, score: entry.score || 0 }))
        .sort((a, b) => b.score - a.score)
        .slice(0, 5);

      entries.forEach(({ key, score }) => {
        console.log(`  ${key}: ${score.toFixed(2)}`);
      });
    }
  }
}

/**
 * Create a specialized cache for different data types
 */
export const createImageCache = (
  maxMemory = 100 * 1024 * 1024, // 100MB
) =>
  new LRUCache<string>({
    maxSize: 200,
    maxMemory,
    ttl: 60 * 60 * 1000, // 1 hour
    getSizeOf: (uri: string) => {
      // More accurate image size estimation
      const baseSize = uri.length * 2; // URI string size
      // Estimate typical image sizes based on URI patterns
      if (uri.includes("thumb") || uri.includes("small")) {
        return baseSize + 50 * 1024; // 50KB for thumbnails
      }
      if (uri.includes("large") || uri.includes("full")) {
        return baseSize + 500 * 1024; // 500KB for large images
      }
      return baseSize + 200 * 1024; // 200KB default estimate
    },
  });

export const createDataCache = <T>(
  maxSize = 100,
  ttl = 15 * 60 * 1000, // 15 minutes
) =>
  new LRUCache<T>({
    maxSize,
    maxMemory: 10 * 1024 * 1024, // 10MB
    ttl,
  });

export const createVideoCache = (
  maxMemory = 500 * 1024 * 1024, // 500MB
) =>
  new LRUCache<string>({
    maxSize: 50,
    maxMemory,
    ttl: 2 * 60 * 60 * 1000, // 2 hours
    getSizeOf: (uri: string) => {
      // More accurate video size estimation based on URI patterns
      const baseSize = uri.length * 2;
      if (uri.includes("preview") || uri.includes("thumb")) {
        return baseSize + 2 * 1024 * 1024; // 2MB for previews
      }
      if (uri.includes("720p") || uri.includes("hd")) {
        return baseSize + 25 * 1024 * 1024; // 25MB for HD videos
      }
      if (uri.includes("1080p") || uri.includes("fhd")) {
        return baseSize + 50 * 1024 * 1024; // 50MB for Full HD
      }
      return baseSize + 15 * 1024 * 1024; // 15MB default estimate
    },
  });

import NetInfo, { NetInfoState, NetInfoSubscription } from "@react-native-community/netinfo";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { healthMonitor } from "./healthMonitor";

export type ConnectionType = "wifi" | "4g" | "5g" | "3g" | "2g" | "ethernet" | "unknown";
export type NetworkQuality = "excellent" | "good" | "fair" | "poor";

export interface NetworkProfile {
  bandwidth: number; // Mbps
  latency: number; // ms
  jitter: number; // ms
  packetLoss: number; // percentage
  connectionType: ConnectionType;
  quality: NetworkQuality;
  stability: number; // 0-1 score
  timestamp: number;
  isMetered: boolean;
  signalStrength?: number;
}

export interface NetworkPattern {
  hour: number;
  dayOfWeek: number;
  averageBandwidth: number;
  averageLatency: number;
  quality: NetworkQuality;
  samples: number;
}

interface NetworkMeasurement {
  bandwidth: number;
  latency: number;
  timestamp: number;
  connectionType: ConnectionType;
}

interface NetworkConditionOptions {
  enableAdaptiveMeasurement?: boolean;
  enablePatternLearning?: boolean;
  enableDataUsageTracking?: boolean;
  measurementInterval?: number;
  historySize?: number;
}

class NetworkProfiler {
  private currentProfile: NetworkProfile | null = null;
  private measurementHistory: NetworkMeasurement[] = [];
  private networkPatterns: Map<string, NetworkPattern> = new Map();
  private stabilityHistory: number[] = [];
  private dataUsage = {
    wifi: 0,
    cellular: 0,
    total: 0,
    resetDate: Date.now(),
  };
  private networkSubscription: NetInfoSubscription | null = null;
  private measurementInterval: NodeJS.Timeout | null = null;
  private options: Required<NetworkConditionOptions>;
  private readonly CACHE_KEY_PREFIX = "network_profiler_";
  private readonly STABILITY_WINDOW = 10;
  private readonly PATTERN_SAMPLE_THRESHOLD = 5;
  private testUrls = [
    "https://www.gstatic.com/generate_204",
    "https://api.github.com",
    "https://www.cloudflare.com/cdn-cgi/trace",
  ];

  constructor(options: NetworkConditionOptions = {}) {
    this.options = {
      enableAdaptiveMeasurement: options.enableAdaptiveMeasurement ?? true,
      enablePatternLearning: options.enablePatternLearning ?? true,
      enableDataUsageTracking: options.enableDataUsageTracking ?? true,
      measurementInterval: options.measurementInterval ?? 30000, // 30 seconds
      historySize: options.historySize ?? 100,
    };

    this.initialize();
  }

  private async initialize(): Promise<void> {
    // Load persisted data
    await this.loadPersistedData();

    // Start network monitoring
    this.startNetworkMonitoring();

    // Start periodic measurements
    if (this.options.enableAdaptiveMeasurement) {
      this.startAdaptiveMeasurement();
    }

    // Initial measurement
    await this.measureNetworkCondition();
  }

  private async loadPersistedData(): Promise<void> {
    try {
      // Load network patterns
      const patternsData = await AsyncStorage.getItem(`${this.CACHE_KEY_PREFIX}patterns`);
      if (patternsData) {
        const patterns = JSON.parse(patternsData);
        this.networkPatterns = new Map(Object.entries(patterns));
      }

      // Load data usage
      const dataUsageData = await AsyncStorage.getItem(`${this.CACHE_KEY_PREFIX}data_usage`);
      if (dataUsageData) {
        this.dataUsage = JSON.parse(dataUsageData);

        // Reset monthly
        const monthDiff = new Date().getMonth() - new Date(this.dataUsage.resetDate).getMonth();
        if (monthDiff !== 0) {
          this.resetDataUsage();
        }
      }

      // Load last profile
      const profileData = await AsyncStorage.getItem(`${this.CACHE_KEY_PREFIX}last_profile`);
      if (profileData) {
        this.currentProfile = JSON.parse(profileData);
      }
    } catch (error) {
      console.error("Failed to load persisted network data:", error);
    }
  }

  private startNetworkMonitoring(): void {
    this.networkSubscription = NetInfo.addEventListener((state) => {
      this.handleNetworkChange(state);
    });
  }

  private async handleNetworkChange(state: NetInfoState): Promise<void> {
    const wasConnected = this.currentProfile !== null;
    const isConnected = state.isConnected;

    if (isConnected && !wasConnected) {
      // Connection restored - measure immediately
      await this.measureNetworkCondition();
    } else if (!isConnected && wasConnected) {
      // Connection lost
      this.currentProfile = {
        bandwidth: 0,
        latency: Infinity,
        jitter: Infinity,
        packetLoss: 100,
        connectionType: "unknown",
        quality: "poor",
        stability: 0,
        timestamp: Date.now(),
        isMetered: false,
      };
    }

    // Update health monitor
    healthMonitor.trackInteraction("network_status", { connected: isConnected });
  }

  private startAdaptiveMeasurement(): void {
    const performMeasurement = async () => {
      const profile = await this.measureNetworkCondition();

      if (profile) {
        // Adjust measurement frequency based on stability
        let nextInterval = this.options.measurementInterval;

        if (profile.stability < 0.5) {
          // Unstable network - measure more frequently
          nextInterval = Math.max(10000, nextInterval / 2);
        } else if (profile.stability > 0.9) {
          // Very stable - measure less frequently
          nextInterval = Math.min(60000, nextInterval * 1.5);
        }

        // Schedule next measurement
        this.measurementInterval = setTimeout(performMeasurement, nextInterval);
      }
    };

    performMeasurement();
  }

  public async measureNetworkCondition(): Promise<NetworkProfile | null> {
    const netInfo = await NetInfo.fetch();

    if (!netInfo.isConnected) {
      return null;
    }

    const connectionType = this.detectConnectionType(netInfo);
    const bandwidth = await this.measureBandwidth();
    const latency = await this.measureLatency();
    const jitter = this.calculateJitter();
    const packetLoss = await this.estimatePacketLoss();
    const stability = this.calculateStability(bandwidth);

    const profile: NetworkProfile = {
      bandwidth,
      latency,
      jitter,
      packetLoss,
      connectionType,
      quality: this.classifyNetworkQuality(bandwidth, latency, stability),
      stability,
      timestamp: Date.now(),
      isMetered: netInfo.details?.isConnectionExpensive ?? false,
      signalStrength: this.getSignalStrength(netInfo),
    };

    // Update history
    this.addMeasurement({
      bandwidth,
      latency,
      timestamp: Date.now(),
      connectionType,
    });

    // Learn patterns
    if (this.options.enablePatternLearning) {
      this.learnNetworkPattern(profile);
    }

    // Track data usage
    if (this.options.enableDataUsageTracking) {
      this.trackDataUsage(connectionType);
    }

    this.currentProfile = profile;
    await this.persistProfile(profile);

    return profile;
  }

  private detectConnectionType(netInfo: NetInfoState): ConnectionType {
    const type = netInfo.type.toLowerCase();

    if (type === "wifi") return "wifi";
    if (type === "ethernet") return "ethernet";

    if (type === "cellular") {
      const cellularGeneration = (netInfo.details as any)?.cellularGeneration;

      if (cellularGeneration) {
        const gen = cellularGeneration.toLowerCase();
        if (gen.includes("5g")) return "5g";
        if (gen.includes("4g") || gen.includes("lte")) return "4g";
        if (gen.includes("3g")) return "3g";
        if (gen.includes("2g")) return "2g";
      }

      return "4g"; // Default to 4G for cellular
    }

    return "unknown";
  }

  private async measureBandwidth(): Promise<number> {
    const measurements: number[] = [];

    for (const url of this.testUrls.slice(0, 2)) {
      try {
        const size = 1024; // Approximate response size in bytes
        const startTime = Date.now();

        const response = await fetch(url, {
          method: "GET",
          cache: "no-cache",
          headers: {
            "Cache-Control": "no-cache",
            Pragma: "no-cache",
          },
        });

        if (response.ok) {
          const duration = Date.now() - startTime;
          const bandwidth = (size * 8) / (duration / 1000) / 1000000; // Mbps
          measurements.push(bandwidth);
        }
      } catch (error) {
        // Continue with other URLs
      }
    }

    if (measurements.length === 0) {
      return this.estimateBandwidthFromConnection();
    }

    // Return median
    measurements.sort((a, b) => a - b);
    return measurements[Math.floor(measurements.length / 2)];
  }

  private async measureLatency(): Promise<number> {
    const measurements: number[] = [];

    for (const url of this.testUrls) {
      try {
        const startTime = Date.now();

        await fetch(url, {
          method: "HEAD",
          cache: "no-cache",
        });

        const latency = Date.now() - startTime;
        measurements.push(latency);
      } catch (error) {
        // Continue with other URLs
      }
    }

    if (measurements.length === 0) {
      return 100; // Default latency
    }

    // Return minimum (best case)
    return Math.min(...measurements);
  }

  private calculateJitter(): number {
    if (this.measurementHistory.length < 2) return 0;

    const recentLatencies = this.measurementHistory.slice(-10).map((m) => m.latency);

    if (recentLatencies.length < 2) return 0;

    let sumDifferences = 0;
    for (let i = 1; i < recentLatencies.length; i++) {
      sumDifferences += Math.abs(recentLatencies[i] - recentLatencies[i - 1]);
    }

    return sumDifferences / (recentLatencies.length - 1);
  }

  private async estimatePacketLoss(): Promise<number> {
    // Simplified packet loss estimation
    // In a real implementation, this would use ICMP or specialized protocols
    let successCount = 0;
    const attempts = 5;

    for (let i = 0; i < attempts; i++) {
      try {
        const response = await fetch(this.testUrls[0], {
          method: "HEAD",
          signal: AbortSignal.timeout(1000),
        });

        if (response.ok) {
          successCount++;
        }
      } catch {
        // Counted as packet loss
      }
    }

    return ((attempts - successCount) / attempts) * 100;
  }

  private calculateStability(currentBandwidth: number): number {
    this.stabilityHistory.push(currentBandwidth);

    if (this.stabilityHistory.length > this.STABILITY_WINDOW) {
      this.stabilityHistory.shift();
    }

    if (this.stabilityHistory.length < 2) return 1;

    // Calculate coefficient of variation
    const mean = this.stabilityHistory.reduce((a, b) => a + b, 0) / this.stabilityHistory.length;
    const variance =
      this.stabilityHistory.reduce((sum, val) => {
        return sum + Math.pow(val - mean, 2);
      }, 0) / this.stabilityHistory.length;

    const coefficientOfVariation = Math.sqrt(variance) / mean;

    // Convert to 0-1 stability score (lower variation = higher stability)
    return Math.max(0, Math.min(1, 1 - coefficientOfVariation));
  }

  private classifyNetworkQuality(bandwidth: number, latency: number, stability: number): NetworkQuality {
    // Weighted scoring
    const bandwidthScore = Math.min(1, bandwidth / 20); // 20 Mbps = perfect
    const latencyScore = Math.max(0, 1 - latency / 200); // 0ms = perfect, 200ms+ = 0
    const stabilityScore = stability;

    const totalScore = bandwidthScore * 0.4 + latencyScore * 0.3 + stabilityScore * 0.3;

    if (totalScore >= 0.8) return "excellent";
    if (totalScore >= 0.6) return "good";
    if (totalScore >= 0.4) return "fair";
    return "poor";
  }

  private getSignalStrength(netInfo: NetInfoState): number | undefined {
    // This would need platform-specific implementation
    // For now, return undefined
    return undefined;
  }

  private addMeasurement(measurement: NetworkMeasurement): void {
    this.measurementHistory.push(measurement);

    // Keep history size limited
    if (this.measurementHistory.length > this.options.historySize) {
      this.measurementHistory.shift();
    }
  }

  private learnNetworkPattern(profile: NetworkProfile): void {
    const now = new Date();
    const hour = now.getHours();
    const dayOfWeek = now.getDay();
    const key = `${dayOfWeek}_${hour}`;

    const existing = this.networkPatterns.get(key);

    if (existing) {
      // Update existing pattern
      const samples = existing.samples + 1;
      const avgBandwidth = (existing.averageBandwidth * existing.samples + profile.bandwidth) / samples;
      const avgLatency = (existing.averageLatency * existing.samples + profile.latency) / samples;

      this.networkPatterns.set(key, {
        hour,
        dayOfWeek,
        averageBandwidth: avgBandwidth,
        averageLatency: avgLatency,
        quality: this.classifyNetworkQuality(avgBandwidth, avgLatency, profile.stability),
        samples,
      });
    } else {
      // Create new pattern
      this.networkPatterns.set(key, {
        hour,
        dayOfWeek,
        averageBandwidth: profile.bandwidth,
        averageLatency: profile.latency,
        quality: profile.quality,
        samples: 1,
      });
    }

    // Persist patterns periodically
    if (Math.random() < 0.1) {
      this.persistPatterns();
    }
  }

  private trackDataUsage(connectionType: ConnectionType): void {
    const estimatedBytes = 1024; // Rough estimate per measurement

    if (connectionType === "wifi") {
      this.dataUsage.wifi += estimatedBytes;
    } else if (["4g", "5g", "3g", "2g"].includes(connectionType)) {
      this.dataUsage.cellular += estimatedBytes;
    }

    this.dataUsage.total += estimatedBytes;

    // Persist periodically
    if (Math.random() < 0.1) {
      this.persistDataUsage();
    }
  }

  private resetDataUsage(): void {
    this.dataUsage = {
      wifi: 0,
      cellular: 0,
      total: 0,
      resetDate: Date.now(),
    };
    this.persistDataUsage();
  }

  private async estimateBandwidthFromConnection(): Promise<number> {
    const netInfo = await NetInfo.fetch();
    const connectionType = this.detectConnectionType(netInfo);

    const bandwidthMap: Record<ConnectionType, number> = {
      "5g": 20,
      "4g": 10,
      "3g": 2,
      "2g": 0.1,
      wifi: 15,
      ethernet: 100,
      unknown: 5,
    };

    return bandwidthMap[connectionType];
  }

  private async persistProfile(profile: NetworkProfile): Promise<void> {
    try {
      await AsyncStorage.setItem(`${this.CACHE_KEY_PREFIX}last_profile`, JSON.stringify(profile));
    } catch (error) {
      console.error("Failed to persist network profile:", error);
    }
  }

  private async persistPatterns(): Promise<void> {
    try {
      const patterns = Object.fromEntries(this.networkPatterns);
      await AsyncStorage.setItem(`${this.CACHE_KEY_PREFIX}patterns`, JSON.stringify(patterns));
    } catch (error) {
      console.error("Failed to persist network patterns:", error);
    }
  }

  private async persistDataUsage(): Promise<void> {
    try {
      await AsyncStorage.setItem(`${this.CACHE_KEY_PREFIX}data_usage`, JSON.stringify(this.dataUsage));
    } catch (error) {
      console.error("Failed to persist data usage:", error);
    }
  }

  public getCurrentProfile(): NetworkProfile | null {
    return this.currentProfile;
  }

  public getPredictedQuality(): NetworkQuality | null {
    const now = new Date();
    const hour = now.getHours();
    const dayOfWeek = now.getDay();
    const key = `${dayOfWeek}_${hour}`;

    const pattern = this.networkPatterns.get(key);

    if (pattern && pattern.samples >= this.PATTERN_SAMPLE_THRESHOLD) {
      return pattern.quality;
    }

    return this.currentProfile?.quality ?? null;
  }

  public getDataUsage(): typeof this.dataUsage {
    return { ...this.dataUsage };
  }

  public shouldReduceDataUsage(): boolean {
    if (!this.currentProfile) return false;

    // Reduce data usage on metered connections or poor quality
    return this.currentProfile.isMetered || this.currentProfile.quality === "poor";
  }

  public async testConnectivity(url?: string): Promise<boolean> {
    try {
      const testUrl = url || this.testUrls[0];
      const response = await fetch(testUrl, {
        method: "HEAD",
        signal: AbortSignal.timeout(5000),
      });

      return response.ok;
    } catch {
      return false;
    }
  }

  public destroy(): void {
    if (this.networkSubscription) {
      this.networkSubscription();
      this.networkSubscription = null;
    }

    if (this.measurementInterval) {
      clearTimeout(this.measurementInterval);
      this.measurementInterval = null;
    }

    // Persist final state
    this.persistPatterns();
    this.persistDataUsage();
  }
}

export const networkProfiler = new NetworkProfiler();

/**
 * Progressive Enhancement System
 *
 * This utility provides capability detection and graceful degradation
 * for features that require native modules or advanced hardware.
 */

import React, { useState, useEffect, useCallback } from "react";
import Constants from "expo-constants";
import { IS_EXPO_GO } from "./environmentCheck";
import { Platform } from "react-native";
import Device from "expo-device";

// Define capability interfaces
export interface DeviceCapabilities {
  hasVisionAPI: boolean;
  hasAudioProcessing: boolean;
  hasSpeechRecognition: boolean;
  hasGPUAcceleration: boolean;
  hasHighPerformanceCPU: boolean;
  hasAdvancedCamera: boolean;
  hasVideoProcessing: boolean;
  hasFaceDetection: boolean;
  hasVoiceModulation: boolean;
  platform: string;
  deviceType: string;
}

export interface FeatureFallbacks {
  faceBlur: () => React.ComponentType<any>;
  voiceChange: () => React.ComponentType<any>;
  liveCaptions: () => React.ComponentType<any>;
  advancedCamera: () => React.ComponentType<any>;
  videoEditor: () => React.ComponentType<any>;
}

/**
 * Detect device capabilities for progressive enhancement
 */
export const detectCapabilities = async (): Promise<DeviceCapabilities> => {
  const deviceType = await Device.getDeviceTypeAsync();
  const platform = Platform.OS;

  const capabilities: DeviceCapabilities = {
    hasVisionAPI: !IS_EXPO_GO,
    hasAudioProcessing: !IS_EXPO_GO,
    hasSpeechRecognition: !IS_EXPO_GO,
    hasGPUAcceleration: !IS_EXPO_GO,
    hasHighPerformanceCPU: deviceType === Device.DeviceType.TABLET || deviceType === Device.DeviceType.DESKTOP,
    hasAdvancedCamera: !IS_EXPO_GO,
    hasVideoProcessing: !IS_EXPO_GO,
    hasFaceDetection: !IS_EXPO_GO,
    hasVoiceModulation: !IS_EXPO_GO,
    platform,
    deviceType: Device.DeviceType[deviceType] || "UNKNOWN",
  };

  if (__DEV__) {
    console.log(" Device Capabilities Detected:", capabilities);
  }

  return capabilities;
};

/**
 * Get fallback UI components for unsupported features
 */
export const getFallbackUI = (feature: keyof FeatureFallbacks): React.ComponentType<any> => {
  const fallbacks: FeatureFallbacks = {
    faceBlur: () => require("../components/fallbacks/ServerProcessingNotice").default,
    voiceChange: () => require("../components/fallbacks/BasicAudioControls").default,
    liveCaptions: () => require("../components/fallbacks/TextInputFallback").default,
    advancedCamera: () => require("../components/fallbacks/BasicCameraControls").default,
    videoEditor: () => require("../components/fallbacks/SimpleVideoEditor").default,
  };

  try {
    return fallbacks[feature]();
  } catch (error) {
    console.warn(`Fallback component for ${feature} not found, using default`);
    return require("../components/fallbacks/FeatureUnavailableNotice").default;
  }
};

/**
 * Hook for using progressive enhancement in components
 */
export const useProgressiveEnhancement = () => {
  const [capabilities, setCapabilities] = useState<DeviceCapabilities | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    detectCapabilities().then((caps) => {
      setCapabilities(caps);
      setLoading(false);
    });
  }, []);

  const getComponent = useCallback(
    <T extends React.ComponentType<any>>(
      advancedComponent: T,
      feature: keyof FeatureFallbacks,
    ): T | React.ComponentType<any> => {
      if (loading || !capabilities) {
        return getFallbackUI(feature);
      }

      // Map features to capabilities
      const featureCapabilityMap: Record<
        keyof FeatureFallbacks,
        keyof Pick<
          DeviceCapabilities,
          | "hasFaceDetection"
          | "hasVoiceModulation"
          | "hasSpeechRecognition"
          | "hasAdvancedCamera"
          | "hasVideoProcessing"
        >
      > = {
        faceBlur: "hasFaceDetection",
        voiceChange: "hasVoiceModulation",
        liveCaptions: "hasSpeechRecognition",
        advancedCamera: "hasAdvancedCamera",
        videoEditor: "hasVideoProcessing",
      };

      const requiredCapability = featureCapabilityMap[feature];
      const booleanCapabilities = capabilities as Pick<
        DeviceCapabilities,
        "hasFaceDetection" | "hasVoiceModulation" | "hasSpeechRecognition" | "hasAdvancedCamera" | "hasVideoProcessing"
      >;
      return booleanCapabilities[requiredCapability] ? advancedComponent : getFallbackUI(feature);
    },
    [capabilities, loading],
  );

  return {
    capabilities,
    loading,
    getComponent,
    isFeatureAvailable: (feature: keyof DeviceCapabilities) => capabilities?.[feature] ?? false,
  };
};

// Singleton instance for global access
let globalCapabilities: DeviceCapabilities | null = null;

export const getGlobalCapabilities = async (): Promise<DeviceCapabilities> => {
  if (!globalCapabilities) {
    globalCapabilities = await detectCapabilities();
  }
  return globalCapabilities;
};

export const isFeatureGloballyAvailable = (feature: keyof DeviceCapabilities): boolean => {
  const value = globalCapabilities?.[feature];
  return typeof value === "boolean" ? value : false;
};

/**
 * Retry logic utilities with exponential backoff
 * Provides robust error handling for transient network errors
 */

import { isRetryableSupabaseError } from "../types/supabaseError";
// Global retry event subscription for lightweight UX signals
export type RetryEventSource = "supabase" | "api" | "unknown";
export interface RetryEvent {
  source: RetryEventSource;
  attempt: number;
  delay: number;
  error: unknown;
}

const retryListeners = new Set<(e: RetryEvent) => void>();
export const subscribeRetryEvents = (listener: (e: RetryEvent) => void) => {
  retryListeners.add(listener);
  return () => retryListeners.delete(listener);
};
const emitRetryEvent = (event: RetryEvent) => {
  retryListeners.forEach((l) => {
    try {
      l(event);
    } catch {
      // ignore listener errors
    }
  });
};

export interface RetryOptions {
  /** Maximum number of retry attempts (default: 3) */
  maxAttempts?: number;
  /** Initial delay in milliseconds (default: 1000) */
  initialDelay?: number;
  /** Maximum delay in milliseconds (default: 10000) */
  maxDelay?: number;
  /** Backoff multiplier (default: 2) */
  backoffMultiplier?: number;
  /** Jitter factor to add randomness (default: 0.1) */
  jitterFactor?: number;
  /** Function to determine if error should trigger retry */
  shouldRetry?: (error: unknown, attempt: number) => boolean;
  /** Callback for retry attempts */
  onRetry?: (error: unknown, attempt: number, delay: number) => void;
}

export interface RetryResult<T> {
  success: boolean;
  data?: T;
  error?: unknown;
  attempts: number;
  totalTime: number;
}

/**
 * Default retry condition - retries on network errors and 5xx status codes
 */
const defaultShouldRetry = (error: unknown, attempt: number): boolean => {
  if (attempt >= 3) return false;

  // Use Supabase error checking first
  if (isRetryableSupabaseError(error)) {
    return true;
  }

  // Retry on network errors
  if (error instanceof Error) {
    const message = error.message.toLowerCase();
    if (
      message.includes("network") ||
      message.includes("fetch") ||
      message.includes("timeout") ||
      message.includes("connection")
    ) {
      return true;
    }
  }

  // Retry on HTTP 5xx errors
  if (typeof error === "object" && error !== null && "status" in error) {
    const status = (error as any).status;
    return status >= 500 && status < 600;
  }

  return false;
};

/**
 * Calculate delay with exponential backoff and jitter
 */
const calculateDelay = (
  attempt: number,
  initialDelay: number,
  maxDelay: number,
  backoffMultiplier: number,
  jitterFactor: number,
): number => {
  const exponentialDelay = initialDelay * Math.pow(backoffMultiplier, attempt - 1);
  const cappedDelay = Math.min(exponentialDelay, maxDelay);

  // Add jitter to prevent thundering herd
  const jitter = cappedDelay * jitterFactor * Math.random();
  return Math.floor(cappedDelay + jitter);
};

/**
 * Sleep utility for delays
 */
const sleep = (ms: number): Promise<void> => new Promise((resolve) => setTimeout(resolve, ms));

/**
 * Retry an async operation with exponential backoff
 */
export const withRetry = async <T>(operation: () => Promise<T>, options: RetryOptions = {}): Promise<T> => {
  const {
    maxAttempts = 3,
    initialDelay = 1000,
    maxDelay = 10000,
    backoffMultiplier = 2,
    jitterFactor = 0.1,
    shouldRetry = defaultShouldRetry,
    onRetry,
  } = options;

  const _startTime = Date.now();
  let lastError: unknown;

  for (let attempt = 1; attempt <= maxAttempts; attempt++) {
    try {
      const result = await operation();
      return result;
    } catch (error) {
      lastError = error;

      // Don't retry on last attempt or if shouldn't retry
      if (attempt === maxAttempts || !shouldRetry(error, attempt)) {
        break;
      }

      const delay = calculateDelay(attempt, initialDelay, maxDelay, backoffMultiplier, jitterFactor);

      if (__DEV__) {
        console.warn(`[Retry] Attempt ${attempt}/${maxAttempts} failed, retrying in ${delay}ms:`, error);
      }

      onRetry?.(error, attempt, delay);
      await sleep(delay);
    }
  }

  throw lastError;
};

/**
 * Retry with detailed result information
 */
export const withRetryResult = async <T>(
  operation: () => Promise<T>,
  options: RetryOptions = {},
): Promise<RetryResult<T>> => {
  const startTime = Date.now();
  let attempts = 0;

  try {
    const data = await withRetry(async () => {
      attempts++;
      return await operation();
    }, options);

    return {
      success: true,
      data,
      attempts,
      totalTime: Date.now() - startTime,
    };
  } catch (error) {
    return {
      success: false,
      error,
      attempts,
      totalTime: Date.now() - startTime,
    };
  }
};

/**
 * Create a retry wrapper for Supabase operations
 */
export const createSupabaseRetry = (options: RetryOptions = {}) => {
  const onRetryCombined = (error: unknown, attempt: number, delay: number) => {
    emitRetryEvent({ source: "supabase", attempt, delay, error });
    options.onRetry?.(error, attempt, delay);
  };

  const supabaseOptions: RetryOptions = {
    maxAttempts: 3,
    initialDelay: 1000,
    maxDelay: 8000,
    shouldRetry: (error, attempt) => {
      // Custom Supabase retry logic
      if (attempt >= 3) return false;

      if (typeof error === "object" && error !== null) {
        const errorObj = error as any;

        // Retry on connection errors
        if (
          errorObj.message?.includes("Failed to fetch") ||
          errorObj.message?.includes("NetworkError") ||
          errorObj.message?.includes("timeout")
        ) {
          return true;
        }

        // Retry on specific Supabase error codes
        if (errorObj.code && ["PGRST301", "PGRST302", "08000", "08003", "08006"].includes(errorObj.code)) {
          return true;
        }

        // Retry on 5xx HTTP errors
        if (errorObj.status >= 500 && errorObj.status < 600) {
          return true;
        }
      }

      return false;
    },
    onRetry: onRetryCombined,
    ...options,
  };

  return <T>(operation: () => Promise<T>) => withRetry(operation, supabaseOptions);
};

/**
 * Create a retry wrapper for API calls
 */
export const createApiRetry = (options: RetryOptions = {}) => {
  const onRetryCombined = (error: unknown, attempt: number, delay: number) => {
    emitRetryEvent({ source: "api", attempt, delay, error });
    options.onRetry?.(error, attempt, delay);
  };

  const apiOptions: RetryOptions = {
    maxAttempts: 3,
    initialDelay: 500,
    maxDelay: 5000,
    shouldRetry: (error, attempt) => {
      if (attempt >= 3) return false;

      // Retry on network errors
      if (error instanceof Error) {
        const message = error.message.toLowerCase();
        if (message.includes("network") || message.includes("fetch") || message.includes("timeout")) {
          return true;
        }
      }

      // Retry on 5xx and some 4xx errors
      if (typeof error === "object" && error !== null && "status" in error) {
        const status = (error as any).status;
        return status >= 500 || status === 408 || status === 429;
      }

      return false;
    },
    onRetry: onRetryCombined,
    ...options,
  };

  return <T>(operation: () => Promise<T>) => withRetry(operation, apiOptions);
};

// Additional exports for video retry functionality
export interface RetryConfig {
  maxAttempts?: number;
  maxRetries?: number;
  backoffMs?: number[];
  initialDelay?: number;
  maxDelay?: number;
  backoffFactor?: number;
  shouldRetry?: (error: unknown, attempt: number) => boolean;
  onRetry?: (attempt: number, delay: number) => void;
}

export function createRetryableOperation<T>(operation: () => Promise<T>, config: RetryConfig): Promise<T> {
  const attempts = config.maxAttempts ?? config.maxRetries ?? config.backoffMs?.length ?? 3;
  const delays = config.backoffMs ?? [];
  const initialDelay = config.initialDelay ?? delays[0] ?? 1000;
  const maxDelay = config.maxDelay ?? delays[delays.length - 1] ?? Math.max(initialDelay * 4, initialDelay);
  const backoffMultiplier = config.backoffFactor ?? 2;

  return withRetry(operation, {
    maxAttempts: attempts,
    initialDelay,
    maxDelay,
    backoffMultiplier,
    shouldRetry: config.shouldRetry,
    onRetry: (error, attempt, delay) => config.onRetry?.(attempt, delay),
  });
}

