import * as FileSystem from "./legacyFileSystem";
import { supabase } from "../lib/supabase";

import { uploadVideoToSupabase } from "./storage";

export interface UploadResult {
  uploadId: string;
  status: "uploading" | "processing" | "completed" | "failed";
  processedVideoUrl?: string;
  thumbnailUrl?: string;
  transcription?: string;
  error?: string;
}

export interface UploadOptions {
  onProgress?: (progress: number, message: string) => void;
  enableFaceBlur?: boolean;
  enableVoiceChange?: boolean;
  enableTranscription?: boolean;
  quality?: "high" | "medium" | "low";
  voiceEffect?: "deep" | "light";
}

/**
 * Upload video anonymously to Supabase storage and initiate processing
 */
export const uploadVideoAnonymously = async (videoUri: string, options: UploadOptions = {}): Promise<UploadResult> => {
  const {
    onProgress,
    enableFaceBlur = true,
    enableVoiceChange = true,
    enableTranscription = true,
    quality = "medium",
    voiceEffect = "deep",
  } = options;

  try {
    onProgress?.(5, "Validating video file...");

    // Validate input file
    try {
      const fileInfo = await FileSystem.getInfoAsync(videoUri);
      if (!fileInfo.exists) {
        throw new Error("Video file does not exist");
      }

      if (fileInfo.size && fileInfo.size > 100 * 1024 * 1024) {
        // 100MB limit
        throw new Error("Video file is too large (max 100MB)");
      }
    } catch (error) {
      if (error instanceof Error && error.message.includes("too large")) {
        throw error;
      }
      throw new Error("Video file does not exist");
    }

    onProgress?.(10, "Preparing upload...");

    // Generate unique upload ID
    const uploadId = `upload_${Date.now()}_${Math.random().toString(36).substring(7)}`;
    const _fileName = `${uploadId}.mp4`;

    onProgress?.(20, "Uploading video...");

    // Require auth for private bucket upload; use streaming helper
    const { data: userData, error: userErr } = await supabase.auth.getUser();
    if (userErr || !userData?.user) {
      throw new Error("You must be signed in to upload video");
    }

    const upload = await uploadVideoToSupabase(videoUri, userData.user.id, {
      onProgress: (p: number) => onProgress?.(20 + (p * 0.2) / 100, "Uploading video..."),
    });

    onProgress?.(40, "Initiating processing...");

    // Call Edge Function for processing using storage path (no public URL)
    const { data: processData, error: processError } = await supabase.functions.invoke("process-video", {
      body: {
        uploadId,
        videoPath: upload.path,
        options: {
          enableFaceBlur,
          enableVoiceChange,
          enableTranscription,
          quality,
          voiceEffect,
        },
      },
    });

    if (processError) {
      console.error("Edge Function error:", processError);
      throw new Error(`Processing initiation failed: ${processError.message}`);
    }

    if (!processData) {
      throw new Error("No response data from Edge Function");
    }

    if (!processData.success) {
      throw new Error(`Processing initiation failed: ${processData.error || "Unknown error"}`);
    }

    onProgress?.(50, "Upload and processing initiated successfully");

    return {
      uploadId,
      status: "processing",
    };
  } catch (error) {
    console.error("Anonymous upload failed:", error);
    return {
      uploadId: "",
      status: "failed",
      error: error instanceof Error ? error.message : "Unknown upload error",
    };
  }
};

/**
 * Poll for processing status of an uploaded video
 * Uses storage-based status tracking for anonymous uploads
 */
export const pollProcessingStatus = async (
  uploadId: string,
  onProgress?: (progress: number, message: string) => void,
): Promise<UploadResult> => {
  const maxRetries = 60; // 5 minutes with 5-second intervals
  const pollInterval = 5000; // 5 seconds

  for (let attempt = 0; attempt < maxRetries; attempt++) {
    try {
      onProgress?.(50 + (attempt / maxRetries) * 40, `Checking status... (${attempt + 1}/${maxRetries})`);

      // Check for status file in storage
      const statusFileName = `status_${uploadId}.json`;
      const { data: statusData, error } = await supabase.storage.from("confessions").download(statusFileName);

      if (error) {
        if (error.message.includes("not found")) {
          // Status file doesn't exist yet, continue polling
          await new Promise((resolve) => setTimeout(resolve, pollInterval));
          continue;
        }
        throw error;
      }

      // Parse status from JSON file
      const statusText = await statusData.text();
      const status = JSON.parse(statusText);

      if (status.status === "completed") {
        onProgress?.(95, "Processing completed, downloading results...");

        return {
          uploadId,
          status: "completed",
          processedVideoUrl: status.processedVideoUrl,
          thumbnailUrl: status.thumbnailUrl,
          transcription: status.transcription,
        };
      } else if (status.status === "failed") {
        return {
          uploadId,
          status: "failed",
          error: status.error || "Processing failed",
        };
      }

      // Wait before next poll
      await new Promise((resolve) => setTimeout(resolve, pollInterval));
    } catch (error) {
      console.error(`Polling attempt ${attempt + 1} failed:`, error);
      // Continue polling on error
    }
  }

  return {
    uploadId,
    status: "failed",
    error: "Processing timeout - video processing took too long",
  };
};

/**
 * Download processed video and save locally
 */
export const downloadProcessedVideo = async (
  processedVideoUrl: string,
  onProgress?: (progress: number, message: string) => void,
): Promise<string> => {
  try {
    onProgress?.(5, "Starting download...");

    const { data: videoData, error: downloadError } = await supabase.storage
      .from("confessions")
      .download(processedVideoUrl);

    if (downloadError) {
      throw new Error(`Download failed: ${downloadError.message}`);
    }

    onProgress?.(50, "Saving video locally...");

    // Save to local filesystem
    const localUri = `${FileSystem.Paths.cache.uri}processed_${Date.now()}.mp4`;
    const arrayBuffer = await videoData.arrayBuffer();
    const base64 = btoa(String.fromCharCode(...new Uint8Array(arrayBuffer)));

    await FileSystem.writeAsStringAsync(localUri, base64, {
      encoding: "base64",
    });

    onProgress?.(100, "Download complete");

    return localUri;
  } catch (error) {
    console.error("Download failed:", error);
    throw new Error(`Failed to download processed video: ${error instanceof Error ? error.message : "Unknown error"}`);
  }
};

/**
 * Complete workflow: upload, poll, and download
 */
export const uploadAndProcessVideo = async (
  videoUri: string,
  options: UploadOptions = {},
): Promise<{ localUri: string; transcription?: string; thumbnailUrl?: string }> => {
  const { onProgress } = options;

  // Step 1: Upload video
  const uploadResult = await uploadVideoAnonymously(videoUri, options);

  if (uploadResult.status === "failed") {
    throw new Error(uploadResult.error || "Upload failed");
  }

  // Step 2: Poll for completion
  const finalResult = await pollProcessingStatus(uploadResult.uploadId, onProgress);

  if (finalResult.status === "failed") {
    throw new Error(finalResult.error || "Processing failed");
  }

  if (!finalResult.processedVideoUrl) {
    throw new Error("No processed video URL received");
  }

  // Step 3: Download processed video
  const localUri = await downloadProcessedVideo(finalResult.processedVideoUrl, onProgress);

  return {
    localUri,
    transcription: finalResult.transcription,
    thumbnailUrl: finalResult.thumbnailUrl,
  };
};

import * as FileSystem from "./legacyFileSystem";
import { Directory } from "expo-file-system";
import { LRUCache } from "./lruCache";
import { videoQualitySelector } from "../services/VideoQualitySelector";
import type { VideoQuality } from "../services/VideoQualitySelector";
import { videoPerformanceConfig, DevicePerformanceTier, NetworkQualityTier } from "../config/videoPerformance";
import NetInfo from "@react-native-community/netinfo";
import { environmentDetector } from "./environmentDetector";

const resolveCacheDirectory = (): string => {
  const fsAny = FileSystem as unknown as {
    cacheDirectory?: string | null;
    documentDirectory?: string | null;
    temporaryDirectory?: string | null;
    Paths?: { cache?: { uri?: string }; document?: { uri?: string } };
  };

  const candidate =
    fsAny.cacheDirectory ??
    fsAny.documentDirectory ??
    fsAny.temporaryDirectory ??
    fsAny.Paths?.cache?.uri ??
    fsAny.Paths?.document?.uri ??
    null;

  if (typeof candidate === "string" && candidate.length > 0) {
    return candidate.endsWith("/") ? candidate : `${candidate}/`;
  }

  throw new Error("Unable to resolve cache directory for video cache manager");
};

interface CacheEntry {
  uri: string;
  localPath: string;
  timestamp: number;
  size: number;
  accessCount: number;
  priority: "high" | "normal" | "low";
  lastAccessTime: number;
  predictedNextAccess?: number;
  quality?: "high" | "medium" | "low";
  contentType?: "thumbnail" | "preview" | "full";
  compressionRatio?: number;
  videoQuality?: "360p" | "720p" | "1080p";
  networkQuality?: NetworkQualityTier;
  deviceTier?: DevicePerformanceTier;
  variantUris?: Map<VideoQuality, string>;
}

interface CacheConfig {
  maxCacheSize: number;
  maxEntries: number;
  preloadLimit: number;
  cleanupThreshold: number;
  memoryPressureThreshold: number;
  compressionEnabled: boolean;
  intelligentPreload: boolean;
  cachePartitioning: boolean;
  idleCleanupInterval: number;
}

class VideoCacheManager {
  private cache: Map<string, CacheEntry> = new Map();
  private lruCache: LRUCache<CacheEntry>;
  private config: CacheConfig = {
    maxCacheSize: 500 * 1024 * 1024, // 500MB
    maxEntries: 100,
    preloadLimit: 5,
    cleanupThreshold: 0.9, // Start cleanup at 90% capacity
    memoryPressureThreshold: 0.8, // Reduce cache when memory usage > 80%
    compressionEnabled: true,
    intelligentPreload: true,
    cachePartitioning: true,
    idleCleanupInterval: 30000, // 30 seconds
  };
  private currentCacheSize = 0;
  private cacheDir = `${resolveCacheDirectory()}video_cache/`;
  private isCleaningUp = false;
  private viewingPatterns: Map<string, number[]> = new Map();
  private cacheHitRate = 0;
  private totalRequests = 0;
  private totalHits = 0;
  private idleCleanupTimer?: ReturnType<typeof setInterval>;
  private memoryMonitorTimer?: ReturnType<typeof setInterval>;
  private cachePartitions: Map<string, Map<string, CacheEntry>> = new Map();
  private deviceTier: DevicePerformanceTier = DevicePerformanceTier.MID;
  private networkQuality: NetworkQualityTier = NetworkQualityTier.FAIR;
  private qualityVariantCache: Map<string, Map<VideoQuality, string>> = new Map();
  private bandwidthAdaptiveDownloading = true;
  private networkListener: any = null;

  constructor(config?: Partial<CacheConfig>) {
    this.config = { ...this.config, ...config };

    // Initialize LRU cache for metadata
    this.lruCache = new LRUCache<CacheEntry>({
      maxSize: this.config.maxEntries,
      ttl: 24 * 60 * 60 * 1000, // 24 hours
      onEvict: (key, entry) => this.handleEviction(key, entry),
      getSizeOf: (entry) => entry.size,
    });

    // Initialize cache partitions with quality-aware partitioning
    this.cachePartitions.set("thumbnail", new Map());
    this.cachePartitions.set("preview", new Map());
    this.cachePartitions.set("full", new Map());
    this.cachePartitions.set("low_quality", new Map());
    this.cachePartitions.set("medium_quality", new Map());
    this.cachePartitions.set("high_quality", new Map());

    this.initializeCache();
    this.startBackgroundTasks();
    this.initializeDeviceAndNetwork();
  }

  async initialize() {
    await this.initializeCache();
    await this.initializeDeviceAndNetwork();
  }

  private async initializeCache() {
    try {
      // Create cache directory if it doesn't exist
      try {
        const dirInfo = await FileSystem.getInfoAsync(this.cacheDir);
        if (!dirInfo.exists) {
          new Directory(this.cacheDir).create({ intermediates: true });
        }
      } catch (error) {
        // Directory doesn't exist, create it
        new Directory(this.cacheDir).create({ intermediates: true });
      }

      // Create partition directories including quality-based partitions
      if (this.config.cachePartitioning) {
        for (const partition of ["thumbnail", "preview", "full", "low_quality", "medium_quality", "high_quality"]) {
          const partitionDir = `${this.cacheDir}${partition}/`;
          try {
            const partitionInfo = await FileSystem.getInfoAsync(partitionDir);
            if (!partitionInfo.exists) {
              new Directory(partitionDir).create({ intermediates: true });
            }
          } catch (error) {
            // Directory doesn't exist, create it
            new Directory(partitionDir).create({ intermediates: true });
          }
        }
      }

      // Load existing cache entries
      await this.loadCacheIndex();

      // Load viewing patterns
      await this.loadViewingPatterns();
    } catch (error) {
      console.error("Failed to initialize video cache:", error);
    }
  }

  private async loadCacheIndex() {
    try {
      const indexPath = `${this.cacheDir}index.json`;
      let indexExists = false;

      try {
        const indexInfo = await FileSystem.getInfoAsync(indexPath);
        indexExists = indexInfo.exists;
      } catch (error) {
        indexExists = false;
      }

      if (indexExists) {
        const indexContent = await FileSystem.readAsStringAsync(indexPath);
        const cacheData = JSON.parse(indexContent);

        for (const [key, entry] of Object.entries(cacheData)) {
          const cacheEntry = entry as any;

          // Restore Map from array if present
          if (cacheEntry.variantUris && Array.isArray(cacheEntry.variantUris)) {
            cacheEntry.variantUris = new Map<VideoQuality, string>(cacheEntry.variantUris as any);
          }

          this.cache.set(key, cacheEntry as CacheEntry);
          this.currentCacheSize += cacheEntry.size;
        }
      }
    } catch (error) {
      console.error("Failed to load cache index:", error);
    }
  }

  private async saveCacheIndex() {
    try {
      const indexPath = `${this.cacheDir}index.json`;
      const cacheData: Record<string, any> = {};

      for (const [key, entry] of this.cache) {
        // Convert Map to array for serialization
        const serializedEntry = {
          ...entry,
          variantUris: entry.variantUris ? Array.from(entry.variantUris.entries()) : undefined,
        };
        cacheData[key] = serializedEntry;
      }

      await FileSystem.writeAsStringAsync(indexPath, JSON.stringify(cacheData));
    } catch (error) {
      console.error("Failed to save cache index:", error);
    }
  }

  private generateCacheKey(uri: string): string {
    // Simple hash function for cache key
    let hash = 0;
    for (let i = 0; i < uri.length; i++) {
      const char = uri.charCodeAt(i);
      hash = (hash << 5) - hash + char;
      hash = hash & hash; // Convert to 32-bit integer
    }
    return Math.abs(hash).toString();
  }

  private async handleEviction(key: string, entry: CacheEntry) {
    try {
      await FileSystem.deleteAsync(entry.localPath, { idempotent: true });

      // Also evict quality variants if they exist
      if (entry.variantUris) {
        for (const [quality, variantPath] of entry.variantUris) {
          try {
            await FileSystem.deleteAsync(variantPath, { idempotent: true });
          } catch (error) {
            console.error(`Failed to evict variant ${quality}:`, error);
          }
        }
      }

      this.cache.delete(key);
      this.currentCacheSize -= entry.size;

      if (__DEV__) {
        console.log(`[VideoCache] Evicted ${key}, freed ${(entry.size / 1024 / 1024).toFixed(2)}MB`);
      }
    } catch (error) {
      console.error("Failed to evict cache entry:", error);
    }
  }

  private async evictLeastRecentlyUsed() {
    if (this.isCleaningUp) return;
    this.isCleaningUp = true;

    try {
      // Use smart eviction strategy
      await this.smartEviction();
    } finally {
      this.isCleaningUp = false;
    }

    await this.saveCacheIndex();
  }

  private async smartEviction() {
    const perfConfig = videoPerformanceConfig.getCacheConfig();
    const targetSize = perfConfig.maxCacheSize * 0.7; // Target 70% capacity based on device tier
    const entries = Array.from(this.cache.entries());

    // Sort by priority, quality, and access patterns
    const sortedEntries = entries.sort(([, a], [, b]) => {
      // Priority-based sorting
      const priorityWeight = { high: 3, normal: 2, low: 1 };
      const aPriority = priorityWeight[a.priority];
      const bPriority = priorityWeight[b.priority];

      if (aPriority !== bPriority) {
        return bPriority - aPriority; // Higher priority first (keep)
      }

      // Quality-based sorting (keep higher quality for high-tier devices)
      if (this.deviceTier === DevicePerformanceTier.HIGH && a.videoQuality && b.videoQuality) {
        const qualityOrder = { "1080p": 3, "720p": 2, "360p": 1 };
        const aQuality = qualityOrder[a.videoQuality] || 0;
        const bQuality = qualityOrder[b.videoQuality] || 0;
        if (aQuality !== bQuality) {
          return bQuality - aQuality;
        }
      }

      // Calculate normalized recency score (0-1, where 1 is most recent)
      const now = Date.now();
      const maxAge = Math.max(...entries.map(([, entry]) => now - entry.timestamp), 1);
      const aRecency = 1 - (now - a.timestamp) / maxAge;
      const bRecency = 1 - (now - b.timestamp) / maxAge;

      // Calculate normalized access frequency score (0-1)
      const maxAccess = Math.max(...entries.map(([, entry]) => entry.accessCount), 1);
      const aFrequency = a.accessCount / maxAccess;
      const bFrequency = b.accessCount / maxAccess;

      // Combined score with proper weighting
      const aScore = aFrequency * 0.6 + aRecency * 0.4;
      const bScore = bFrequency * 0.6 + bRecency * 0.4;

      return aScore - bScore; // Lower score first (evict)
    });

    // Evict entries until we reach target size
    for (const [key, entry] of sortedEntries) {
      if (this.currentCacheSize <= targetSize) break;

      // Evict lower quality variants first for popular videos
      if (entry.variantUris && entry.accessCount > 5) {
        const variants = Array.from(entry.variantUris.entries());
        for (const [quality, path] of variants) {
          if (quality === "360p" && this.deviceTier !== DevicePerformanceTier.LOW) {
            try {
              const fileInfo = await FileSystem.getInfoAsync(path);
              if (fileInfo.exists) {
                await FileSystem.deleteAsync(path, { idempotent: true });
                entry.variantUris.delete(quality);
                this.currentCacheSize -= (fileInfo as any).size || 0;
              }
            } catch (error) {
              console.error("Failed to evict quality variant:", error);
            }
          }
        }
      }

      if (this.currentCacheSize <= targetSize) break;
      await this.handleEviction(key, entry);
    }
  }

  async getCachedVideo(uri: string, priority: "high" | "normal" | "low" = "normal"): Promise<string | null> {
    const originalKey = this.generateCacheKey(uri);
    let entry = this.cache.get(originalKey);
    let cacheKey = originalKey;
    let selectedQuality: VideoQuality | null = null;

    if (!entry) {
      const qualityResult = await videoQualitySelector.selectVideoQuality(uri);
      selectedQuality = qualityResult.selectedQuality as VideoQuality;
      const qualityUri = videoQualitySelector.getQualityForUri(uri, selectedQuality);
      const qualityKey = this.generateCacheKey(qualityUri);
      entry = this.cache.get(qualityKey);
      if (entry) {
        cacheKey = qualityKey;
      }
    }

    this.totalRequests++;

    if (entry) {
      // Update access information
      const now = Date.now();
      entry.timestamp = now;
      entry.lastAccessTime = now;
      entry.accessCount++;
      entry.priority = priority; // Update priority based on current usage
      entry.deviceTier = this.deviceTier;
      entry.networkQuality = this.networkQuality;

      // Track hit rate
      this.totalHits++;
      this.cacheHitRate = this.totalHits / this.totalRequests;

      // Update viewing patterns for intelligent preloading
      if (this.config.intelligentPreload) {
        this.updateViewingPattern(uri, now);
      }

      // Update existing entry, don't create duplicate under new key
      const existingKey = Array.from(this.cache.entries()).find(([_, e]) => e.localPath === entry!.localPath)?.[0];

      if (existingKey) {
        cacheKey = existingKey;
        this.cache.set(existingKey, entry);
        this.lruCache.set(existingKey, entry);
      } else {
        cacheKey = this.generateCacheKey(uri);
        this.cache.set(cacheKey, entry);
        this.lruCache.set(cacheKey, entry);
      }

      // Check for quality variant if available
      const desiredQuality = selectedQuality ?? (videoPerformanceConfig.getQualitySelection().quality as VideoQuality);
      if (entry.variantUris && entry.variantUris.has(desiredQuality)) {
        const variantPath = entry.variantUris.get(desiredQuality)!;
        try {
          const variantInfo = await FileSystem.getInfoAsync(variantPath);
          if (variantInfo.exists) {
            return variantPath;
          }
        } catch (error) {
          // Variant file doesn't exist, continue to original
        }
      }

      // Verify original file still exists
      try {
        const fileInfo = await FileSystem.getInfoAsync(entry.localPath);
        if (fileInfo.exists) {
          // Trigger cleanup if we're approaching capacity
          if (this.currentCacheSize > this.config.maxCacheSize * this.config.cleanupThreshold) {
            // Don't await - run cleanup in background
            this.evictLeastRecentlyUsed().catch((error) => {
              console.error("Background cleanup failed:", error);
            });
          }

          return entry.localPath;
        }
      } catch (error) {
        // File was deleted externally, remove from cache
        this.cache.delete(cacheKey);
        this.lruCache.delete(cacheKey);
        this.currentCacheSize -= entry.size;
      }
    }

    return null;
  }

  async cacheVideo(uri: string, priority: "high" | "normal" | "low" = "normal"): Promise<string> {
    // Get quality selection for this video
    const qualityResult = await videoQualitySelector.selectVideoQuality(uri);
    const targetQuality = qualityResult.selectedQuality;
    const variants = qualityResult.variants;

    // Use quality-specific URI if available
    const targetUri = variants.find((v) => v.quality === targetQuality)?.uri || uri;
    const cacheKey = this.generateCacheKey(targetUri);
    const existingEntry = await this.getCachedVideo(targetUri, priority);

    if (existingEntry) {
      return existingEntry;
    }

    try {
      const fileName = `${cacheKey}_${targetQuality}.mp4`;
      const qualityPartition = this.getQualityPartition(targetQuality);
      const localPath = `${this.cacheDir}${qualityPartition}/${fileName}`;

      // Apply bandwidth-adaptive downloading
      if (this.bandwidthAdaptiveDownloading && this.networkQuality === NetworkQualityTier.POOR) {
        // Use lower quality for poor networks
        const fallbackUri = variants.find((v) => v.quality === "360p")?.uri || targetUri;
        const downloadResult = await FileSystem.downloadAsync(fallbackUri, localPath);

        if (downloadResult.status === 200) {
          return await this.processCachedVideo(fallbackUri, localPath, priority, "360p", qualityResult);
        }
      }

      // Download the video with retry logic for quality fallback
      let downloadResult = await FileSystem.downloadAsync(targetUri, localPath);

      if (downloadResult.status !== 200 && qualityResult.fallbackQuality) {
        // Try fallback quality
        const fallbackUri = variants.find((v) => v.quality === qualityResult.fallbackQuality)?.uri || uri;
        downloadResult = await FileSystem.downloadAsync(fallbackUri, localPath);
      }

      if (downloadResult.status === 200) {
        return await this.processCachedVideo(targetUri, localPath, priority, targetQuality, qualityResult);
      } else {
        throw new Error(`Download failed with status: ${downloadResult.status}`);
      }
    } catch (error) {
      console.error("Failed to cache video:", error);
      return uri; // Return original URI as fallback
    }
  }

  private async processCachedVideo(
    uri: string,
    localPath: string,
    priority: "high" | "normal" | "low",
    quality: "360p" | "720p" | "1080p",
    qualityResult: any,
  ): Promise<string> {
    let fileSize = 0;
    try {
      const fileInfo = await FileSystem.getInfoAsync(localPath);
      fileSize = fileInfo.exists && !fileInfo.isDirectory ? (fileInfo as any).size || 0 : 0;
    } catch (error) {
      fileSize = 0;
    }

    // Check if we need to evict old entries
    if (this.currentCacheSize + fileSize > this.config.maxCacheSize) {
      await this.evictLeastRecentlyUsed();
    }

    const cacheKey = this.generateCacheKey(uri);

    // Add to cache with quality information
    const entry: CacheEntry = {
      uri,
      localPath,
      timestamp: Date.now(),
      size: fileSize,
      accessCount: 1,
      priority,
      lastAccessTime: Date.now(),
      videoQuality: quality,
      deviceTier: this.deviceTier,
      networkQuality: this.networkQuality,
      variantUris: new Map<VideoQuality, string>(),
    };

    // Store quality variants if caching multiple qualities
    if (videoPerformanceConfig.shouldEnableFeature("multiQualityCaching")) {
      for (const variant of qualityResult.variants) {
        if (variant.quality !== quality) {
          // Queue background caching of other variants
          this.cacheQualityVariant(uri, variant.quality, variant.uri, cacheKey);
        }
      }
    }

    this.cache.set(cacheKey, entry);
    this.lruCache.set(cacheKey, entry);
    this.currentCacheSize += fileSize;
    await this.saveCacheIndex();

    return localPath;
  }

  private getQualityPartition(quality: "360p" | "720p" | "1080p"): string {
    const qualityMap = {
      "360p": "low_quality",
      "720p": "medium_quality",
      "1080p": "high_quality",
    };
    return qualityMap[quality] || "medium_quality";
  }

  private async cacheQualityVariant(
    originalUri: string,
    quality: "360p" | "720p" | "1080p",
    variantUri: string,
    parentCacheKey: string,
  ): Promise<void> {
    try {
      const variantKey = `${parentCacheKey}_${quality}`;
      const fileName = `${variantKey}.mp4`;
      const qualityPartition = this.getQualityPartition(quality);
      const localPath = `${this.cacheDir}${qualityPartition}/${fileName}`;

      // Use background queue for variant caching
      import("../services/VideoBackgroundQueue")
        .then(({ videoBackgroundQueue, JobType, JobPriority }) => {
          videoBackgroundQueue.enqueueJob(
            JobType.QUALITY_VARIANT_GENERATION,
            {
              originalUri,
              quality,
              variantUri,
              parentCacheKey,
              localPath,
            },
            JobPriority.LOW,
            {
              onComplete: async (result) => {
                if (result.success) {
                  try {
                    const downloadResult = await FileSystem.downloadAsync(variantUri, localPath);
                    if (downloadResult.status === 200) {
                      const entry = this.cache.get(parentCacheKey);
                      if (entry && entry.variantUris) {
                        entry.variantUris.set(quality as VideoQuality, localPath);
                        this.cache.set(parentCacheKey, entry);
                      }
                    }
                  } catch (error) {
                    console.error(`Failed to cache quality variant ${quality}:`, error);
                  }
                }
              },
            },
          );
        })
        .catch((error) => {
          console.error("Failed to load background queue:", error);
        });
    } catch (error) {
      console.error("Failed to queue quality variant caching:", error);
    }
  }

  async clearCache(): Promise<void> {
    try {
      await FileSystem.deleteAsync(this.cacheDir, { idempotent: true });
      this.cache.clear();
      this.currentCacheSize = 0;
      await this.initializeCache();
    } catch (error) {
      console.error("Failed to clear cache:", error);
    }
  }

  getCacheSize(): number {
    return this.currentCacheSize;
  }

  private startBackgroundTasks() {
    // Clear any existing timers/listeners first
    this.clearBackgroundTasks();

    // Start idle cleanup with device-aware intervals
    const perfConfig = videoPerformanceConfig.getCacheConfig();
    const cleanupInterval = perfConfig.cleanupInterval;

    if (cleanupInterval > 0) {
      this.idleCleanupTimer = setInterval(() => {
        this.performIdleCleanup();
      }, cleanupInterval);
    }

    // Start memory pressure monitoring
    this.memoryMonitorTimer = setInterval(() => {
      this.checkMemoryPressure();
    }, 5000); // Check every 5 seconds

    // Start network monitoring for adaptive caching
    this.networkListener = NetInfo.addEventListener((state) => {
      this.handleNetworkChange(state);
    });
  }

  private clearBackgroundTasks() {
    if (this.idleCleanupTimer) {
      clearInterval(this.idleCleanupTimer);
      this.idleCleanupTimer = undefined;
    }
    if (this.memoryMonitorTimer) {
      clearInterval(this.memoryMonitorTimer);
      this.memoryMonitorTimer = undefined;
    }
    if (this.networkListener) {
      this.networkListener();
      this.networkListener = null;
    }
  }

  private async performIdleCleanup() {
    if (this.isCleaningUp) return;

    try {
      // Clean up old temporary files
      const now = Date.now();
      const maxAge = 2 * 60 * 60 * 1000; // 2 hours

      for (const [key, entry] of this.cache.entries()) {
        if (now - entry.lastAccessTime > maxAge && entry.priority === "low") {
          await this.handleEviction(key, entry);
        }
      }

      // Optimize cache based on access patterns
      if (this.config.intelligentPreload) {
        await this.optimizeCacheBasedOnPatterns();
      }
    } catch (error) {
      console.error("Idle cleanup failed:", error);
    }
  }

  private async checkMemoryPressure() {
    // Get actual memory info from environment detector
    const memoryInfo = await environmentDetector.getMemoryInfo();
    const memoryPressure = (memoryInfo.totalMemory - memoryInfo.availableMemory) / memoryInfo.totalMemory;

    // Use device-aware memory pressure thresholds
    const perfConfig = videoPerformanceConfig.getCacheConfig();

    if (memoryPressure > perfConfig.memoryPressureThreshold) {
      // Progressive cache degradation based on device tier
      const reductionRatio = this.deviceTier === DevicePerformanceTier.LOW ? 0.3 : 0.5;
      await this.reduceCache(reductionRatio);

      // Reduce quality variants under pressure
      if (memoryPressure > 0.9) {
        await this.evictQualityVariants();
      }
    }
  }

  private async evictQualityVariants(): Promise<void> {
    for (const [key, entry] of this.cache.entries()) {
      if (entry.variantUris && entry.variantUris.size > 0) {
        // Keep only the most appropriate quality for current conditions
        const targetQuality = await this.getOptimalQualityForCurrentConditions();

        for (const [quality, path] of entry.variantUris) {
          if (quality !== targetQuality) {
            try {
              await FileSystem.deleteAsync(path, { idempotent: true });
              entry.variantUris.delete(quality);
            } catch (error) {
              console.error(`Failed to evict quality variant ${quality}:`, error);
            }
          }
        }
      }
    }
  }

  private async getOptimalQualityForCurrentConditions(): Promise<"360p" | "720p" | "1080p"> {
    const qualitySelection = videoPerformanceConfig.getQualitySelection();
    return qualitySelection.quality as "360p" | "720p" | "1080p";
  }

  private async reduceCache(targetRatio: number) {
    const targetSize = this.config.maxCacheSize * targetRatio;

    while (this.currentCacheSize > targetSize && this.cache.size > 0) {
      // Remove lowest priority items first
      const entries = Array.from(this.cache.entries());
      const lowestPriority = entries.sort((a, b) => {
        const priorityMap = { low: 0, normal: 1, high: 2 };
        return priorityMap[a[1].priority] - priorityMap[b[1].priority];
      })[0];

      if (lowestPriority) {
        await this.handleEviction(lowestPriority[0], lowestPriority[1]);
      } else {
        break;
      }
    }
  }

  private updateViewingPattern(uri: string, timestamp: number) {
    const patterns = this.viewingPatterns.get(uri) || [];
    patterns.push(timestamp);

    // Keep only recent patterns (last 10 accesses)
    if (patterns.length > 10) {
      patterns.shift();
    }

    this.viewingPatterns.set(uri, patterns);

    // Predict next access time
    if (patterns.length >= 2) {
      const intervals = [];
      for (let i = 1; i < patterns.length; i++) {
        intervals.push(patterns[i] - patterns[i - 1]);
      }
      const avgInterval = intervals.reduce((a, b) => a + b, 0) / intervals.length;

      // Update entry with predicted next access
      const entry = this.cache.get(this.generateCacheKey(uri));
      if (entry) {
        entry.predictedNextAccess = timestamp + avgInterval;
      }
    }
  }

  private async optimizeCacheBasedOnPatterns() {
    const now = Date.now();
    const upcomingVideos: string[] = [];

    // Find videos likely to be accessed soon
    for (const [key, entry] of this.cache.entries()) {
      if (
        entry.predictedNextAccess &&
        entry.predictedNextAccess - now < 60000 && // Within next minute
        entry.predictedNextAccess > now
      ) {
        upcomingVideos.push(entry.uri);
      }
    }

    // Preload predicted videos
    if (upcomingVideos.length > 0) {
      await this.warmCache(upcomingVideos.slice(0, 3)); // Warm up to 3 videos
    }
  }

  async warmCache(uris: string[]) {
    for (const uri of uris) {
      const cacheKey = this.generateCacheKey(uri);
      const entry = this.cache.get(cacheKey);

      if (entry) {
        // Touch the entry to keep it warm
        entry.lastAccessTime = Date.now();
        this.lruCache.set(cacheKey, entry);
      }
    }
  }

  private async loadViewingPatterns() {
    try {
      const patternsPath = `${this.cacheDir}patterns.json`;
      let patternsExist = false;

      try {
        const patternsInfo = await FileSystem.getInfoAsync(patternsPath);
        patternsExist = patternsInfo.exists;
      } catch (error) {
        patternsExist = false;
      }

      if (patternsExist) {
        const patternsContent = await FileSystem.readAsStringAsync(patternsPath);
        const patterns = JSON.parse(patternsContent);
        this.viewingPatterns = new Map(Object.entries(patterns));
      }
    } catch (error) {
      console.error("Failed to load viewing patterns:", error);
    }
  }

  private async saveViewingPatterns() {
    try {
      const patternsPath = `${this.cacheDir}patterns.json`;
      const patterns = Object.fromEntries(this.viewingPatterns);
      await FileSystem.writeAsStringAsync(patternsPath, JSON.stringify(patterns));
    } catch (error) {
      console.error("Failed to save viewing patterns:", error);
    }
  }

  getCacheStats(): {
    size: number;
    count: number;
    maxSize: number;
    hitRate: number;
    priorityBreakdown: Record<string, number>;
    oldestEntry: number;
    newestEntry: number;
    averageAccessFrequency: number;
    storageEfficiency: number;
    predictiveAccuracy?: number;
  } {
    const lruStats = this.lruCache.getStats();
    const entries = Array.from(this.cache.values());

    const priorityBreakdown = entries.reduce(
      (acc, entry) => {
        acc[entry.priority] = (acc[entry.priority] || 0) + 1;
        return acc;
      },
      {} as Record<string, number>,
    );

    const timestamps = entries.map((e) => e.timestamp);

    const avgAccessFreq = entries.length > 0 ? entries.reduce((sum, e) => sum + e.accessCount, 0) / entries.length : 0;

    const storageEfficiency = this.config.maxCacheSize > 0 ? this.currentCacheSize / this.config.maxCacheSize : 0;

    return {
      size: this.currentCacheSize,
      count: this.cache.size,
      maxSize: this.config.maxCacheSize,
      hitRate: this.cacheHitRate,
      priorityBreakdown,
      oldestEntry: timestamps.length > 0 ? Math.min(...timestamps) : 0,
      newestEntry: timestamps.length > 0 ? Math.max(...timestamps) : 0,
      averageAccessFrequency: avgAccessFreq,
      storageEfficiency,
    };
  }

  /**
   * Preload videos with device-aware and network-aware support
   */
  async preloadVideos(uris: string[], priority: "high" | "normal" | "low" = "normal"): Promise<void> {
    // Get device-specific preload configuration
    const preloadProfile = videoPerformanceConfig.getPreloadProfile();
    const maxConcurrent = preloadProfile.maxConcurrentPreloads;
    const preloadWindow = Math.min(uris.length, preloadProfile.preloadWindowSize);

    // Filter URIs based on preload window
    const urisToPreload = uris.slice(0, preloadWindow);

    // Apply network-aware preloading
    if (this.networkQuality === NetworkQualityTier.POOR) {
      // Reduce preload for poor networks
      const reducedUris = urisToPreload.slice(0, Math.ceil(preloadWindow / 2));
      return this.preloadVideosWithConcurrency(reducedUris, priority, Math.max(1, maxConcurrent / 2));
    }

    // Check if aggressive preloading is enabled for this device tier
    if (videoPerformanceConfig.shouldEnableFeature("aggressivePreloading")) {
      // Preload with higher concurrency for high-tier devices
      return this.preloadVideosWithConcurrency(urisToPreload, priority, maxConcurrent * 2);
    }

    return this.preloadVideosWithConcurrency(urisToPreload, priority, maxConcurrent);
  }

  private async preloadVideosWithConcurrency(
    uris: string[],
    priority: "high" | "normal" | "low",
    concurrency: number,
  ): Promise<void> {
    // Limit concurrent preloads to prevent overwhelming the system
    const chunks = [];
    for (let i = 0; i < uris.length; i += concurrency) {
      chunks.push(uris.slice(i, i + concurrency));
    }

    for (const chunk of chunks) {
      const preloadPromises = chunk.map((uri) =>
        this.cacheVideo(uri, priority).catch((error) => {
          console.error(`Failed to preload video ${uri}:`, error);
        }),
      );

      await Promise.allSettled(preloadPromises);
    }
  }

  /**
   * Update cache configuration with device-aware settings
   */
  updateConfig(newConfig?: Partial<CacheConfig>): void {
    // Get device-specific configuration
    const perfConfig = videoPerformanceConfig.getCacheConfig();

    // Merge device-specific config with provided config
    this.config = {
      ...this.config,
      maxCacheSize: perfConfig.maxCacheSize,
      cleanupThreshold: perfConfig.memoryPressureThreshold,
      ...newConfig,
    };

    // Clear and restart background tasks with new config
    this.clearBackgroundTasks();
    this.startBackgroundTasks();

    // Update LRU cache limits
    if (newConfig?.maxEntries || newConfig?.maxCacheSize) {
      // Note: LRUCache doesn't support dynamic config updates
      // In a real implementation, you might need to recreate the cache
      console.warn("Cache config updated, consider restarting the cache for full effect");
    }
  }

  /**
   * Force cleanup of expired and low-priority entries
   */
  async forceCleanup(): Promise<{ removedCount: number; freedSpace: number }> {
    const initialSize = this.currentCacheSize;
    const initialCount = this.cache.size;

    // Clean up expired entries from LRU cache
    const _expiredRemoved = this.lruCache.cleanup();

    // Force eviction if still over threshold
    if (this.currentCacheSize > this.config.maxCacheSize * 0.8) {
      await this.evictLeastRecentlyUsed();
    }

    const freedSpace = initialSize - this.currentCacheSize;
    const removedCount = initialCount - this.cache.size;

    return { removedCount, freedSpace };
  }

  /**
   * Cleanup on destroy
   */
  destroy() {
    this.clearBackgroundTasks();
    this.saveViewingPatterns();
    this.saveCacheIndex();
  }

  /**
   * Initialize device and network detection
   */
  private async initializeDeviceAndNetwork(): Promise<void> {
    try {
      // Detect device tier
      const deviceInfo = await environmentDetector.getDeviceInfo();
      const memoryInfo = await environmentDetector.getMemoryInfo();

      const totalMemoryGB = memoryInfo.totalMemory / (1024 * 1024 * 1024);
      if (totalMemoryGB >= 6) {
        this.deviceTier = DevicePerformanceTier.HIGH;
      } else if (totalMemoryGB >= 4) {
        this.deviceTier = DevicePerformanceTier.MID;
      } else {
        this.deviceTier = DevicePerformanceTier.LOW;
      }

      videoPerformanceConfig.setDeviceTier(this.deviceTier);

      // Detect initial network quality
      const netInfo = await NetInfo.fetch();
      this.updateNetworkQuality(netInfo);

      // Update cache config based on device tier
      this.updateConfig();
    } catch (error) {
      console.error("Failed to initialize device and network detection:", error);
    }
  }

  /**
   * Handle network changes
   */
  private handleNetworkChange(state: any): void {
    this.updateNetworkQuality(state);

    // Adjust cache strategy based on network change
    if (this.networkQuality === NetworkQualityTier.POOR) {
      // Reduce cleanup interval on poor networks
      if (this.idleCleanupTimer) {
        clearInterval(this.idleCleanupTimer);
        this.idleCleanupTimer = setInterval(() => {
          this.performIdleCleanup();
        }, 60000); // Faster cleanup on poor networks
      }
    }
  }

  /**
   * Update network quality based on connection info
   */
  private updateNetworkQuality(state: any): void {
    if (!state.isConnected) {
      this.networkQuality = NetworkQualityTier.POOR;
      return;
    }

    const type = state.type?.toLowerCase();
    const effectiveType = state.details?.cellularGeneration?.toLowerCase() || type;

    if (effectiveType === "wifi" || effectiveType === "5g") {
      this.networkQuality = NetworkQualityTier.EXCELLENT;
    } else if (effectiveType === "4g") {
      this.networkQuality = NetworkQualityTier.GOOD;
    } else if (effectiveType === "3g") {
      this.networkQuality = NetworkQualityTier.FAIR;
    } else {
      this.networkQuality = NetworkQualityTier.POOR;
    }

    videoPerformanceConfig.setNetworkQuality(this.networkQuality);
  }

  /**
   * Get device-aware cache statistics
   */
  getDeviceAwareCacheStats(): any {
    const baseStats = this.getCacheStats();
    return {
      ...baseStats,
      deviceTier: this.deviceTier,
      networkQuality: this.networkQuality,
      qualityVariants: this.getQualityVariantStats(),
      adaptiveSettings: {
        currentPreloadLimit: videoPerformanceConfig.getPreloadProfile().maxConcurrentPreloads,
        currentMemoryLimit: videoPerformanceConfig.getPreloadProfile().memoryLimit,
        currentCleanupInterval: videoPerformanceConfig.getCacheConfig().cleanupInterval,
      },
    };
  }

  private getQualityVariantStats(): any {
    let variantCount = 0;
    let variantSize = 0;
    const qualityDistribution: Record<string, number> = {};

    for (const entry of this.cache.values()) {
      if (entry.variantUris) {
        variantCount += entry.variantUris.size;
        for (const quality of entry.variantUris.keys()) {
          qualityDistribution[quality] = (qualityDistribution[quality] || 0) + 1;
        }
      }
    }

    return {
      count: variantCount,
      size: variantSize,
      distribution: qualityDistribution,
    };
  }
}

export const videoCacheManager = new VideoCacheManager();

import { VideoError, VideoErrorType } from "../types/videoErrors";
import { VideoErrorRecoveryService } from "../services/VideoErrorRecoveryService";
import NetInfo, { NetInfoState } from "@react-native-community/netinfo";
import * as Device from "expo-device";

export type ErrorSeverity = "low" | "medium" | "high" | "critical";
export type ErrorContext = "loading" | "playback" | "network" | "format" | "permission";

export interface UserFriendlyError {
  title: string;
  message: string;
  actionText?: string;
  secondaryActionText?: string;
  severity: ErrorSeverity;
  context: ErrorContext;
  technicalDetails?: string;
  retryStrategy: RetryStrategy;
}

export interface RetryStrategy {
  shouldRetry: boolean;
  maxRetries: number;
  backoffMs: number[];
  explanation: string;
}

export interface ErrorAnalytics {
  errorCode: string;
  errorType: VideoErrorType;
  deviceInfo: {
    model: string | null;
    osVersion: string | null;
    memory: number | null;
  };
  networkInfo?: NetInfoState;
  timestamp: number;
  attemptNumber: number;
}

export class VideoErrorMessages {
  private static retryAttempts: Map<string, number> = new Map();
  private static errorHistory: ErrorAnalytics[] = [];
  private static maxHistorySize = 50;

  static getUserFriendlyError(
    error: VideoError,
    attemptNumber: number = 1,
    userState?: { isOffline?: boolean; isLowBandwidth?: boolean },
  ): UserFriendlyError {
    const errorKey = `${error.type}-${error.code}`;
    this.retryAttempts.set(errorKey, attemptNumber);

    if (userState?.isOffline) {
      return this.getOfflineError();
    }

    switch (error.type) {
      case VideoErrorType.NETWORK:
        return this.getNetworkError(error, attemptNumber, userState?.isLowBandwidth);

      case VideoErrorType.DECODE:
      case VideoErrorType.FORMAT:
        return this.getFormatError(error, attemptNumber);

      case VideoErrorType.PERMISSION:
        return this.getPermissionError(error);

      case VideoErrorType.MEMORY:
        return this.getMemoryError(error, attemptNumber);

      case VideoErrorType.SERVER:
        return this.getServerError(error, attemptNumber);

      case VideoErrorType.TIMEOUT:
        return this.getTimeoutError(error, attemptNumber);

      case VideoErrorType.UNKNOWN:
      default:
        return this.getGenericError(error, attemptNumber);
    }
  }

  private static getOfflineError(): UserFriendlyError {
    return {
      title: "You're offline",
      message: "Connect to the internet to load videos",
      actionText: "Check settings",
      secondaryActionText: "View downloaded",
      severity: "medium",
      context: "network",
      retryStrategy: {
        shouldRetry: true,
        maxRetries: Infinity,
        backoffMs: [1000, 2000, 5000],
        explanation: "Will retry automatically when connection restored",
      },
    };
  }

  private static getNetworkError(
    error: VideoError,
    attemptNumber: number,
    isLowBandwidth?: boolean,
  ): UserFriendlyError {
    if (isLowBandwidth) {
      return {
        title: "Slow connection detected",
        message: "Video quality adjusted for your connection speed",
        actionText: "Load anyway",
        secondaryActionText: "Settings",
        severity: "low",
        context: "network",
        technicalDetails: error.debugInfo,
        retryStrategy: {
          shouldRetry: true,
          maxRetries: 5,
          backoffMs: [500, 1000, 2000, 4000, 8000],
          explanation: "Retrying with lower quality settings",
        },
      };
    }

    const messages = [
      "Having trouble connecting. Let's try again!",
      "Connection hiccup. Trying once more...",
      "Network is being stubborn. One more attempt...",
      "Connection issues persist. Last try!",
    ];

    const messageIndex = Math.min(attemptNumber - 1, messages.length - 1);

    return {
      title: attemptNumber === 1 ? "Connection issue" : "Still having trouble",
      message: messages[messageIndex],
      actionText: "Retry now",
      secondaryActionText: attemptNumber > 2 ? "Report issue" : undefined,
      severity: attemptNumber > 3 ? "high" : "medium",
      context: "network",
      technicalDetails: error.debugInfo,
      retryStrategy: {
        shouldRetry: attemptNumber < 4,
        maxRetries: 4,
        backoffMs: [1000, 2000, 4000, 8000],
        explanation: `Retry ${attemptNumber}/4 with exponential backoff`,
      },
    };
  }

  private static getFormatError(error: VideoError, attemptNumber: number): UserFriendlyError {
    const isCodecIssue = error.debugInfo?.includes("codec") || error.debugInfo?.includes("h264");

    return {
      title: "Video format issue",
      message: isCodecIssue ? "This video format isn't supported on your device" : "Unable to play this video format",
      actionText: attemptNumber === 1 ? "Try different quality" : "Skip video",
      secondaryActionText: "Report problem",
      severity: "medium",
      context: "format",
      technicalDetails: error.debugInfo,
      retryStrategy: {
        shouldRetry: attemptNumber === 1 && !isCodecIssue,
        maxRetries: 1,
        backoffMs: [500],
        explanation: isCodecIssue ? "Device doesn't support this codec" : "Trying alternative format",
      },
    };
  }

  private static getPermissionError(error: VideoError): UserFriendlyError {
    return {
      title: "Permission needed",
      message: "Allow app to access media to play videos",
      actionText: "Open settings",
      secondaryActionText: "Learn why",
      severity: "high",
      context: "permission",
      technicalDetails: error.debugInfo,
      retryStrategy: {
        shouldRetry: false,
        maxRetries: 0,
        backoffMs: [],
        explanation: "User needs to grant permissions manually",
      },
    };
  }

  private static getMemoryError(error: VideoError, attemptNumber: number): UserFriendlyError {
    const deviceMemory = Device.totalMemory;
    const isLowEndDevice = deviceMemory && deviceMemory < 2 * 1024 * 1024 * 1024;

    return {
      title: "Memory issue",
      message: isLowEndDevice
        ? "Close other apps to free up memory for videos"
        : "Too many videos loaded. Clearing cache...",
      actionText: attemptNumber === 1 ? "Clear & retry" : "Restart app",
      severity: "high",
      context: "playback",
      technicalDetails: `Available memory: ${deviceMemory}, ${error.debugInfo}`,
      retryStrategy: {
        shouldRetry: attemptNumber === 1,
        maxRetries: 1,
        backoffMs: [2000],
        explanation: "Clearing memory and retrying once",
      },
    };
  }

  private static getServerError(error: VideoError, attemptNumber: number): UserFriendlyError {
    const statusCode = parseInt(error.code) || 500;

    if (statusCode === 404) {
      return {
        title: "Video not found",
        message: "This video may have been removed or moved",
        actionText: "Browse other videos",
        severity: "low",
        context: "loading",
        retryStrategy: {
          shouldRetry: false,
          maxRetries: 0,
          backoffMs: [],
          explanation: "Video doesn't exist",
        },
      };
    }

    if (statusCode >= 500) {
      return {
        title: "Server issue",
        message: "Our servers are having a moment. We're on it!",
        actionText: "Try again",
        secondaryActionText: "Check status",
        severity: attemptNumber > 2 ? "critical" : "medium",
        context: "network",
        technicalDetails: `HTTP ${statusCode}: ${error.debugInfo}`,
        retryStrategy: {
          shouldRetry: attemptNumber < 3,
          maxRetries: 3,
          backoffMs: [2000, 5000, 10000],
          explanation: "Server error, retrying with longer delays",
        },
      };
    }

    return this.getGenericError(error, attemptNumber);
  }

  private static getTimeoutError(error: VideoError, attemptNumber: number): UserFriendlyError {
    return {
      title: "Taking too long",
      message: attemptNumber === 1 ? "Video is taking longer than usual to load" : "Still waiting for the video...",
      actionText: "Keep waiting",
      secondaryActionText: "Try lower quality",
      severity: "low",
      context: "loading",
      technicalDetails: error.debugInfo,
      retryStrategy: {
        shouldRetry: true,
        maxRetries: 3,
        backoffMs: [0, 0, 0],
        explanation: "Extending timeout duration",
      },
    };
  }

  private static getGenericError(error: VideoError, attemptNumber: number): UserFriendlyError {
    const messages = [
      "Something went wrong. Let's try that again!",
      "Still having issues. Trying a different approach...",
      "This is tricky. One more attempt...",
    ];

    return {
      title: "Oops!",
      message: messages[Math.min(attemptNumber - 1, messages.length - 1)],
      actionText: "Retry",
      secondaryActionText: attemptNumber > 2 ? "Get help" : undefined,
      severity: attemptNumber > 2 ? "medium" : "low",
      context: "loading",
      technicalDetails: `${error.type}: ${error.code} - ${error.debugInfo}`,
      retryStrategy: {
        shouldRetry: attemptNumber < 3,
        maxRetries: 3,
        backoffMs: [1000, 3000, 5000],
        explanation: "Standard retry with backoff",
      },
    };
  }

  static getProgressiveErrorMessage(error: VideoError, attemptHistory: number[]): string {
    const totalAttempts = attemptHistory.reduce((sum, a) => sum + a, 0);

    if (totalAttempts === 0) {
      return "Loading video...";
    } else if (totalAttempts === 1) {
      return "Having a small hiccup...";
    } else if (totalAttempts === 2) {
      return "Taking a bit longer than expected...";
    } else if (totalAttempts === 3) {
      return "We're working on it...";
    } else {
      return "Thanks for your patience. Almost there...";
    }
  }

  static getSmartRecoverySuggestion(
    error: VideoError,
    deviceCapabilities?: {
      hasWifi?: boolean;
      batteryLevel?: number;
      freeStorage?: number;
    },
  ): string[] {
    const suggestions: string[] = [];

    if (error.type === VideoErrorType.NETWORK && !deviceCapabilities?.hasWifi) {
      suggestions.push("Connect to Wi-Fi for better video quality");
    }

    if (error.type === VideoErrorType.MEMORY && deviceCapabilities?.freeStorage) {
      if (deviceCapabilities.freeStorage < 100 * 1024 * 1024) {
        suggestions.push("Free up storage space for smoother playback");
      }
    }

    if (deviceCapabilities?.batteryLevel && deviceCapabilities.batteryLevel < 20) {
      suggestions.push("Low battery may affect video performance");
    }

    if (error.type === VideoErrorType.DECODE) {
      suggestions.push("Try updating your app for better video support");
    }

    return suggestions;
  }

  static async logErrorForAnalytics(error: VideoError, attemptNumber: number): Promise<void> {
    const netInfo = await NetInfo.fetch();

    const analytics: ErrorAnalytics = {
      errorCode: error.code,
      errorType: error.type,
      deviceInfo: {
        model: Device.modelName,
        osVersion: Device.osVersion,
        memory: Device.totalMemory,
      },
      networkInfo: netInfo,
      timestamp: Date.now(),
      attemptNumber,
    };

    this.errorHistory.push(analytics);

    if (this.errorHistory.length > this.maxHistorySize) {
      this.errorHistory.shift();
    }

    if (__DEV__) {
      console.log("[VideoErrorAnalytics]", analytics);
    }
  }

  static getErrorTrend(): {
    mostCommonError?: VideoErrorType;
    averageRetries: number;
    successRate: number;
  } {
    if (this.errorHistory.length === 0) {
      return { averageRetries: 0, successRate: 1 };
    }

    const errorCounts = new Map<VideoErrorType, number>();
    let totalRetries = 0;
    let maxAttempts = 0;

    this.errorHistory.forEach((e) => {
      errorCounts.set(e.errorType, (errorCounts.get(e.errorType) || 0) + 1);
      totalRetries += e.attemptNumber;
      maxAttempts = Math.max(maxAttempts, e.attemptNumber);
    });

    const mostCommonError = Array.from(errorCounts.entries()).sort((a, b) => b[1] - a[1])[0]?.[0];

    const averageRetries = totalRetries / this.errorHistory.length;
    const successRate = 1 - this.errorHistory.filter((e) => e.attemptNumber >= 3).length / this.errorHistory.length;

    return { mostCommonError, averageRetries, successRate };
  }

  static clearErrorHistory(): void {
    this.errorHistory = [];
    this.retryAttempts.clear();
  }

  static shouldSuggestQualityReduction(error: VideoError): boolean {
    return (
      error.type === VideoErrorType.NETWORK ||
      error.type === VideoErrorType.TIMEOUT ||
      error.type === VideoErrorType.MEMORY ||
      (error.type === VideoErrorType.DECODE &&
        typeof error.debugInfo === "string" &&
        error.debugInfo.includes("bitrate"))
    );
  }

  static getLocalizedMessage(error: UserFriendlyError, locale: string = "en"): UserFriendlyError {
    return error;
  }
}

import {
  VideoErrorCode,
  VideoErrorSeverity,
  BaseVideoError,
  VideoDisposalError,
  VideoLoadError,
  VideoNetworkError,
  VideoPlaybackError,
  VideoPermissionError,
  VideoSourceError,
} from "../types/videoErrors";
import { RetryConfig } from "./retryLogic";

// Comprehensive error classification patterns
const ERROR_PATTERNS = {
  disposal: [
    "NativeSharedObjectNotFoundException",
    "FunctionCallException",
    "Unable to find the native shared object",
    "already released",
    "disposed",
    "deallocated",
    "null reference",
    "JSI",
    "Hermes",
  ],
  network: [
    "NetworkError",
    "Failed to fetch",
    "ERR_NETWORK",
    "ERR_INTERNET_DISCONNECTED",
    "ECONNREFUSED",
    "ETIMEDOUT",
    "network_error",
    "offline",
    "no internet",
    "connection failed",
  ],
  decode: ["decode", "codec", "unsupported format", "invalid video", "corrupted", "malformed", "ERR_VIDEO_DECODE"],
  source: ["404", "403", "401", "not found", "forbidden", "unauthorized", "invalid url", "source not available"],
  permission: ["permission denied", "not allowed", "access denied", "unauthorized", "forbidden"],
  rateLimit: ["rate limit", "too many requests", "429", "throttled", "quota exceeded"],
  playback: ["playback", "stalled", "buffering", "stuck", "not playing", "player error"],
  memory: ["out of memory", "memory", "allocation failed", "OOM", "memory leak"],
};

// Error recovery strategies
export interface ErrorRecoveryStrategy {
  type: "immediate" | "delayed" | "manual" | "fallback" | "none";
  retryConfig?: RetryConfig;
  fallbackAction?: () => Promise<void>;
  userMessage?: string;
  requiresUserAction?: boolean;
  canAutoRecover?: boolean;
}

// Error context for better debugging
export interface ErrorContext {
  timestamp: number;
  videoId?: string;
  source?: string;
  playerState?: string;
  networkStatus?: boolean;
  deviceInfo?: {
    platform: string;
    version: string;
    isHermes: boolean;
  };
  previousErrors?: BaseVideoError[];
}

// Error metrics for tracking
export interface ErrorMetrics {
  errorCode: VideoErrorCode;
  count: number;
  lastOccurrence: number;
  recoveryAttempts: number;
  recoverySuccesses: number;
  averageRecoveryTime?: number;
}

class VideoErrorAnalyzer {
  private static errorHistory: Map<string, ErrorMetrics> = new Map();
  private static errorPatternCache: Map<string, VideoErrorCode> = new Map();

  /**
   * Detect the specific type of video error from an unknown error
   */
  static detectVideoErrorType(error: unknown): VideoErrorCode {
    if (!error) return VideoErrorCode.UNKNOWN;

    const errorStr = this.getErrorString(error);

    // Check cache first
    const cached = this.errorPatternCache.get(errorStr);
    if (cached) return cached;

    // Check each pattern category
    for (const [category, patterns] of Object.entries(ERROR_PATTERNS)) {
      if (this.matchesPatterns(errorStr, patterns)) {
        const errorCode = this.categoryToErrorCode(category);
        this.errorPatternCache.set(errorStr, errorCode);
        return errorCode;
      }
    }

    return VideoErrorCode.UNKNOWN;
  }

  /**
   * Create a recovery strategy based on error type and context
   */
  static createErrorRecoveryStrategy(error: BaseVideoError, context?: ErrorContext): ErrorRecoveryStrategy {
    const metrics = this.getErrorMetrics(error.code);
    const severity = error.severity || VideoErrorSeverity.ERROR;

    // Check if too many failures
    if (metrics.count > 10 && metrics.recoverySuccesses < 2) {
      return {
        type: "none",
        userMessage: "This issue appears to be persistent. Please try again later.",
        requiresUserAction: true,
        canAutoRecover: false,
      };
    }

    switch (error.code) {
      case VideoErrorCode.DISPOSAL_ERROR:
      case VideoErrorCode.DISPOSAL_FAILED:
      case VideoErrorCode.DISPOSAL_TIMEOUT:
        return {
          type: "immediate",
          retryConfig: {
            maxRetries: 3,
            initialDelay: 100,
            maxDelay: 1000,
            backoffFactor: 2,
          },
          canAutoRecover: true,
        };

      case VideoErrorCode.NETWORK_ERROR:
      case VideoErrorCode.CONNECTION_FAILED:
        return {
          type: context?.networkStatus === false ? "manual" : "delayed",
          retryConfig: {
            maxRetries: 5,
            initialDelay: 2000,
            maxDelay: 30000,
            backoffFactor: 2,
            shouldRetry: () => context?.networkStatus !== false,
          },
          userMessage: "Connection issue. Retrying...",
          canAutoRecover: true,
        };

      case VideoErrorCode.RATE_LIMITED:
        return {
          type: "delayed",
          retryConfig: {
            maxRetries: 3,
            initialDelay: 30000, // Start with 30 second delay
            maxDelay: 120000,
            backoffFactor: 2,
          },
          userMessage: "Too many requests. Please wait a moment.",
          canAutoRecover: true,
        };

      case VideoErrorCode.SOURCE_NOT_FOUND:
      case VideoErrorCode.SOURCE_INVALID:
        return {
          type: "fallback",
          fallbackAction: async () => {
            // Fallback video loading logic
            console.log("Loading fallback video source");
          },
          userMessage: "Video not available. Loading alternative...",
          canAutoRecover: true,
        };

      case VideoErrorCode.DECODE_ERROR:
      case VideoErrorCode.UNSUPPORTED_FORMAT:
        return {
          type: "fallback",
          userMessage: "This video format is not supported.",
          requiresUserAction: false,
          canAutoRecover: false,
        };

      case VideoErrorCode.PERMISSION_DENIED:
      case VideoErrorCode.UNAUTHORIZED:
        return {
          type: "manual",
          userMessage: "You don't have permission to view this content.",
          requiresUserAction: true,
          canAutoRecover: false,
        };

      case VideoErrorCode.PLAYBACK_STALLED:
      case VideoErrorCode.BUFFERING_TIMEOUT:
        return {
          type: "immediate",
          retryConfig: {
            maxRetries: 3,
            initialDelay: 1000,
            maxDelay: 5000,
            backoffFactor: 1.5,
          },
          userMessage: "Video is buffering...",
          canAutoRecover: true,
        };

      default:
        return {
          type: severity === VideoErrorSeverity.CRITICAL ? "manual" : "delayed",
          retryConfig: {
            maxRetries: 3,
            initialDelay: 1000,
            maxDelay: 10000,
            backoffFactor: 2,
          },
          canAutoRecover: severity !== VideoErrorSeverity.CRITICAL,
        };
    }
  }

  /**
   * Check if an error is recoverable
   */
  static isRecoverableError(error: BaseVideoError): boolean {
    const nonRecoverableCodes = [
      VideoErrorCode.UNSUPPORTED_FORMAT,
      VideoErrorCode.PERMISSION_DENIED,
      VideoErrorCode.UNAUTHORIZED,
      VideoErrorCode.DECODE_ERROR,
    ];

    if (nonRecoverableCodes.includes(error.code)) {
      return false;
    }

    // Check severity
    if (error.severity === VideoErrorSeverity.CRITICAL) {
      return false;
    }

    // Check error history
    const metrics = this.getErrorMetrics(error.code);
    if (metrics.count > 20 && metrics.recoverySuccesses === 0) {
      return false;
    }

    return true;
  }

  /**
   * Detect Hermes-specific errors
   */
  static isHermesError(error: unknown): boolean {
    const errorStr = this.getErrorString(error);
    const hermesPatterns = [
      "HermesInternal",
      "JSI",
      "jsi::",
      "Hermes",
      "FunctionCallException",
      "NativeSharedObjectNotFoundException",
    ];

    return this.matchesPatterns(errorStr, hermesPatterns);
  }

  /**
   * Get error severity based on type and frequency
   */
  static getErrorSeverity(error: BaseVideoError): VideoErrorSeverity {
    const metrics = this.getErrorMetrics(error.code);

    // Escalate severity based on frequency
    if (metrics.count > 10) {
      if (error.severity === VideoErrorSeverity.WARNING) {
        return VideoErrorSeverity.ERROR;
      }
      if (error.severity === VideoErrorSeverity.ERROR) {
        return VideoErrorSeverity.CRITICAL;
      }
    }

    return error.severity || VideoErrorSeverity.ERROR;
  }

  /**
   * Create error with context
   */
  static createContextualError(code: VideoErrorCode, message: string, context?: ErrorContext): BaseVideoError {
    const severity = this.getDefaultSeverity(code);
    const recoverable = this.isRecoverableCode(code);
    const metadata: Record<string, unknown> | undefined = context
      ? {
          context,
        }
      : undefined;

    const error = this.instantiateError(code, message, metadata, recoverable, severity);

    if (context) {
      (error as any).context = context;
    }

    this.recordError(error);
    return error;
  }

  /**
   * Extract error context for debugging
   */
  static extractErrorContext(error: unknown): ErrorContext {
    const timestamp = Date.now();
    const context: ErrorContext = { timestamp };

    if (error && typeof error === "object") {
      const err = error as any;

      // Extract common properties
      if (err.videoId) context.videoId = err.videoId;
      if (err.source) context.source = err.source;
      if (err.playerState) context.playerState = err.playerState;
      if (err.networkStatus !== undefined) context.networkStatus = err.networkStatus;

      // Add device info
      const platformInfo = (globalThis as { Platform?: { OS?: unknown; Version?: unknown } }).Platform;
      context.deviceInfo = {
        platform: typeof platformInfo?.OS === "string" ? (platformInfo.OS as string) : "unknown",
        version: typeof platformInfo?.Version === "number" ? String(platformInfo.Version as number) : "unknown",
        isHermes: this.isHermesRuntime(),
      };

      // Extract stack trace info if available
      if (err.stack) {
        const stack = String(err.stack);
        // Parse relevant info from stack
      }
    }

    return context;
  }

  /**
   * Get recovery suggestions for users
   */
  static getRecoverySuggestions(error: BaseVideoError): string[] {
    const suggestions: string[] = [];

    switch (error.code) {
      case VideoErrorCode.NETWORK_ERROR:
      case VideoErrorCode.CONNECTION_FAILED:
        suggestions.push(
          "Check your internet connection",
          "Try switching between Wi-Fi and mobile data",
          "Restart the app",
        );
        break;

      case VideoErrorCode.PLAYBACK_STALLED:
      case VideoErrorCode.BUFFERING_TIMEOUT:
        suggestions.push(
          "Wait for the video to buffer",
          "Try lowering video quality",
          "Close other apps to free up resources",
        );
        break;

      case VideoErrorCode.SOURCE_NOT_FOUND:
        suggestions.push("This video may have been removed", "Try refreshing the feed", "Check back later");
        break;

      case VideoErrorCode.RATE_LIMITED:
        suggestions.push(
          "You've made too many requests",
          "Wait a few minutes before trying again",
          "Avoid rapid scrolling through videos",
        );
        break;

      default:
        suggestions.push(
          "Try again in a moment",
          "Restart the app if the problem persists",
          "Contact support if this continues",
        );
    }

    return suggestions;
  }

  /**
   * Track error patterns for optimization
   */
  static analyzeErrorPatterns(): {
    mostFrequent: VideoErrorCode[];
    recentTrend: "increasing" | "decreasing" | "stable";
    recommendations: string[];
  } {
    const now = Date.now();
    const recentWindow = 5 * 60 * 1000; // 5 minutes

    // Get most frequent errors
    const sortedErrors = Array.from(this.errorHistory.entries())
      .sort((a, b) => b[1].count - a[1].count)
      .slice(0, 5)
      .map(([code]) => code as VideoErrorCode);

    // Analyze recent trend
    const recentErrors = Array.from(this.errorHistory.values()).filter((m) => now - m.lastOccurrence < recentWindow);

    const trend = this.calculateTrend(recentErrors);

    // Generate recommendations
    const recommendations = this.generateRecommendations(sortedErrors, trend);

    return {
      mostFrequent: sortedErrors,
      recentTrend: trend,
      recommendations,
    };
  }

  // Helper methods
  private static getErrorString(error: unknown): string {
    if (!error) return "";
    if (typeof error === "string") return error.toLowerCase();
    if (error instanceof Error) return (error.message + " " + error.stack).toLowerCase();
    if (typeof error === "object" && "message" in error) {
      return String((error as any).message).toLowerCase();
    }
    return JSON.stringify(error).toLowerCase();
  }

  private static matchesPatterns(str: string, patterns: string[]): boolean {
    return patterns.some((pattern) => str.includes(pattern.toLowerCase()));
  }

  private static categoryToErrorCode(category: string): VideoErrorCode {
    const mapping: Record<string, VideoErrorCode> = {
      disposal: VideoErrorCode.DISPOSAL_ERROR,
      network: VideoErrorCode.NETWORK_ERROR,
      decode: VideoErrorCode.DECODE_ERROR,
      source: VideoErrorCode.SOURCE_NOT_FOUND,
      permission: VideoErrorCode.PERMISSION_DENIED,
      rateLimit: VideoErrorCode.RATE_LIMITED,
      playback: VideoErrorCode.PLAYBACK_STALLED,
      memory: VideoErrorCode.UNKNOWN,
    };
    return mapping[category] || VideoErrorCode.UNKNOWN;
  }

  private static instantiateError(
    code: VideoErrorCode,
    message: string,
    metadata: Record<string, unknown> | undefined,
    recoverable: boolean,
    severity: VideoErrorSeverity,
  ): BaseVideoError {
    switch (code) {
      case VideoErrorCode.DISPOSAL_ERROR:
      case VideoErrorCode.DISPOSAL_FAILED:
      case VideoErrorCode.DISPOSAL_TIMEOUT:
        return new VideoDisposalError(message, metadata, severity);

      case VideoErrorCode.NETWORK_ERROR:
      case VideoErrorCode.CONNECTION_FAILED:
      case VideoErrorCode.LOAD_FAILED:
        return new VideoNetworkError(message, metadata, severity);

      case VideoErrorCode.PLAYBACK_FAILED:
      case VideoErrorCode.PLAYBACK_STALLED:
      case VideoErrorCode.BUFFERING_TIMEOUT:
        return new VideoPlaybackError(code, message, metadata, severity);

      case VideoErrorCode.PERMISSION_DENIED:
      case VideoErrorCode.UNAUTHORIZED:
        return new VideoPermissionError(message, metadata);

      case VideoErrorCode.SOURCE_NOT_FOUND:
      case VideoErrorCode.SOURCE_INVALID:
        return new VideoSourceError(code, message, metadata, severity);

      default:
        return new BaseVideoError(code, message, metadata, recoverable, severity);
    }
  }

  private static getDefaultSeverity(code: VideoErrorCode): VideoErrorSeverity {
    const criticalCodes: VideoErrorCode[] = [VideoErrorCode.PERMISSION_DENIED, VideoErrorCode.UNAUTHORIZED];

    const warningCodes: VideoErrorCode[] = [
      VideoErrorCode.DISPOSAL_TIMEOUT,
      VideoErrorCode.BUFFERING_TIMEOUT,
      VideoErrorCode.DISPOSAL_ERROR,
    ];

    if (criticalCodes.includes(code)) return VideoErrorSeverity.CRITICAL;
    if (warningCodes.includes(code)) return VideoErrorSeverity.WARNING;
    return VideoErrorSeverity.ERROR;
  }

  private static isRecoverableCode(code: VideoErrorCode): boolean {
    const nonRecoverable: VideoErrorCode[] = [
      VideoErrorCode.PERMISSION_DENIED,
      VideoErrorCode.UNAUTHORIZED,
      VideoErrorCode.SOURCE_NOT_FOUND,
      VideoErrorCode.SOURCE_INVALID,
      VideoErrorCode.DISPOSAL_ERROR,
      VideoErrorCode.DISPOSAL_FAILED,
    ];

    return !nonRecoverable.includes(code);
  }

  private static getErrorMetrics(code: VideoErrorCode): ErrorMetrics {
    let metrics = this.errorHistory.get(code);
    if (!metrics) {
      metrics = {
        errorCode: code,
        count: 0,
        lastOccurrence: 0,
        recoveryAttempts: 0,
        recoverySuccesses: 0,
      };
      this.errorHistory.set(code, metrics);
    }
    return metrics;
  }

  private static recordError(error: BaseVideoError): void {
    const metrics = this.getErrorMetrics(error.code);
    metrics.count++;
    metrics.lastOccurrence = Date.now();

    // Cleanup old cache entries if too large
    if (this.errorPatternCache.size > 1000) {
      const entriesToDelete = Array.from(this.errorPatternCache.keys()).slice(0, 500);
      entriesToDelete.forEach((key) => this.errorPatternCache.delete(key));
    }
  }

  private static isHermesRuntime(): boolean {
    try {
      // @ts-ignore
      return typeof HermesInternal !== "undefined";
    } catch {
      return false;
    }
  }

  private static calculateTrend(recentErrors: ErrorMetrics[]): "increasing" | "decreasing" | "stable" {
    if (recentErrors.length < 2) return "stable";

    // Simple trend analysis based on error frequency
    const firstHalf = recentErrors.slice(0, Math.floor(recentErrors.length / 2));
    const secondHalf = recentErrors.slice(Math.floor(recentErrors.length / 2));

    const firstHalfCount = firstHalf.reduce((sum, m) => sum + m.count, 0);
    const secondHalfCount = secondHalf.reduce((sum, m) => sum + m.count, 0);

    if (secondHalfCount > firstHalfCount * 1.2) return "increasing";
    if (secondHalfCount < firstHalfCount * 0.8) return "decreasing";
    return "stable";
  }

  private static generateRecommendations(frequentErrors: VideoErrorCode[], trend: string): string[] {
    const recommendations: string[] = [];

    if (trend === "increasing") {
      recommendations.push("Error rate is increasing. Consider investigating recent changes.");
    }

    if (frequentErrors.includes(VideoErrorCode.NETWORK_ERROR)) {
      recommendations.push("Implement offline mode or better network error handling");
    }

    if (frequentErrors.includes(VideoErrorCode.DISPOSAL_ERROR)) {
      recommendations.push("Review video player disposal logic for memory leaks");
    }

    if (frequentErrors.includes(VideoErrorCode.RATE_LIMITED)) {
      recommendations.push("Implement request throttling or caching");
    }

    return recommendations;
  }
}

